CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(IN p_WarehouseID INT, IN p_AdjustmentType VARCHAR(50), IN p_AdjustmentDate TIMESTAMP DEFAULT NULL) LANGUAGE plpgsql AS $$ DECLARE v_AdjustmentDate TIMESTAMP := COALESCE(p_AdjustmentDate, CURRENT_TIMESTAMP); v_ProductID INT; v_CurrentQty INT; v_AdjustmentQty INT; v_NewQty INT; v_TotalProcessed INT := 0; v_ErrorCount INT := 0; v_ErrorMessage TEXT; rec RECORD; BEGIN START TRANSACTION; FOR rec IN SELECT ProductID, QuantityOnHand, CASE p_AdjustmentType WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand WHEN 'DAMAGE' THEN -DamagedQuantity WHEN 'RETURN' THEN ReturnedQuantity ELSE 0 END AS AdjustmentQty FROM InventoryStaging WHERE WarehouseID = p_WarehouseID AND Status = 'PENDING' ORDER BY ProductID LOOP v_ProductID := rec.ProductID; v_CurrentQty := rec.QuantityOnHand; v_AdjustmentQty := rec.AdjustmentQty; BEGIN v_NewQty := v_CurrentQty + v_AdjustmentQty; IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = v_ProductID AND WarehouseID = p_WarehouseID) THEN UPDATE Inventory SET QuantityOnHand = v_NewQty, LastAdjustmentDate = v_AdjustmentDate, LastAdjustmentType = p_AdjustmentType WHERE ProductID = v_ProductID AND WarehouseID = p_WarehouseID; ELSE INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType) VALUES (v_ProductID, p_WarehouseID, CASE WHEN v_NewQty < 0 THEN 0 ELSE v_NewQty END, v_AdjustmentDate, p_AdjustmentType); END IF; INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy) VALUES (v_ProductID, p_WarehouseID, v_AdjustmentDate, p_AdjustmentType, v_CurrentQty, v_AdjustmentQty, v_NewQty, CURRENT_USER); v_TotalProcessed := v_TotalProcessed + 1; EXCEPTION WHEN OTHERS THEN v_ErrorCount := v_ErrorCount + 1; INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID) VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, v_ProductID); END; END LOOP; UPDATE InventoryStaging SET Status = 'PROCESSED', ProcessedDate = CURRENT_TIMESTAMP WHERE WarehouseID = p_WarehouseID AND Status = 'PENDING'; COMMIT; RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_TotalProcessed, v_ErrorCount; EXCEPTION WHEN OTHERS THEN ROLLBACK; RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM; END; $$;
