CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(PayPeriodStart TIMESTAMP, PayPeriodEnd TIMESTAMP, DepartmentID INT DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    PayrollID INT;
    rec RECORD;
    GrossPay DECIMAL(10,2);
    TaxDeduction DECIMAL(10,2);
    NetPay DECIMAL(10,2);
    TotalGross DECIMAL(15,2) := 0;
    TotalNet DECIMAL(15,2) := 0;
    employee_cursor CURSOR FOR 
        SELECT e.EmployeeID, e.HourlyRate, 
               COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours, 
               COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours 
        FROM Employees e 
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID 
            AND t.WorkDate BETWEEN PayPeriodStart AND PayPeriodEnd 
        WHERE e.Status = 'ACTIVE' 
            AND (SP_ProcessPayroll.DepartmentID IS NULL OR e.DepartmentID = SP_ProcessPayroll.DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate 
        ORDER BY e.EmployeeID;
BEGIN
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (PayPeriodStart, PayPeriodEnd, DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING')
    RETURNING PayrollHeader.PayrollID INTO PayrollID;

    OPEN employee_cursor;
    LOOP
        FETCH employee_cursor INTO rec;
        EXIT WHEN NOT FOUND;

        GrossPay := (rec.RegularHours * rec.HourlyRate) + (rec.OvertimeHours * rec.HourlyRate * 1.5);
        TaxDeduction := GrossPay * 0.20;
        NetPay := GrossPay - TaxDeduction;

        INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (PayrollID, rec.EmployeeID, rec.HourlyRate, rec.RegularHours, rec.OvertimeHours, GrossPay, TaxDeduction, NetPay);

        TotalGross := TotalGross + GrossPay;
        TotalNet := TotalNet + NetPay;
    END LOOP;
    CLOSE employee_cursor;

    UPDATE PayrollHeader 
    SET TotalGrossPay = TotalGross, 
        TotalNetPay = TotalNet, 
        Status = 'COMPLETED', 
        CompletedDate = CURRENT_TIMESTAMP 
    WHERE PayrollID = PayrollID;

    RAISE NOTICE 'Payroll processing completed. Payroll ID: %', PayrollID;

EXCEPTION
    WHEN OTHERS THEN
        IF employee_cursor%ISOPEN THEN
            CLOSE employee_cursor;
        END IF;

        UPDATE PayrollHeader 
        SET Status = 'FAILED', 
            ErrorMessage = SQLERRM 
        WHERE PayrollID = PayrollID;

        RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable VARCHAR(128), TargetTable VARCHAR(128), BatchSize INT DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE 
    SQL TEXT;
    SourceCount INT;
    TargetCount INT;
    BatchCount INT := 0;
    MigrationID INT;
    ErrorMessage VARCHAR(4000);
    RowCount INT;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (SourceTable, TargetTable, CURRENT_TIMESTAMP, 'RUNNING')
    RETURNING MigrationID INTO MigrationID;

    EXECUTE 'SELECT COUNT(*) FROM ' || quote_ident(SourceTable) INTO SourceCount;
    UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;

    EXECUTE 'DELETE FROM ' || quote_ident(TargetTable);

    WHILE BatchCount < SourceCount LOOP
        SQL := 'INSERT INTO ' || quote_ident(TargetTable) || 
               ' SELECT * FROM ' || quote_ident(SourceTable) || 
               ' WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(TargetTable) || 
               ' t WHERE t.ID = ' || quote_ident(SourceTable) || '.ID) LIMIT ' || BatchSize;
        EXECUTE SQL;
        GET DIAGNOSTICS RowCount = ROW_COUNT;
        IF RowCount = 0 THEN
            EXIT;
        END IF;
        BatchCount := BatchCount + RowCount;
    END LOOP;

    EXECUTE 'SELECT COUNT(*) FROM ' || quote_ident(TargetTable) INTO TargetCount;
    UPDATE MigrationLog SET 
        EndTime = CURRENT_TIMESTAMP,
        TargetCount = TargetCount,
        Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = MigrationID;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
EXCEPTION
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS ErrorMessage = MESSAGE_TEXT;
        UPDATE MigrationLog SET 
            EndTime = CURRENT_TIMESTAMP,
            Status = 'FAILED',
            ErrorMessage = ErrorMessage
        WHERE MigrationID = MigrationID;
        RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    cur_products CURSOR FOR SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := CURRENT_TIMESTAMP;
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO v_ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    IF v_ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    OPEN cur_products;
    LOOP
        FETCH cur_products INTO v_ProductID, v_ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (v_ProductID, OldCategoryID, NewCategoryID, EffectiveDate, CURRENT_USER);

            UPDATE Products 
            SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDate, LastModifiedBy = CURRENT_USER 
            WHERE ProductID = v_ProductID;

            v_UpdatedCount := v_UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (v_ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;
    CLOSE cur_products;

    UPDATE ProductCategories SET LastUpdateDate = EffectiveDate WHERE CategoryID IN (OldCategoryID, NewCategoryID);
    RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;

EXCEPTION WHEN OTHERS THEN
    IF cur_products%ISOPEN THEN
        CLOSE cur_products;
    END IF;
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE p_job_create_bond_info_all()
LANGUAGE plpgsql
AS $$
DECLARE 
    colname VARCHAR;
    sqlstr VARCHAR;
BEGIN
    TRUNCATE TABLE tmp_bond_info_all_20010;
    INSERT INTO tmp_bond_info_all_20010(f_val, def_id, id)
    SELECT t1.field_value, t1.FIELD_DEFINITION_ID, t2.ID
    FROM T_OBJECT_FIELD_DATA t1
    JOIN T_PJ_Bond_Info t2 ON t1.parent_id = t2.APPLY_ID
    WHERE t1.OBJECT_NO = 20010 AND t1.FIELD_VALUE IS NOT NULL;

    TRUNCATE TABLE tmp_bond_info_all_20020;
    INSERT INTO tmp_bond_info_all_20020(f_val, def_id, id)
    SELECT t1.field_value, t1.FIELD_DEFINITION_ID, t2.id
    FROM T_OBJECT_FIELD_DATA t1
    JOIN T_PJ_Bond_Info t2 ON t1.PARENT_ID = t2.TEMP_BONDID
    WHERE t1.OBJECT_NO = 20020 AND t1.FIELD_VALUE IS NOT NULL;

    TRUNCATE TABLE t_pj_bond_info_all;
    INSERT INTO t_pj_bond_info_all(id, temp_bondid, apply_id)
    SELECT t1.ID, t1.TEMP_BONDID, t1.APPLY_ID
    FROM T_PJ_Bond_Info t1;

    FOR colname IN 
        SELECT attname 
        FROM pg_attribute 
        WHERE attrelid = 't_pj_bond_info_all'::regclass 
          AND attname LIKE 'def%' 
          AND attnum > 0 
          AND NOT attisdropped
    LOOP
        sqlstr := 'UPDATE t_pj_bond_info_all a SET ' || colname || ' = b.f_val FROM tmp_bond_info_all_20020 b WHERE a.id = b.id AND b.def_id = ' || REPLACE(colname, 'def_', '');
        EXECUTE sqlstr;

        sqlstr := 'UPDATE t_pj_bond_info_all a SET ' || colname || ' = b.f_val FROM tmp_bond_info_all_20010 b WHERE a.id = b.id AND a.' || colname || ' IS NULL AND b.def_id = ' || REPLACE(colname, 'def_', '');
        EXECUTE sqlstr;
    END LOOP;
END;
$$;

CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id INTEGER, OUT p_result TEXT) AS $$
DECLARE
    total_emp INTEGER;
    avg_salary NUMERIC;
    max_salary NUMERIC;
    active_projects INTEGER;
    v_employee_id INTEGER;
    v_last_name VARCHAR(50);
    v_salary NUMERIC(8,2);
    v_dept_name VARCHAR(50);
    v_sql TEXT;
BEGIN
    SELECT department_name INTO STRICT v_dept_name FROM departments WHERE department_id = p_dept_id;
    SELECT COUNT(*), AVG(salary), MAX(salary) INTO total_emp, avg_salary, max_salary FROM employees WHERE department_id = p_dept_id;
    SELECT COUNT(*) INTO active_projects FROM projects WHERE department_id = p_dept_id AND status = 'ACTIVE';
    p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || total_emp || ',  "salary_stats": {    "average": ' || COALESCE(avg_salary::text, 'null') || ',    "max": ' || COALESCE(max_salary::text, 'null') || '  },  "active_projects": ' || active_projects || ',  "top_earners": [';
    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    FOR v_employee_id, v_last_name, v_salary IN EXECUTE v_sql USING p_dept_id
    LOOP
        p_result := p_result || '    {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;
    p_result := rtrim(p_result, ',') || '  ]}';
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

