CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL) AS
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName VARCHAR(100);
    product_cursor REFCURSOR;
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := CURRENT_TIMESTAMP;
    END IF;
    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;
    SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;
    OPEN product_cursor FOR SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    LOOP
        FETCH product_cursor INTO ProductID, ProductName;
        EXIT WHEN NOT FOUND;
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDate, SESSION_USER);
            UPDATE Products SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDate, LastModifiedBy = SESSION_USER
            WHERE ProductID = ProductID;
            UpdatedCount := UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;
    CLOSE product_cursor;
    UPDATE ProductCategories SET LastUpdateDate = EffectiveDate
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);
    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
EXCEPTION WHEN OTHERS THEN
    IF product_cursor%ISOPEN THEN
        CLOSE product_cursor;
    END IF;
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id IN INTEGER, OUT p_result TEXT) AS $$ 
DECLARE
    v_stats RECORD;
    v_result RECORD;
    v_cursor REFCURSOR;
    v_sql TEXT;
    v_dept_name VARCHAR(50);
BEGIN
    SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary 
    FROM EMPLOYEES WHERE department_id = p_dept_id;

    SELECT COUNT(*) INTO v_stats.active_projects 
    FROM PROJECTS WHERE department_id = p_dept_id AND status = 'ACTIVE';

    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';

    OPEN v_cursor FOR EXECUTE v_sql USING p_dept_id;

    p_result := format('{"department": "%s", "total_employees": %s, "salary_stats": {"average": %s, "max": %s}, "active_projects": %s, "top_earners": [', 
        v_dept_name, v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary, v_stats.active_projects);

    LOOP
        FETCH v_cursor INTO v_result;
        EXIT WHEN NOT FOUND;
        p_result := p_result || format('{"id": %s, "name": "%s", "salary": %s},', 
            v_result.employee_id, v_result.last_name, v_result.salary);
    END LOOP;

    p_result := rtrim(p_result, ',') || ']}';

    CLOSE v_cursor;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := format('{"error": "%s"}', SQLERRM);
END;
$$ LANGUAGE plpgsql;

