CREATE OR REPLACE FUNCTION VALIDATE_CONTEXT() RETURNS void AS $$
DECLARE
    l_type# integer;
    l_owner# integer;
    l_ltype varchar(30) := 'FIRST';
    l_status integer;
    l_compile_sql varchar(2000);
    l_dbo_name varchar(32);
    c1 record;
BEGIN
    FOR c1 IN
        SELECT dbo_name, dbo_type
        FROM ctxsys.dr$dbo
        WHERE dbo_type <> 'PUBLIC SYNONYM'
        ORDER BY dbo_type, dbo_name
    LOOP
        IF (c1.dbo_type <> l_ltype) THEN
            l_type# := CASE c1.dbo_type
                         WHEN 'INDEX' THEN 1
                         WHEN 'TABLE' THEN 2
                         WHEN 'VIEW' THEN 4
                         WHEN 'SEQUENCE' THEN 6
                         WHEN 'PROCEDURE' THEN 7
                         WHEN 'FUNCTION' THEN 8
                         WHEN 'PACKAGE' THEN 9
                         WHEN 'PACKAGE BODY' THEN 11
                         WHEN 'TYPE' THEN 13
                         WHEN 'TYPE BODY' THEN 14
                         WHEN 'LIBRARY' THEN 22
                         WHEN 'INDEXTYPE' THEN 32
                         WHEN 'OPERATOR' THEN 33
                         ELSE 0
                       END;
            l_ltype := c1.dbo_type;
        END IF;
    END LOOP;
    PERFORM dbms_registry_valid('CONTEXT');
    <<endfunc>>
    NULL;
EXCEPTION
    WHEN OTHERS THEN
        PERFORM ctxsys.drue_text_on_stack(SQLERRM, 'validate_context');
        PERFORM dbms_registry_invalid('CONTEXT');
        RAISE;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS void AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_report_sql TEXT;
    v_sales_record RECORD;
    v_start_date DATE;
    v_end_date DATE;
    v_rep_count INTEGER := 0;
BEGIN
    v_start_date := make_date(p_year, (p_quarter - 1) * 3 + 1, 1);
    v_end_date := (date_trunc('month', v_start_date + interval '2 months') + interval '1 month' - interval '1 day')::date;

    SELECT nextval('sales_report_seq') INTO v_report_id;

    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';

    -- First, insert the header record. The totals will be updated after the loop.
    -- This mimics the Oracle logic of inserting the header before processing details.
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, NOW(), 0);

    FOR v_sales_record IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, v_sales_record.sales_rep_id, v_sales_record.sales_rep_name, v_sales_record.total_sales, v_sales_record.commission_rate, v_sales_record.commission_amount);

        v_total_sales := v_total_sales + v_sales_record.total_sales;
        v_total_commission := v_total_commission + v_sales_record.commission_amount;
        v_rep_count := v_rep_count + 1;
    END LOOP;

    UPDATE sales_report_header
    SET total_sales = v_total_sales,
        total_commission = v_total_commission,
        total_reps = v_rep_count
    WHERE report_id = v_report_id;

    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable IN VARCHAR(128), TargetTable IN VARCHAR(128), BatchSize IN INT DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE
    SQL TEXT;
    SourceCount INT;
    TargetCount INT;
    BatchCount INT := 0;
    MigrationID INT;
    ErrorMessage TEXT;
    rows_affected INT;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, NOW(), 'RUNNING') RETURNING MigrationID INTO MigrationID;

    SQL := 'SELECT COUNT(*) FROM ' || quote_ident(SourceTable);
    EXECUTE SQL INTO SourceCount;

    UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;

    SQL := 'DELETE FROM ' || quote_ident(TargetTable);
    EXECUTE SQL;

    WHILE BatchCount < SourceCount LOOP
        SQL := 'INSERT INTO ' || quote_ident(TargetTable) || ' SELECT * FROM ' || quote_ident(SourceTable) || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(TargetTable) || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize;
        EXECUTE SQL;
        GET DIAGNOSTICS rows_affected = ROW_COUNT;

        IF rows_affected = 0 THEN
            EXIT;
        END IF;

        BatchCount := BatchCount + rows_affected;
    END LOOP;

    SQL := 'SELECT COUNT(*) FROM ' || quote_ident(TargetTable);
    EXECUTE SQL INTO TargetCount;

    UPDATE MigrationLog SET EndTime = NOW(), TargetCount = TargetCount, Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = MigrationID;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;

EXCEPTION
    WHEN OTHERS THEN
        ErrorMessage := SQLERRM;
        UPDATE MigrationLog SET EndTime = NOW(), Status = 'FAILED', ErrorMessage = ErrorMessage WHERE MigrationID = MigrationID;
        RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;
$$;

