CREATE OR REPLACE FUNCTION CHECKANDUPDATESALARIES(p_dept_id IN INTEGER, p_percent IN NUMERIC, p_min_dept IN INTEGER DEFAULT NULL, p_max_dept IN INTEGER DEFAULT NULL)
RETURNS TEXT AS $$
DECLARE
    v_stats RECORD;
    v_result RECORD;
    v_cursor REFCURSOR;
    v_sql TEXT;
    v_dept_name VARCHAR(50);
    v_rows_updated INTEGER := 0;
    v_total_budget NUMERIC := 0;
    dept_rec RECORD;
    v_temp_rows INTEGER;
    p_result TEXT;
BEGIN
    SELECT department_name INTO v_dept_name
    FROM DEPARTMENTS
    WHERE department_id = p_dept_id;

    SELECT COUNT(*) as total_emp, AVG(salary) as avg_salary, MAX(salary) as max_salary
    INTO v_stats
    FROM EMPLOYEES
    WHERE department_id = p_dept_id;

    v_sql := 'SELECT employee_id, last_name, salary FROM employees ' ||
             'WHERE department_id = ' || p_dept_id || ' ORDER BY salary DESC LIMIT 5';

    OPEN v_cursor FOR EXECUTE v_sql;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_stats.total_emp ||
                ', "salary_stats": { "average": ' || COALESCE(v_stats.avg_salary, 0) || ', "max": ' || COALESCE(v_stats.max_salary, 0) || ' }, "top_earners": [';

    LOOP
        FETCH v_cursor INTO v_result;
        EXIT WHEN NOT FOUND;
        p_result := p_result || ' {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
    END LOOP;

    p_result := RTRIM(p_result, ',') || ' ] }';
    CLOSE v_cursor;

    FOR dept_rec IN
        SELECT department_id, budget
        FROM DEPARTMENTS
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
          AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE EMPLOYEES
        SET salary = COALESCE(salary, 3000) * (1 + p_percent / 100)
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_temp_rows = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_temp_rows;

        SELECT SUM(salary) INTO v_total_budget
        FROM EMPLOYEES
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, NOW();

    RETURN p_result;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
        RETURN p_result;
    WHEN OTHERS THEN
        p_result := '{"error": "' || REPLACE(SQLERRM, '"', '\"') || '"}';
        RETURN p_result;
END;
$$ LANGUAGE plpgsql;

