CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id INTEGER, p_adjustment_type VARCHAR, p_adjustment_date DATE DEFAULT CURRENT_DATE)
RETURNS VOID AS $$
DECLARE
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    rec RECORD;
BEGIN
    FOR rec IN
        SELECT
            product_id,
            quantity_on_hand,
            CASE p_adjustment_type
                WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                WHEN 'DAMAGE' THEN -damaged_quantity
                WHEN 'RETURN' THEN returned_quantity
                ELSE 0
            END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            UPDATE inventory
            SET quantity_on_hand = inventory.quantity_on_hand + rec.adjustment_qty,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type
            WHERE product_id = rec.product_id AND warehouse_id = p_warehouse_id;

            IF NOT FOUND THEN
                INSERT INTO inventory (
                    product_id, warehouse_id, quantity_on_hand,
                    last_adjustment_date, last_adjustment_type
                ) VALUES (
                    rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty),
                    p_adjustment_date, p_adjustment_type
                );
            END IF;

            INSERT INTO inventory_audit_log (
                product_id, warehouse_id, adjustment_date, adjustment_type,
                old_quantity, adjustment_quantity, new_quantity, created_by
            ) VALUES (
                rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type,
                rec.quantity_on_hand, rec.adjustment_qty, rec.quantity_on_hand + rec.adjustment_qty,
                CURRENT_USER
            );

            v_total_processed := v_total_processed + 1;

        EXCEPTION WHEN OTHERS THEN
            v_error_count := v_error_count + 1;
            INSERT INTO error_log (error_date, error_message, product_id)
            VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, rec.product_id);
        END;
    END LOOP;

    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = CURRENT_TIMESTAMP
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;

EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year IN INTEGER, p_quarter IN INTEGER, p_sales_rep_id IN INTEGER DEFAULT NULL)
RETURNS void AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_rep_count INTEGER := 0;
    v_report_sql TEXT;
    v_sales_rec RECORD;
    v_start_date DATE;
    v_end_date DATE;
BEGIN
    v_start_date := make_date(p_year, (p_quarter - 1) * 3 + 1, 1);
    v_end_date := (date_trunc('month', v_start_date + interval '2 months') + interval '1 month' - interval '1 day')::date;

    SELECT nextval('sales_report_seq') INTO v_report_id;

    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, NOW(), 0);

    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount
                     FROM sales_representatives sr
                     LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2
                     WHERE ($3 IS NULL OR sr.sales_rep_id = $3)
                     GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
                     ORDER BY total_sales DESC';

    FOR v_sales_rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id
    LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, v_sales_rec.sales_rep_id, v_sales_rec.sales_rep_name, v_sales_rec.total_sales, v_sales_rec.commission_rate, v_sales_rec.commission_amount);

        v_total_sales := v_total_sales + v_sales_rec.total_sales;
        v_total_commission := v_total_commission + v_sales_rec.commission_amount;
        v_rep_count := v_rep_count + 1;
    END LOOP;

    UPDATE sales_report_header
    SET total_sales = v_total_sales,
        total_commission = v_total_commission,
        total_reps = v_rep_count
    WHERE report_id = v_report_id;

    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

