CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable IN VARCHAR(128), TargetTable IN VARCHAR(128), BatchSize IN INT DEFAULT 10000) AS $$
DECLARE
    SQL_TEXT TEXT;
    SourceCount INT;
    TargetCount INT;
    BatchCount INT := 0;
    MigrationID INT;
    ErrorMessage VARCHAR(4000);
    v_row_count INT;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, NOW(), 'RUNNING') RETURNING MigrationID INTO MigrationID;
    SQL_TEXT := 'SELECT COUNT(*) FROM ' || quote_ident(SourceTable);
    EXECUTE SQL_TEXT INTO SourceCount;
    UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;
    SQL_TEXT := 'DELETE FROM ' || quote_ident(TargetTable);
    EXECUTE SQL_TEXT;
    WHILE BatchCount < SourceCount LOOP
        SQL_TEXT := 'INSERT INTO ' || quote_ident(TargetTable) || ' SELECT * FROM ' || quote_ident(SourceTable) || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(TargetTable) || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize;
        EXECUTE SQL_TEXT;
        GET DIAGNOSTICS v_row_count = ROW_COUNT;
        IF v_row_count = 0 THEN
            EXIT;
        END IF;
        BatchCount := BatchCount + v_row_count;
    END LOOP;
    SQL_TEXT := 'SELECT COUNT(*) FROM ' || quote_ident(TargetTable);
    EXECUTE SQL_TEXT INTO TargetCount;
    UPDATE MigrationLog SET EndTime = NOW(), TargetCount = TargetCount, Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = MigrationID;
    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
EXCEPTION
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS ErrorMessage = MESSAGE_TEXT;
        IF MigrationID IS NOT NULL THEN
            UPDATE MigrationLog SET EndTime = NOW(), Status = 'FAILED', ErrorMessage = ErrorMessage WHERE MigrationID = MigrationID;
        END IF;
        RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;
$$ LANGUAGE plpgsql;

