    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, NOW(), 'RUNNING') RETURNING MigrationID INTO MigrationID;
    SQL_TEXT := 'SELECT COUNT(*) FROM ' || SourceTable;
    EXECUTE IMMEDIATE SQL_TEXT INTO SourceCount;
    UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;
    SQL_TEXT := 'DELETE FROM ' || TargetTable;
    EXECUTE IMMEDIATE SQL_TEXT;
    WHILE BatchCount < SourceCount LOOP
        SQL_TEXT := 'INSERT INTO ' || TargetTable || ' SELECT * FROM ' || SourceTable || ' WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = ' || SourceTable || '.ID) LIMIT ' || BatchSize;
        EXECUTE IMMEDIATE SQL_TEXT;
        GET DIAGNOSTICS rows_affected = ROW_COUNT;
        IF rows_affected = 0 THEN
            EXIT;
        END IF;
        BatchCount := BatchCount + rows_affected;
    END LOOP;
    SQL_TEXT := 'SELECT COUNT(*) FROM ' || TargetTable;
    EXECUTE IMMEDIATE SQL_TEXT INTO TargetCount;
    UPDATE MigrationLog SET EndTime = NOW(), TargetCount = TargetCount, Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = MigrationID;
    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
EXCEPTION
    WHEN OTHERS THEN
        ErrorMessage := SQLERRM;
        UPDATE MigrationLog SET EndTime = NOW(), Status = 'FAILED', ErrorMessage = ErrorMessage WHERE MigrationID = MigrationID;
        RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable IN VARCHAR(128), TargetTable IN VARCHAR(128), BatchSize IN INT DEFAULT 10000)
AS
DECLARE
    l_SQL VARCHAR(32767);
    l_SourceCount INT;
    l_TargetCount INT;
    l_BatchCount INT := 0;
    l_MigrationID INT;
    l_ErrorMessage VARCHAR(4000);
    l_rows_affected INT;
BEGIN
    BEGIN
        INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, NOW(), 'RUNNING') RETURNING MigrationID INTO l_MigrationID;
        l_SQL := 'SELECT COUNT(*) FROM ' || SourceTable;
        EXECUTE l_SQL INTO l_SourceCount;
        UPDATE MigrationLog SET SourceCount = l_SourceCount WHERE MigrationID = l_MigrationID;
        l_SQL := 'DELETE FROM ' || TargetTable;
        EXECUTE l_SQL;
        WHILE l_BatchCount < l_SourceCount LOOP
            l_SQL := 'INSERT INTO ' || TargetTable || ' SELECT * FROM ' || SourceTable || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize;
            EXECUTE l_SQL;
            GET DIAGNOSTICS l_rows_affected = ROW_COUNT;
            IF l_rows_affected = 0 THEN
                EXIT;
            END IF;
            l_BatchCount := l_BatchCount + l_rows_affected;
        END LOOP;
        l_SQL := 'SELECT COUNT(*) FROM ' || TargetTable;
        EXECUTE l_SQL INTO l_TargetCount;
        UPDATE MigrationLog SET EndTime = NOW(), TargetCount = l_TargetCount, Status = CASE WHEN l_SourceCount = l_TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = l_MigrationID;
        RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, l_SourceCount, l_TargetCount;
    EXCEPTION
        WHEN OTHERS THEN
            l_ErrorMessage := SQLERRM;
            UPDATE MigrationLog SET EndTime = NOW(), Status = 'FAILED', ErrorMessage = l_ErrorMessage WHERE MigrationID = l_MigrationID;
            RAISE EXCEPTION 'Data migration failed: %', l_ErrorMessage;
    END;
END;

CREATE OR REPLACE FUNCTION SP_UpdateProductCategories(OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL)
RETURNS VOID AS $$
DECLARE
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_EffectiveDateFinal TIMESTAMP;
    product_cursor CURSOR FOR
        SELECT ProductID, ProductName
        FROM Products
        WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
    v_EffectiveDateFinal := COALESCE(EffectiveDate, NOW());

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO v_ProductCount
    FROM Products
    WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    IF v_ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO v_ProductID, v_ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (v_ProductID, OldCategoryID, NewCategoryID, v_EffectiveDateFinal, CURRENT_USER);

            UPDATE Products
            SET CategoryID = NewCategoryID,
                LastModifiedDate = v_EffectiveDateFinal,
                LastModifiedBy = CURRENT_USER
            WHERE ProductID = v_ProductID;

            v_UpdatedCount := v_UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (v_ProductID, SQLERRM, NOW());
        END;
    END LOOP;
    CLOSE product_cursor;

    UPDATE ProductCategories
    SET LastUpdateDate = v_EffectiveDateFinal
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;

EXCEPTION
    WHEN OTHERS THEN
        IF product_cursor%ISOPEN THEN
            CLOSE product_cursor;
        END IF;
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

