CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id INTEGER)
RETURNS TEXT AS $$
DECLARE
    v_dept_name VARCHAR(50);
    v_total_emp BIGINT;
    v_avg_salary NUMERIC;
    v_max_salary NUMERIC;
    v_active_projects BIGINT;
    v_earner_rec RECORD;
    v_result TEXT;
BEGIN
    SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM EMPLOYEES
    WHERE department_id = p_dept_id;

    SELECT COUNT(*)
    INTO v_active_projects
    FROM PROJECTS
    WHERE department_id = p_dept_id AND status = 'ACTIVE';

    v_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || COALESCE(v_avg_salary, 0) || ',    "max": ' || COALESCE(v_max_salary, 0) || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';

    FOR v_earner_rec IN
        SELECT employee_id, last_name, salary
        FROM employees
        WHERE department_id = p_dept_id
        ORDER BY salary DESC
        LIMIT 5
    LOOP
        v_result := v_result || '    {"id": ' || v_earner_rec.employee_id || ', "name": "' || v_earner_rec.last_name || '", "salary": ' || v_earner_rec.salary || '},';
    END LOOP;

    v_result := RTRIM(v_result, ',') || '  ]}';

    RETURN v_result;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        RETURN '{"error": "Department not found"}';
    WHEN OTHERS THEN
        RETURN '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE usp_UpdateOrderStatus(IN p_CustomerID INT, IN p_OldStatus VARCHAR(20), IN p_NewStatus VARCHAR(20), OUT p_RowsUpdated INT, OUT p_ErrorMessage VARCHAR(200), OUT p_StatusCode INT) LANGUAGE plpgsql AS $$
DECLARE
    v_ProcessingFee DECIMAL(10, 2);
    fee_cursor CURSOR FOR
        SELECT Fee FROM OrderDetails
        WHERE OrderID IN (
            SELECT OrderID FROM Orders WHERE CustomerID = p_CustomerID AND Status = p_NewStatus
        );
BEGIN
    p_RowsUpdated := 0;
    IF p_OldStatus = p_NewStatus THEN
        p_ErrorMessage := '新旧状态不能相同';
        p_StatusCode := 400;
        RETURN;
    END IF;

    UPDATE Orders
    SET Status = p_NewStatus, LastUpdated = NOW()
    WHERE CustomerID = p_CustomerID
      AND Status = p_OldStatus
      AND OrderDate > NOW() - INTERVAL '3 months';

    GET DIAGNOSTICS p_RowsUpdated = ROW_COUNT;

    IF p_RowsUpdated = 0 THEN
        p_ErrorMessage := '未找到符合条件的订单';
        p_StatusCode := 404;
        RETURN;
    END IF;

    INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
    SELECT OrderID, p_OldStatus, p_NewStatus, NOW()
    FROM Orders
    WHERE CustomerID = p_CustomerID AND Status = p_NewStatus;

    OPEN fee_cursor;
    LOOP
        FETCH fee_cursor INTO v_ProcessingFee;
        EXIT WHEN NOT FOUND;
        v_ProcessingFee := v_ProcessingFee * 0.95;
    END LOOP;
    CLOSE fee_cursor;

    p_ErrorMessage := '更新成功';
    p_StatusCode := 200;
    COMMIT;

EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK;
        p_ErrorMessage := SQLERRM;
        p_StatusCode := 500;
END;
$$;

