CREATE OR REPLACE FUNCTION FN_GET_RUNNING_BALANCE(p_account_id IN INTEGER, p_transaction_date IN DATE) RETURNS NUMERIC AS $$
DECLARE
    v_balance NUMERIC := 0;
    txn RECORD;
BEGIN
    FOR txn IN
        SELECT transaction_amount, transaction_type
        FROM account_transactions
        WHERE account_id = p_account_id
          AND transaction_date <= p_transaction_date
        ORDER BY transaction_date, transaction_id
    LOOP
        IF txn.transaction_type = 'CREDIT' THEN
            v_balance := v_balance + txn.transaction_amount;
        ELSE
            v_balance := v_balance - txn.transaction_amount;
        END IF;
    END LOOP;

    RETURN v_balance;
EXCEPTION
    WHEN OTHERS THEN
        RETURN NULL;
END;
$$ LANGUAGE plpgsql;

