CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(p_PayPeriodStart IN TIMESTAMP, p_PayPeriodEnd IN TIMESTAMP, p_DepartmentID IN INT DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    v_PayrollID INT;
    v_EmployeeID INT;
    v_HourlyRate DECIMAL(10,2);
    v_RegularHours DECIMAL(5,2);
    v_OvertimeHours DECIMAL(5,2);
    v_GrossPay DECIMAL(10,2);
    v_TaxDeduction DECIMAL(10,2);
    v_NetPay DECIMAL(10,2);
    v_TotalGross DECIMAL(15,2) := 0;
    v_TotalNet DECIMAL(15,2) := 0;
    v_ErrorMessage TEXT;
    employee_rec RECORD;
BEGIN
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (p_PayPeriodStart, p_PayPeriodEnd, p_DepartmentID, NOW(), 'PROCESSING')
    RETURNING PayrollID INTO v_PayrollID;

    FOR employee_rec IN
        SELECT
            e.EmployeeID,
            e.HourlyRate,
            COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
            COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN p_PayPeriodStart AND p_PayPeriodEnd
        WHERE e.Status = 'ACTIVE' AND (p_DepartmentID IS NULL OR e.DepartmentID = p_DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID
    LOOP
        v_GrossPay := (employee_rec.RegularHours * employee_rec.HourlyRate) + (employee_rec.OvertimeHours * employee_rec.HourlyRate * 1.5);
        v_TaxDeduction := v_GrossPay * 0.20;
        v_NetPay := v_GrossPay - v_TaxDeduction;

        INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (v_PayrollID, employee_rec.EmployeeID, employee_rec.HourlyRate, employee_rec.RegularHours, employee_rec.OvertimeHours, v_GrossPay, v_TaxDeduction, v_NetPay);

        v_TotalGross := v_TotalGross + v_GrossPay;
        v_TotalNet := v_TotalNet + v_NetPay;
    END LOOP;

    UPDATE PayrollHeader
    SET TotalGrossPay = v_TotalGross,
        TotalNetPay = v_TotalNet,
        Status = 'COMPLETED',
        CompletedDate = NOW()
    WHERE PayrollID = v_PayrollID;

    RAISE NOTICE 'Payroll processing completed. Payroll ID: %', v_PayrollID;

EXCEPTION
    WHEN OTHERS THEN
        v_ErrorMessage := SQLERRM;
        IF v_PayrollID IS NOT NULL THEN
            UPDATE PayrollHeader
            SET Status = 'FAILED',
                ErrorMessage = v_ErrorMessage
            WHERE PayrollID = v_PayrollID;
        END IF;
        RAISE EXCEPTION 'Payroll processing failed: %', v_ErrorMessage;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_CleanupOldLogs(p_retention_days INT DEFAULT 90)
LANGUAGE plpgsql
AS $$
DECLARE
    v_cutoff_date TIMESTAMP;
    v_deleted_count INT := 0;
    v_rows_affected INT;
BEGIN
    v_cutoff_date := NOW() - (p_retention_days * INTERVAL '1 day');
    BEGIN
        DELETE FROM ApplicationLogs WHERE LogDate < v_cutoff_date;
        GET DIAGNOSTICS v_rows_affected = ROW_COUNT;
        v_deleted_count := v_deleted_count + v_rows_affected;

        DELETE FROM ErrorLogs WHERE ErrorDate < v_cutoff_date;
        GET DIAGNOSTICS v_rows_affected = ROW_COUNT;
        v_deleted_count := v_deleted_count + v_rows_affected;

        DELETE FROM AuditLogs WHERE AuditDate < v_cutoff_date;
        GET DIAGNOSTICS v_rows_affected = ROW_COUNT;
        v_deleted_count := v_deleted_count + v_rows_affected;

        INSERT INTO CleanupHistory (CleanupDate, TableName, RecordsDeleted, RetentionDays)
        VALUES (NOW(), 'MULTIPLE_LOG_TABLES', v_deleted_count, p_retention_days);

        RAISE NOTICE 'Cleanup completed. % records deleted.', v_deleted_count;
    EXCEPTION
        WHEN OTHERS THEN
            RAISE EXCEPTION 'Log cleanup failed: %', SQLERRM;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN p_OldCategoryID INT, IN p_NewCategoryID INT, IN p_EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_errm TEXT;
    product_cursor CURSOR FOR
        SELECT ProductID, ProductName FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';
BEGIN
    IF p_EffectiveDate IS NULL THEN
        p_EffectiveDate := NOW();
    END IF;

    BEGIN -- Corresponds to the outer TRY block
        IF p_OldCategoryID = p_NewCategoryID THEN
            RAISE EXCEPTION 'Old and new category IDs cannot be the same';
        END IF;

        SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';

        IF ProductCount = 0 THEN
            RAISE NOTICE 'No active products found in category %', p_OldCategoryID;
            RETURN;
        END IF;

        OPEN product_cursor;
        LOOP
            FETCH product_cursor INTO v_ProductID, v_ProductName;
            EXIT WHEN NOT FOUND;

            BEGIN -- Corresponds to the inner TRY block for each row
                INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
                VALUES (v_ProductID, p_OldCategoryID, p_NewCategoryID, p_EffectiveDate, CURRENT_USER);

                UPDATE Products
                SET CategoryID = p_NewCategoryID,
                    LastModifiedDate = p_EffectiveDate,
                    LastModifiedBy = CURRENT_USER
                WHERE ProductID = v_ProductID;

                UpdatedCount := UpdatedCount + 1;
            EXCEPTION
                WHEN OTHERS THEN -- Corresponds to the inner CATCH block
                    GET STACKED DIAGNOSTICS v_errm = MESSAGE_TEXT;
                    INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                    VALUES (v_ProductID, v_errm, NOW());
            END;
        END LOOP;
        CLOSE product_cursor;

        UPDATE ProductCategories
        SET LastUpdateDate = p_EffectiveDate
        WHERE CategoryID IN (p_OldCategoryID, p_NewCategoryID);

        RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

    EXCEPTION
        WHEN OTHERS THEN -- Corresponds to the outer CATCH block
            CLOSE product_cursor; -- Ensure cursor is closed on error
            GET STACKED DIAGNOSTICS v_errm = MESSAGE_TEXT;
            RAISE EXCEPTION 'Category update failed: %', v_errm;
    END;
END;
$$;

