WITH RECURSIVE emp_hierarchy(level, ename, empno, mgr) AS (
    SELECT 1, ename, empno, mgr FROM emp WHERE mgr IS NULL
    UNION ALL
    SELECT h.level + 1, e.ename, e.empno, e.mgr FROM emp e JOIN emp_hierarchy h ON e.mgr = h.empno
)
SELECT level, repeat(' ', 2 * (level - 1)) || ename AS name, empno, mgr FROM emp_hierarchy;

CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE) RETURNS void AS $$
DECLARE
    v_deleted_rows INTEGER;
BEGIN
    WITH deleted_rows AS (
        DELETE FROM active_data
        WHERE record_date < p_archive_date
        RETURNING record_id, record_data
    )
    INSERT INTO archive_table (record_id, record_data)
    SELECT record_id, record_data FROM deleted_rows;

    GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;

    INSERT INTO audit_log (log_message)
    VALUES (v_deleted_rows || ' rows archived.');
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION func_audit_emp_sal() RETURNS TRIGGER AS $$
BEGIN
    IF (NEW.sal <> OLD.sal) THEN
        INSERT INTO emp_audit(emp_id, old_sal, new_sal) VALUES (OLD.empno, OLD.sal, NEW.sal);
    END IF;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trg_audit_emp_sal
BEFORE UPDATE OF sal ON emp
FOR EACH ROW
EXECUTE PROCEDURE func_audit_emp_sal();

