CREATE OR REPLACE FUNCTION validate_context() RETURNS void LANGUAGE plpgsql AS $$
DECLARE 
    l_type integer;
    l_owner integer;
    l_ltype varchar(30) := 'FIRST';
    l_status integer;
    l_compile_sql text;
    l_dbo_name varchar(32);
BEGIN
    SELECT usesysid INTO l_owner FROM pg_user WHERE usename = 'ctxsys';
    FOR c1 IN (SELECT dbo_name, dbo_type FROM ctxsys.dr$dbo WHERE dbo_type != 'PUBLIC SYNONYM' ORDER BY dbo_type, dbo_name) LOOP
        IF (c1.dbo_type != l_ltype) THEN
            SELECT CASE c1.dbo_type
                WHEN 'INDEX' THEN 1
                WHEN 'TABLE' THEN 2
                WHEN 'VIEW' THEN 4
                WHEN 'SEQUENCE' THEN 6
                WHEN 'PROCEDURE' THEN 7
                WHEN 'FUNCTION' THEN 8
                WHEN 'PACKAGE' THEN 9
                WHEN 'PACKAGE BODY' THEN 11
                WHEN 'TYPE' THEN 13
                WHEN 'TYPE BODY' THEN 14
                WHEN 'LIBRARY' THEN 22
                WHEN 'INDEXTYPE' THEN 32
                WHEN 'OPERATOR' THEN 33
                ELSE 0
            END INTO l_type;
            l_ltype := c1.dbo_type;
        END IF;

        l_status := -1;
        FOR c2 IN (SELECT relisvalid AS status FROM pg_class WHERE relowner = l_owner AND relname = c1.dbo_name AND relkind = l_type) LOOP
            l_status := c2.status::integer;
        END LOOP;

        IF (l_status != 1) THEN
            l_dbo_name := quote_ident(c1.dbo_name);
            l_compile_sql := CASE c1.dbo_type
                WHEN 'VIEW' THEN 'ALTER VIEW ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PROCEDURE' THEN 'ALTER FUNCTION ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'FUNCTION' THEN 'ALTER FUNCTION ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE' THEN 'ALTER SCHEMA ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE BODY' THEN 'ALTER SCHEMA ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'TYPE' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'TYPE BODY' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'INDEXTYPE' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'OPERATOR' THEN 'ALTER OPERATOR ctxsys.' || l_dbo_name || ' COMPILE'
                ELSE NULL
            END;

            IF l_compile_sql IS NULL THEN
                RAISE NOTICE 'FAILED CHECK FOR %, %', c1.dbo_type, c1.dbo_name;
                PERFORM ctxsys.drue_invalid('CONTEXT');
                RETURN;
            ELSE
                BEGIN
                    EXECUTE l_compile_sql;
                EXCEPTION WHEN OTHERS THEN
                    RAISE NOTICE 'FAILED CHECK FOR %, %', c1.dbo_type, c1.dbo_name;
                    PERFORM ctxsys.drue_invalid('CONTEXT');
                    RETURN;
                END;
            END IF;
        END IF;
    END LOOP;

    PERFORM ctxsys.drue_valid('CONTEXT');

EXCEPTION WHEN OTHERS THEN
    PERFORM ctxsys.drue_text_on_stack(SQLERRM, 'validate_context');
    PERFORM ctxsys.drue_invalid('CONTEXT');
    RAISE;
END;
$$;

DO $$ 
DECLARE
    TYPE t_inventory_rec IS RECORD (product_id INTEGER, current_qty INTEGER, adjustment_qty INTEGER, new_qty INTEGER);
    TYPE t_inventory_tab IS RECORD[];
    v_inventory t_inventory_tab := '{}';
    v_batch_size CONSTANT INTEGER := 1000;
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    r_inventory RECORD;
    c_inventory CURSOR FOR SELECT 
        product_id, 
        quantity_on_hand, 
        CASE $2 
            WHEN 'RECOUNT' THEN physical_count - quantity_on_hand 
            WHEN 'DAMAGE' THEN -damaged_quantity 
            WHEN 'RETURN' THEN returned_quantity 
            ELSE 0 
        END AS adjustment_qty 
    FROM inventory_staging 
    WHERE warehouse_id = $1 AND status = 'PENDING'
    ORDER BY product_id;
BEGIN
    SAVEPOINT bulk_update_start;

    FOR r_inventory IN c_inventory LOOP
        v_inventory := ARRAY_APPEND(v_inventory, ROW(r_inventory.product_id, r_inventory.quantity_on_hand, r_inventory.adjustment_qty));

        IF ARRAY_LENGTH(v_inventory, 1) >= v_batch_size THEN
            FOREACH r_inventory IN ARRAY v_inventory LOOP
                BEGIN
                    PERFORM * FROM inventory inv
                    WHERE inv.product_id = r_inventory.product_id AND inv.warehouse_id = $1;

                    IF FOUND THEN
                        UPDATE inventory
                        SET 
                            quantity_on_hand = quantity_on_hand + r_inventory.adjustment_qty,
                            last_adjustment_date = COALESCE($3, now()::timestamp),
                            last_adjustment_type = $2
                        WHERE product_id = r_inventory.product_id AND warehouse_id = $1;

                        INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
                        VALUES (
                            r_inventory.product_id, 
                            $1, 
                            COALESCE($3, now()::timestamp), 
                            $2, 
                            r_inventory.current_qty, 
                            r_inventory.adjustment_qty, 
                            r_inventory.current_qty + r_inventory.adjustment_qty, 
                            session_user
                        );

                    ELSE
                        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                        VALUES (
                            r_inventory.product_id, 
                            $1, 
                            GREATEST(0, r_inventory.adjustment_qty), 
                            COALESCE($3, now()::timestamp), 
                            $2
                        );

                        INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
                        VALUES (
                            r_inventory.product_id, 
                            $1, 
                            COALESCE($3, now()::timestamp), 
                            $2, 
                            r_inventory.current_qty, 
                            r_inventory.adjustment_qty, 
                            r_inventory.current_qty + r_inventory.adjustment_qty, 
                            session_user
                        );
                    END IF;

                    v_total_processed := v_total_processed + 1;

                EXCEPTION WHEN OTHERS THEN
                    v_error_count := v_error_count + 1;
                    INSERT INTO error_log (error_date, error_message, product_id)
                    VALUES (now(), 'Inventory update failed: ' || SQLERRM, r_inventory.product_id);
                END;
            END LOOP;

            v_inventory := '{}'; -- Reset inventory batch
        END IF;
    END LOOP;

    UPDATE inventory_staging
    SET 
        status = 'PROCESSED', 
        processed_date = now()
    WHERE warehouse_id = $1 AND status = 'PENDING';

    COMMIT;

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;

EXCEPTION 
    WHEN OTHERS THEN
        ROLLBACK TO SAVEPOINT bulk_update_start; 
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END $$;

DO $$
DECLARE 
  p_year INTEGER;
  p_quarter INTEGER;
  p_sales_rep_id INTEGER DEFAULT NULL;
  v_report_id BIGINT;
  v_total_sales NUMERIC := 0;
  v_total_commission NUMERIC := 0;
  v_report_sql TEXT;
  v_start_date DATE;
  v_end_date DATE;

  TYPE t_sales_summary IS RECORD (
    sales_rep_id INTEGER,
    sales_rep_name VARCHAR(100),
    total_sales NUMERIC,
    commission_rate NUMERIC,
    commission_amount NUMERIC
  );

  TYPE t_sales_tab IS TABLE OF t_sales_summary;
  v_sales_data t_sales_tab;

BEGIN
  v_start_date := TO_DATE(p_year::TEXT || '-' || LPAD(((p_quarter - 1) * 3 + 1)::TEXT, 2, '0') || '-01', 'YYYY-MM-DD');
  v_end_date := (v_start_date + INTERVAL '2 months')::DATE + INTERVAL '1 month' - INTERVAL '1 day';

  SELECT nextval('sales_report_seq') INTO v_report_id;

  v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) AS total_sales, 
                  sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount 
                  FROM sales_representatives sr 
                  LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 
                  WHERE ($3 IS NULL OR sr.sales_rep_id = $3) 
                  GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate 
                  ORDER BY total_sales DESC';

  EXECUTE v_report_sql INTO v_sales_data USING v_start_date, v_end_date, p_sales_rep_id;

  INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
  VALUES (v_report_id, p_year, p_quarter, NOW(), ARRAY_LENGTH(v_sales_data, 1));

  FOR i IN 1..ARRAY_LENGTH(v_sales_data, 1) LOOP
    INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, v_sales_data[i].sales_rep_id, v_sales_data[i].sales_rep_name, v_sales_data[i].total_sales, 
            v_sales_data[i].commission_rate, v_sales_data[i].commission_amount);

    v_total_sales := v_total_sales + v_sales_data[i].total_sales;
    v_total_commission := v_total_commission + v_sales_data[i].commission_amount;
  END LOOP;

  UPDATE sales_report_header
  SET total_sales = v_total_sales, total_commission = v_total_commission
  WHERE report_id = v_report_id;

  COMMIT;

  RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;

EXCEPTION
  WHEN OTHERS THEN
    ROLLBACK;
    RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END $$;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories (IN OldCategoryID INT, IN NewCategoryID INT, IN EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName VARCHAR(100);
    EffectiveDateToUse TIMESTAMP;
BEGIN
    -- Set effective date if NULL
    IF EffectiveDate IS NULL THEN
        EffectiveDateToUse := CURRENT_TIMESTAMP;
    ELSE
        EffectiveDateToUse := EffectiveDate;
    END IF;

    -- Check if OldCategoryID and NewCategoryID are the same
    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    -- Count products in the OldCategoryID
    SELECT COUNT(1) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    -- Exit if no products found
    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    FOR ProductID, ProductName IN 
        SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE'
    LOOP
        BEGIN
            -- Insert into ProductCategoryHistory
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDateToUse, SESSION_USER);

            -- Update Products table
            UPDATE Products 
            SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDateToUse, LastModifiedBy = SESSION_USER
            WHERE ProductID = ProductID;

            UpdatedCount := UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            -- Log errors in CategoryUpdateErrors
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate) 
            VALUES (ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    -- Update ProductCategories table
    UPDATE ProductCategories 
    SET LastUpdateDate = EffectiveDateToUse 
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    -- Final output
    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

