CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE) RETURNS VOID AS $$
DECLARE
    v_deleted_rows INTEGER;
BEGIN
    WITH deleted_rows AS (
        DELETE FROM active_data
        WHERE record_date < p_archive_date
        RETURNING record_id, record_data
    )
    INSERT INTO archive_table (record_id, record_data)
    SELECT record_id, record_data FROM deleted_rows;

    GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;

    INSERT INTO audit_log (log_message) VALUES (v_deleted_rows || ' rows archived.');

    COMMIT;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id INTEGER) RETURNS TEXT LANGUAGE plpgsql AS $$
DECLARE
    v_stats RECORD;
    v_result RECORD;
    v_sql TEXT;
    v_dept_name TEXT;
    p_result TEXT := '';
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;

    SELECT COUNT(*) AS total_emp, AVG(salary) AS avg_salary, MAX(salary) AS max_salary INTO v_stats
    FROM employees WHERE department_id = p_dept_id;

    SELECT COUNT(*) INTO v_stats.active_projects
    FROM projects WHERE department_id = p_dept_id AND status = 'ACTIVE';

    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    FOR v_result IN EXECUTE v_sql USING p_dept_id LOOP
        p_result := p_result || format(
            '{"id": %s, "name": "%s", "salary": %s},',
            v_result.employee_id,
            v_result.last_name,
            v_result.salary
        );
    END LOOP;

    p_result := format(
        '{  "department": "%s",  "total_employees": %s,  "salary_stats": {    "average": %s,    "max": %s  },  "active_projects": %s,  "top_earners": [%s]}',
        v_dept_name,
        v_stats.total_emp,
        coalesce(v_stats.avg_salary, 'null'),
        coalesce(v_stats.max_salary, 'null'),
        v_stats.active_projects,
        RTRIM(p_result, ',')
    );

    RETURN p_result;

EXCEPTION
    WHEN OTHERS THEN
        RETURN format('{"error": "%s"}', sqlerrm);
END;
$$;

CREATE OR REPLACE PROCEDURE usp_UpdateOrderStatus (IN CustomerID INT, IN OldStatus VARCHAR(20), IN NewStatus VARCHAR(20), OUT RowsUpdated INT, OUT ErrorMessage VARCHAR(200)) 
LANGUAGE plpgsql AS $$
DECLARE
    OrderCount INT;
    TranCount INT = TXID_CURRENT();
    ProcessingFee NUMERIC(10,2) = 0;
    fee_cursor REFCURSOR;
BEGIN
    IF OldStatus = NewStatus THEN
        ErrorMessage := '新旧状态不能相同';
        RETURN 400;
    END IF;

    BEGIN
        IF pg_try_advisory_xact_lock(TranCount) THEN
            BEGIN
                UPDATE Orders 
                SET Status = NewStatus, LastUpdated = CURRENT_TIMESTAMP 
                WHERE CustomerID = CustomerID 
                AND Status = OldStatus 
                AND OrderDate > (CURRENT_DATE - INTERVAL '3 months');

                GET DIAGNOSTICS RowsUpdated = ROW_COUNT;

                IF RowsUpdated = 0 THEN
                    ErrorMessage := '未找到符合条件的订单';
                    RETURN 404;
                END IF;

                INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
                SELECT OrderID, OldStatus, NewStatus, CURRENT_TIMESTAMP 
                FROM Orders 
                WHERE CustomerID = CustomerID 
                AND Status = NewStatus;

                OPEN fee_cursor FOR SELECT Fee FROM OrderDetails 
                WHERE OrderID IN (SELECT OrderID FROM Orders WHERE CustomerID = CustomerID AND Status = NewStatus);

                LOOP
                    FETCH fee_cursor INTO ProcessingFee;
                    EXIT WHEN NOT FOUND;
                    ProcessingFee := ProcessingFee * 0.95;
                END LOOP;

                CLOSE fee_cursor;

                COMMIT;
                ErrorMessage := '更新成功';
                RETURN 200;
            EXCEPTION WHEN OTHERS THEN
                IF TXID_CURRENT() IS NOT NULL THEN
                    ROLLBACK;
                END IF;
                ErrorMessage := SQLERRM || ' (Line:' || PG_CONTEXT || ')';
                RETURN 500;
            END;
        END IF;
    END;

END;
