CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL, OUT p_result text)
LANGUAGE plpgsql
AS $$
DECLARE
    v_total_emp numeric;
    v_avg_salary numeric;
    v_max_salary numeric;
    v_employee_id numeric;
    v_last_name varchar(50);
    v_salary numeric;
    v_sql text;
    v_dept_name varchar(50);
    v_rows_updated integer := 0;
    v_total_budget numeric := 0;
    dept_rec RECORD;
    v_cur REFCURSOR;
BEGIN
    SELECT department_name INTO v_dept_name
    FROM departments
    WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    OPEN v_cur FOR EXECUTE v_sql USING p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp || ', "salary_stats": { "average": ' || v_avg_salary || ', "max": ' || v_max_salary || ' }, "top_earners": [';

    LOOP
        FETCH v_cur INTO v_employee_id, v_last_name, v_salary;
        EXIT WHEN NOT FOUND;
        p_result := p_result || ' {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;
    p_result := rtrim(p_result, ',') || ' ] }';
    CLOSE v_cur;

    FOR dept_rec IN
        SELECT department_id, budget
        FROM departments
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
          AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE employees
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
        WHERE department_id = dept_rec.department_id;
        GET DIAGNOSTICS v_rows_updated = v_rows_updated + ROW_COUNT;

        SELECT SUM(salary) INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$$;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL) RETURNS text AS $$
DECLARE
    v_total_emp numeric;
    v_avg_salary numeric;
    v_max_salary numeric;
    v_employee_id numeric;
    v_last_name varchar(50);
