    OPEN v_cur_cursor FOR EXECUTE v_sql USING p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp ||
                ', "salary_stats": { "average": ' || v_avg_salary || ', "max": ' || v_max_salary || ' }, "top_earners": [';

    LOOP
        FETCH v_cur_cursor INTO v_employee_id, v_last_name, v_salary;
        EXIT WHEN NOT FOUND;
        p_result := p_result || ' {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;
    CLOSE v_cur_cursor;

    p_result := RTRIM(p_result, ',') || ' ] }';

    FOR dept_rec IN
        SELECT department_id, budget
        FROM departments
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
          AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE employees
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100)
        WHERE department_id = dept_rec.department_id;
        GET DIAGNOSTICS v_rows_updated = v_rows_updated + ROW_COUNT;

        SELECT SUM(salary) INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

