CREATE OR REPLACE PROCEDURE SP_DataMigration(IN SourceTable NVARCHAR2(128), IN TargetTable NVARCHAR2(128), IN BatchSize INTEGER DEFAULT 10000)
AS
    SQL_TEXT TEXT;
    SourceCount INTEGER;
    TargetCount INTEGER;
    BatchCount INTEGER := 0;
    MigrationID INTEGER;
    ErrorMessage VARCHAR2(4000);
BEGIN
    BEGIN
        EXECUTE IMMEDIATE 'INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (''' || SourceTable || ''', ''' || TargetTable || ''', CURRENT_TIMESTAMP, ''RUNNING'')';
        SELECT currval(pg_get_serial_sequence('MigrationLog','MigrationID')) INTO MigrationID;
        SQL_TEXT := 'SELECT COUNT(*) FROM ' || SourceTable;
        EXECUTE IMMEDIATE SQL_TEXT INTO SourceCount;
        EXECUTE IMMEDIATE 'UPDATE MigrationLog SET SourceCount = ' || SourceCount || ' WHERE MigrationID = ' || MigrationID;
        EXECUTE IMMEDIATE 'DELETE FROM ' || TargetTable;
        WHILE BatchCount < SourceCount LOOP
            SQL_TEXT := 'INSERT INTO ' || TargetTable || ' SELECT * FROM (SELECT * FROM ' || SourceTable || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = s.ID) LIMIT ' || BatchSize || ') sub';
            EXECUTE IMMEDIATE SQL_TEXT;
            GET DIAGNOSTICS TargetCount = ROW_COUNT;
            IF TargetCount = 0 THEN
                EXIT;
            END IF;
            BatchCount := BatchCount + TargetCount;
        END LOOP;
        SQL_TEXT := 'SELECT COUNT(*) FROM ' || TargetTable;
        EXECUTE IMMEDIATE SQL_TEXT INTO TargetCount;
        EXECUTE IMMEDIATE 'UPDATE MigrationLog SET EndTime = CURRENT_TIMESTAMP, TargetCount = ' || TargetCount || ', Status = CASE WHEN ' || SourceCount || ' = ' || TargetCount || ' THEN ''SUCCESS'' ELSE ''WARNING'' END WHERE MigrationID = ' || MigrationID;
        RAISE NOTICE 'Migration completed for %, Source: %, Target: %', SourceTable, SourceCount, TargetCount;
    EXCEPTION
        WHEN OTHERS THEN
            ErrorMessage := SQLERRM;
            EXECUTE IMMEDIATE 'UPDATE MigrationLog SET EndTime = CURRENT_TIMESTAMP, Status = ''FAILED'', ErrorMessage = ''' || replace(ErrorMessage,'''','''''') || ''' WHERE MigrationID = ' || MigrationID;
            RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
    END;
END;

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable VARCHAR(128), TargetTable VARCHAR(128), BatchSize INTEGER DEFAULT 10000)
AS
DECLARE
    SQL_TEXT TEXT;
    SourceCount INTEGER;
    TargetCount INTEGER;
    BatchCount INTEGER := 0;
    MigrationID INTEGER;
    ErrorMessage TEXT;
    ROWS_AFFECTED INTEGER;
BEGIN
    BEGIN
        INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
        VALUES (SourceTable, TargetTable, NOW(), 'RUNNING');
        SELECT currval(pg_get_serial_sequence('MigrationLog','MigrationID')) INTO MigrationID;
        SQL_TEXT := format('SELECT count(*) FROM %I', SourceTable);
        EXECUTE SQL_TEXT INTO SourceCount;
        UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;
        SQL_TEXT := format('DELETE FROM %I', TargetTable);
        EXECUTE SQL_TEXT;
        WHILE BatchCount < SourceCount LOOP
            SQL_TEXT := format(
                'INSERT INTO %I SELECT * FROM (SELECT * FROM %I s WHERE NOT EXISTS (SELECT 1 FROM %I t WHERE t.ID = s.ID) LIMIT %s) sub',
                TargetTable, SourceTable, TargetTable, BatchSize
            );
            EXECUTE SQL_TEXT;
            GET DIAGNOSTICS ROWS_AFFECTED = ROW_COUNT;
            IF ROWS_AFFECTED = 0 THEN
                EXIT;
            END IF;
            BatchCount := BatchCount + ROWS_AFFECTED;
        END LOOP;
        SQL_TEXT := format('SELECT count(*) FROM %I', TargetTable);
        EXECUTE SQL_TEXT INTO TargetCount;
        UPDATE MigrationLog
        SET EndTime = NOW(),
            TargetCount = TargetCount,
            Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END
        WHERE MigrationID = MigrationID;
        RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
    EXCEPTION
        WHEN OTHERS THEN
            GET STACKED DIAGNOSTICS ErrorMessage = MESSAGE_TEXT;
            UPDATE MigrationLog
            SET EndTime = NOW(),
                Status = 'FAILED',
                ErrorMessage = ErrorMessage
            WHERE MigrationID = MigrationID;
            RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
    END;
END;
/

