CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id numeric, OUT p_result text) RETURNS text AS $$
DECLARE
    v_total_emp numeric;
    v_avg_salary numeric;
    v_max_salary numeric;
    v_active_projects numeric;
    v_employee_id numeric;
    v_last_name varchar(50);
    v_salary numeric(8,2);
    v_sql text;
    v_dept_name varchar(50);
    v_result text := '';
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    SELECT COUNT(*)
    INTO v_active_projects
    FROM projects
    WHERE department_id = p_dept_id AND status = 'ACTIVE';

    v_sql := format('SELECT employee_id, last_name, salary FROM employees WHERE department_id = %s ORDER BY salary DESC LIMIT 5', p_dept_id);

    p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || v_avg_salary || ',    "max": ' || v_max_salary || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';

    FOR v_employee_id, v_last_name, v_salary IN EXECUTE v_sql LOOP
        p_result := p_result || '    {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;

    p_result := rtrim(p_result, ',') || '  ]}';
    RETURN;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
        RETURN;
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
        RETURN;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE usp_updateorderstatus(IN customerid INT, IN oldstatus VARCHAR(20), IN newstatus VARCHAR(20), OUT rowsupdated INT, OUT errormessage VARCHAR(200))
AS
DECLARE
    ordercount INT;
    trancount INT := (SELECT COUNT(*) FROM pg_catalog.pg_locks WHERE locktype='transactionid');
    processingfee NUMERIC(10,2) := 0;
BEGIN
    IF oldstatus = newstatus THEN
        errormessage := '新旧状态不能相同';
        rowsupdated := 0;
        RETURN;
    END IF;

    BEGIN
        IF trancount = 0 THEN
            START TRANSACTION;
        END IF;

        UPDATE orders
        SET status = newstatus,
            lastupdated = NOW()
        WHERE customerid = customerid
          AND status = oldstatus
          AND orderdate > (NOW() + INTERVAL '-3 month');

        GET DIAGNOSTICS rowsupdated = ROW_COUNT;

        IF rowsupdated = 0 THEN
            errormessage := '未找到符合条件的订单';
            IF trancount = 0 THEN
                ROLLBACK;
            END IF;
            RETURN;
        END IF;

        INSERT INTO orderlog(orderid, oldstatus, newstatus, changedate)
        SELECT orderid, oldstatus, newstatus, NOW()
        FROM orders
        WHERE customerid = customerid
          AND status = newstatus;

        FOR fee_rec IN
            SELECT fee
            FROM orderdetails
            WHERE orderid IN (
                SELECT orderid
                FROM orders
                WHERE customerid = customerid
                  AND status = newstatus
            )
        LOOP
            processingfee := fee_rec.fee * 0.95;
        END LOOP;

        IF trancount = 0 THEN
            COMMIT;
        END IF;

        errormessage := '更新成功';
    EXCEPTION
        WHEN OTHERS THEN
            IF trancount = 0 THEN
                ROLLBACK;
            END IF;
            errormessage := SQLERRM;
    END;
END;
/

