CREATE OR REPLACE FUNCTION validate_context()
RETURNS void AS $$
DECLARE
    l_type integer;
    l_owner integer;
    l_ltype varchar(30) := 'FIRST';
    l_status integer;
    l_compile_sql text;
    l_dbo_name varchar(32);
BEGIN
    SELECT usesysid INTO l_owner FROM pg_catalog.pg_user WHERE usename = 'ctsys';
    FOR c1 IN SELECT dbo_name, dbo_type FROM ctxsys.dr_dbo WHERE dbo_type != 'PUBLIC SYNONYM' ORDER BY dbo_type, dbo_name LOOP
        IF c1.dbo_type != l_ltype THEN
            SELECT CASE c1.dbo_type
                WHEN 'INDEX' THEN 1
                WHEN 'TABLE' THEN 2
                WHEN 'VIEW' THEN 4
                WHEN 'SEQUENCE' THEN 6
                WHEN 'PROCEDURE' THEN 7
                WHEN 'FUNCTION' THEN 8
                WHEN 'PACKAGE' THEN 9
                WHEN 'PACKAGE BODY' THEN 11
                WHEN 'TYPE' THEN 13
                WHEN 'TYPE BODY' THEN 14
                WHEN 'LIBRARY' THEN 22
                WHEN 'INDEXTYPE' THEN 32
                WHEN 'OPERATOR' THEN 33
                ELSE 0
            END INTO l_type;
            l_ltype := c1.dbo_type;
        END IF;
        l_status := -1;
        FOR c2 IN SELECT status FROM sys.obj$ WHERE owner = l_owner AND name = c1.dbo_name AND type = l_type LOOP
            l_status := c2.status;
        END LOOP;
        IF l_status != 1 THEN
            l_dbo_name := quote_ident(c1.dbo_name);
            l_compile_sql := CASE c1.dbo_type
                WHEN 'VIEW' THEN 'ALTER VIEW ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PROCEDURE' THEN 'ALTER PROCEDURE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'FUNCTION' THEN 'ALTER FUNCTION ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE BODY' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'TYPE' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'TYPE BODY' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'INDEXTYPE' THEN 'ALTER INDEX ctxsys.' || l_dbo_name || ' REINDEX'
                WHEN 'OPERATOR' THEN NULL
                ELSE NULL
            END;
            IF l_compile_sql IS NULL THEN
                RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
                PERFORM dbms_registry_invalid('CONTEXT');
                RETURN;
            ELSE
                BEGIN
                    EXECUTE l_compile_sql;
                EXCEPTION WHEN OTHERS THEN
                    RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
                    PERFORM dbms_registry_invalid('CONTEXT');
                    RETURN;
                END;
            END IF;
        END IF;
    END LOOP;
    PERFORM dbms_registry_valid('CONTEXT');
EXCEPTION WHEN OTHERS THEN
    PERFORM ctxsys_drue_text_on_stack(SQLERRM, 'validate_context');
    PERFORM dbms_registry_invalid('CONTEXT');
    PERFORM ctxsys_drue_raise();
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id numeric, p_adjustment_type varchar, p_adjustment_date date DEFAULT now()) RETURNS void AS $$
DECLARE
    v_inventory RECORD;
    v_batch_size CONSTANT integer := 1000;
    v_total_processed integer := 0;
    v_error_count integer := 0;
    c_inventory CURSOR FOR
        SELECT product_id,
               quantity_on_hand AS current_qty,
               CASE p_adjustment_type
                   WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                   WHEN 'DAMAGE' THEN -damaged_quantity
                   WHEN 'RETURN' THEN returned_quantity
                   ELSE 0
               END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
          AND status = 'PENDING'
        ORDER BY product_id;
BEGIN
    SAVEPOINT bulk_update_start;
    OPEN c_inventory;
    LOOP
        FETCH c_inventory INTO v_inventory;
        EXIT WHEN NOT FOUND;
        BEGIN
            WITH src AS (
                SELECT v_inventory.product_id AS product_id,
                       p_warehouse_id AS warehouse_id,
                       v_inventory.adjustment_qty AS adj_qty
            )
            INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
            SELECT s.product_id, s.warehouse_id, GREATEST(0, s.adj_qty), p_adjustment_date, p_adjustment_type
            FROM src s
            ON CONFLICT (product_id, warehouse_id) DO UPDATE
            SET quantity_on_hand = inventory.quantity_on_hand + EXCLUDED.quantity_on_hand,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type;
            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (v_inventory.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_inventory.current_qty, v_inventory.adjustment_qty, v_inventory.current_qty + v_inventory.adjustment_qty, current_user);
            v_total_processed := v_total_processed + 1;
        EXCEPTION WHEN OTHERS THEN
            v_error_count := v_error_count + 1;
            INSERT INTO error_log (error_date, error_message, product_id)
            VALUES (now(), 'Inventory update failed: ' || SQLERRM, v_inventory.product_id);
        END;
    END LOOP;
    CLOSE c_inventory;
    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = now()
    WHERE warehouse_id = p_warehouse_id
      AND status = 'PENDING';
    COMMIT;
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION WHEN OTHERS THEN
    ROLLBACK TO SAVEPOINT bulk_update_start;
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year numeric, p_quarter numeric, p_sales_rep_id numeric DEFAULT NULL) RETURNS void AS $$
DECLARE
    v_report_id numeric;
    v_total_sales numeric := 0;
    v_total_commission numeric := 0;
    v_report_sql text;
    v_sales_data RECORD;
    v_start_date date;
    v_end_date date;
BEGIN
    v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + interval '2 month')::date + (interval '1 month' - interval '1 day');
    SELECT nextval('sales_report_seq') INTO v_report_id;
    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';
    FOR v_sales_data IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, v_sales_data.sales_rep_id, v_sales_data.sales_rep_name, v_sales_data.total_sales, v_sales_data.commission_rate, v_sales_data.commission_amount);
        v_total_sales := v_total_sales + v_sales_data.total_sales;
        v_total_commission := v_total_commission + v_sales_data.commission_amount;
    END LOOP;
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, now(), (SELECT COUNT(*) FROM sales_representatives WHERE p_sales_rep_id IS NULL OR sales_rep_id = p_sales_rep_id));
    UPDATE sales_report_header
    SET total_sales = v_total_sales,
        total_commission = v_total_commission
    WHERE report_id = v_report_id;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION WHEN OTHERS THEN
    ROLLBACK;
    RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(IN WarehouseID INTEGER, IN AdjustmentType NVARCHAR(50), IN AdjustmentDate TIMESTAMP DEFAULT NULL)
AS
DECLARE
    ProductID INTEGER;
    CurrentQty INTEGER;
    AdjustmentQty INTEGER;
    NewQty INTEGER;
    TotalProcessed INTEGER := 0;
    ErrorCount INTEGER := 0;
    ErrorMessage NVARCHAR(4000);
    CURSOR inventory_cursor IS
        SELECT ProductID,
               QuantityOnHand,
               CASE AdjustmentType
                   WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                   WHEN 'DAMAGE' THEN -DamagedQuantity
                   WHEN 'RETURN' THEN ReturnedQuantity
                   ELSE 0
               END AS AdjustmentQty
        FROM InventoryStaging
        WHERE WarehouseID = WarehouseID
          AND Status = 'PENDING'
        ORDER BY ProductID;
BEGIN
    IF AdjustmentDate IS NULL THEN
        AdjustmentDate := NOW();
    END IF;

    BEGIN
        OPEN inventory_cursor;
        FETCH inventory_cursor INTO ProductID, CurrentQty, AdjustmentQty;
        WHILE inventory_cursor%FOUND LOOP
            BEGIN
                NewQty := CurrentQty + AdjustmentQty;
                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = ProductID AND WarehouseID = WarehouseID) THEN
                    UPDATE Inventory
                       SET QuantityOnHand = NewQty,
                           LastAdjustmentDate = AdjustmentDate,
                           LastAdjustmentType = AdjustmentType
                     WHERE ProductID = ProductID
                       AND WarehouseID = WarehouseID;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (ProductID, WarehouseID, CASE WHEN NewQty < 0 THEN 0 ELSE NewQty END, AdjustmentDate, AdjustmentType);
                END IF;

                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, CurrentQty, AdjustmentQty, NewQty, SESSION_USER);

                TotalProcessed := TotalProcessed + 1;
            EXCEPTION
                WHEN OTHERS THEN
                    ErrorCount := ErrorCount + 1;
                    INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                    VALUES (NOW(), 'Inventory update failed: ' || SQLERRM, ProductID);
            END;
            FETCH inventory_cursor INTO ProductID, CurrentQty, AdjustmentQty;
        END LOOP;
        CLOSE inventory_cursor;

        UPDATE InventoryStaging
           SET Status = 'PROCESSED',
               ProcessedDate = NOW()
         WHERE WarehouseID = WarehouseID
           AND Status = 'PENDING';

        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', TotalProcessed, ErrorCount;
    EXCEPTION
        WHEN OTHERS THEN
            ErrorMessage := SQLERRM;
            IF inventory_cursor%ISOPEN THEN
                CLOSE inventory_cursor;
            END IF;
            RAISE EXCEPTION 'Bulk inventory update failed: %', ErrorMessage;
    END;
END;

CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(IN PayPeriodStart TIMESTAMP, IN PayPeriodEnd TIMESTAMP, IN DepartmentID INTEGER DEFAULT NULL)
AS
DECLARE
    PayrollID INTEGER;
    EmployeeID INTEGER;
    HourlyRate NUMERIC(10,2);
    RegularHours NUMERIC(5,2);
    OvertimeHours NUMERIC(5,2);
    GrossPay NUMERIC(10,2);
    TaxDeduction NUMERIC(10,2);
    NetPay NUMERIC(10,2);
    TotalGross NUMERIC(15,2) := 0;
    TotalNet NUMERIC(15,2) := 0;
    CURSOR employee_cursor IS
        SELECT e.EmployeeID,
               e.HourlyRate,
               COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
               COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID
                             AND t.WorkDate BETWEEN PayPeriodStart AND PayPeriodEnd
        WHERE e.Status = 'ACTIVE'
          AND (DepartmentID IS NULL OR e.DepartmentID = DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID;
BEGIN
    BEGIN
        INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
        VALUES (PayPeriodStart, PayPeriodEnd, DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING')
        RETURNING PayrollID INTO PayrollID;

        OPEN employee_cursor;
        LOOP
            FETCH employee_cursor INTO EmployeeID, HourlyRate, RegularHours, OvertimeHours;
            EXIT WHEN employee_cursor%NOTFOUND;

            GrossPay := (RegularHours * HourlyRate) + (OvertimeHours * HourlyRate * 1.5);
            TaxDeduction := GrossPay * 0.20;
            NetPay := GrossPay - TaxDeduction;

            INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
            VALUES (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay);

            TotalGross := TotalGross + GrossPay;
            TotalNet := TotalNet + NetPay;
        END LOOP;
        CLOSE employee_cursor;

        UPDATE PayrollHeader
           SET TotalGrossPay = TotalGross,
               TotalNetPay = TotalNet,
               Status = 'COMPLETED',
               CompletedDate = CURRENT_TIMESTAMP
         WHERE PayrollID = PayrollID;

        RAISE NOTICE 'Payroll processing completed. Payroll ID: %', PayrollID;
    EXCEPTION
        WHEN OTHERS THEN
            IF employee_cursor%ISOPEN THEN
                CLOSE employee_cursor;
            END IF;
            UPDATE PayrollHeader
               SET Status = 'FAILED',
                   ErrorMessage = SQLERRM
             WHERE PayrollID = PayrollID;
            RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
    END;
END;
/

CREATE OR REPLACE PROCEDURE SP_CleanupOldLogs(retentiondays INT DEFAULT 90)
AS
DECLARE
    cutoffdate TIMESTAMP;
    deletedcount INT;
BEGIN
    cutoffdate := CURRENT_TIMESTAMP + INTERVAL '-' || retentiondays || ' day';
    BEGIN
        DELETE FROM ApplicationLogs WHERE LogDate < cutoffdate;
        GET DIAGNOSTICS deletedcount = ROW_COUNT;

        DELETE FROM ErrorLogs WHERE ErrorDate < cutoffdate;
        GET DIAGNOSTICS deletedcount = deletedcount + ROW_COUNT;

        DELETE FROM AuditLogs WHERE AuditDate < cutoffdate;
        GET DIAGNOSTICS deletedcount = deletedcount + ROW_COUNT;

        INSERT INTO CleanupHistory (CleanupDate, TableName, RecordsDeleted, RetentionDays)
        VALUES (CURRENT_TIMESTAMP, 'MULTIPLE_LOG_TABLES', deletedcount, retentiondays);

        RAISE NOTICE 'Cleanup completed. % records deleted.', deletedcount;
    EXCEPTION
        WHEN OTHERS THEN
            RAISE EXCEPTION 'Log cleanup failed: %', SQLERRM;
    END;
END;
/

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable NVARCHAR2(128), TargetTable NVARCHAR2(128), BatchSize INTEGER DEFAULT 10000)
AS
    SQL_stmt TEXT;
    SourceCount INTEGER;
    TargetCount INTEGER;
    BatchCount INTEGER := 0;
    MigrationID INTEGER;
    ErrorMessage NVARCHAR2(4000);
BEGIN
    BEGIN
        EXECUTE IMMEDIATE 'INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (:1, :2, CURRENT_TIMESTAMP, ''RUNNING'')' USING SourceTable, TargetTable;
        SELECT currval(pg_get_serial_sequence('MigrationLog','MigrationID')) INTO MigrationID;

        SQL_stmt := 'SELECT COUNT(*) FROM ' || SourceTable;
        EXECUTE IMMEDIATE SQL_stmt INTO SourceCount;

        EXECUTE IMMEDIATE 'UPDATE MigrationLog SET SourceCount = ' || SourceCount || ' WHERE MigrationID = ' || MigrationID;

        SQL_stmt := 'DELETE FROM ' || TargetTable;
        EXECUTE IMMEDIATE SQL_stmt;

        WHILE BatchCount < SourceCount LOOP
            SQL_stmt := 'INSERT INTO ' || TargetTable || 
                        ' SELECT * FROM (SELECT * FROM ' || SourceTable || 
                        ' WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = ' || SourceTable || '.ID) LIMIT ' || BatchSize || ') sub';
            EXECUTE IMMEDIATE SQL_stmt;
            GET DIAGNOSTICS TargetCount = ROW_COUNT;
            IF TargetCount = 0 THEN
                EXIT;
            END IF;
            BatchCount := BatchCount + TargetCount;
        END LOOP;

        SQL_stmt := 'SELECT COUNT(*) FROM ' || TargetTable;
        EXECUTE IMMEDIATE SQL_stmt INTO TargetCount;

        EXECUTE IMMEDIATE 'UPDATE MigrationLog SET EndTime = CURRENT_TIMESTAMP, TargetCount = ' || TargetCount || ', Status = ' ||
                          'CASE WHEN ' || SourceCount || ' = ' || TargetCount || ' THEN ''SUCCESS'' ELSE ''WARNING'' END WHERE MigrationID = ' || MigrationID;

        RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
    EXCEPTION
        WHEN OTHERS THEN
            ErrorMessage := SQLERRM;
            EXECUTE IMMEDIATE 'UPDATE MigrationLog SET EndTime = CURRENT_TIMESTAMP, Status = ''FAILED'', ErrorMessage = ' || quote_literal(ErrorMessage) || ' WHERE MigrationID = ' || MigrationID;
            RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
    END;
END;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INTEGER, IN NewCategoryID INTEGER, IN EffectiveDate TIMESTAMP DEFAULT NULL)
AS
DECLARE
    ProductCount INTEGER;
    UpdatedCount INTEGER := 0;
    ProductID INTEGER;
    ProductName VARCHAR(100);
    EffectiveDateVar TIMESTAMP;
    product_cursor CURSOR FOR SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDateVar := NOW();
    ELSE
        EffectiveDateVar := EffectiveDate;
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO ProductID, ProductName;
        EXIT WHEN NOT FOUND;
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDateVar, SESSION_USER);

            UPDATE Products
            SET CategoryID = NewCategoryID,
                LastModifiedDate = EffectiveDateVar,
                LastModifiedBy = SESSION_USER
            WHERE ProductID = ProductID;

            UpdatedCount := UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (ProductID, SQLERRM, NOW());
        END;
    END LOOP;
    CLOSE product_cursor;

    UPDATE ProductCategories
    SET LastUpdateDate = EffectiveDateVar
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE PROCEDURE p_job_create_bond_info_all()
AS
DECLARE
    colname varchar(100);
    sqlstr text;
    c_names CURSOR FOR
        SELECT column_name
        FROM information_schema.columns
        WHERE table_name='t_pj_bond_info_all'
          AND column_name LIKE 'def%';
BEGIN
    TRUNCATE TABLE tmp_bond_info_all_20010;
    INSERT INTO tmp_bond_info_all_20010(f_val,def_id,id)
    SELECT field_value,field_definition_id,t2.id
    FROM t_object_field_data t1,t_pj_bond_info t2
    WHERE t1.object_no = 20010
      AND t1.field_value IS NOT NULL
      AND t1.parent_id = t2.apply_id;

    TRUNCATE TABLE tmp_bond_info_all_20020;
    INSERT INTO tmp_bond_info_all_20020(f_val,def_id,id)
    SELECT field_value,field_definition_id,t2.id AS bondId
    FROM t_object_field_data t1,t_pj_bond_info t2
    WHERE t1.object_no = 20020
      AND t1.field_value IS NOT NULL
      AND t1.parent_id = t2.temp_bondid;

    TRUNCATE TABLE t_pj_bond_info_all;
    INSERT INTO t_pj_bond_info_all(id,temp_bondid,apply_id)
    SELECT t1.id,t1.temp_bondid,t1.apply_id
    FROM t_pj_bond_info t1;

    OPEN c_names;
    LOOP
        FETCH c_names INTO colname;
        EXIT WHEN NOT FOUND;
        sqlstr := 'UPDATE t_pj_bond_info_all a SET '||colname||'=b.f_val FROM tmp_bond_info_all_20020 b WHERE a.id=b.id AND b.def_id='||replace(colname,'def_','');
        EXECUTE IMMEDIATE sqlstr;
        sqlstr := 'UPDATE t_pj_bond_info_all a SET '||colname||'=b.f_val FROM tmp_bond_info_all_20010 b WHERE a.id=b.id AND a.'||colname||' IS NULL AND b.def_id='||replace(colname,'def_','');
        EXECUTE IMMEDIATE sqlstr;
    END LOOP;
    CLOSE c_names;
END;
