CREATE OR REPLACE PROCEDURE SP_TSA_WARNING3(I_VESSEL_CODE IN VARCHAR2, I_MANAGER IN VARCHAR2, I_DURATION IN VARCHAR2, I_SOG IN VARCHAR2, I_AREA IN VARCHAR2, OUT_CURSOR OUT SYS_REFCURSOR) IS
  OBJ_TAB_TYPE OBJECT_TABLE := OBJECT_TABLE();
  OFFICE_TAB_TYPE OBJECT_TABLE := OBJECT_TABLE();
  I NUMBER;
  LN NUMBER;
  V_MANAGER VARCHAR2(50);
  V_SOG VARCHAR2(50);
  V_DURATION VARCHAR2(50);
  V_SORTCODE NUMBER;
BEGIN
  I:=1;
  LN:=1;
  IF NVL(I_MANAGER,'00000000')='00000000' THEN
    V_MANAGER:=NULL;
  ELSE
    V_MANAGER:=I_MANAGER;
    FOR LS IN (select OFFICE_CODE from sys_office where nvl(is_dept,1)=0 start with OFFICE_CODE=V_MANAGER connect by NOCYCLE supperior_office_CODE=prior office_CODE) LOOP
      OFFICE_TAB_TYPE.EXTEND;
      OFFICE_TAB_TYPE(LN) := OBJECTTABTYPE(LS.OFFICE_CODE,'','','','','','', '', NULL,'','','','','');
      LN:=LN+1;
    END LOOP;
  END IF;
  select PARA_VALUE INTO V_SOG from TSA_MONITOR_CONF WHERE PARA_TYPE='MAXSPEED';
  select PARA_VALUE INTO V_DURATION from TSA_MONITOR_CONF WHERE PARA_TYPE='MONITOR_DURATION';
  FOR X IN (SELECT * FROM (SELECT VW.VESSEL_MMSI, V.VESSEL_ID, TO_DATE(SUBSTR(VW.VESSEL_POSIDATE,1,19),'YYYY-MM-DD HH24:MI:SS') AS ACTIVITY_DATE, VW.VESSEL_LAT AS LAT, VW.VESSEL_LON AS LON, V.VESSEL_CODE, V.VESSEL_NAME, VW.VESSEL_SPEED, CASE WHEN VW.VESSEL_STATE IN ('0','8') THEN '1' ELSE '0' END AS SAILING_STATUS, VM.MANAGER AS OFFICE_CODE, VM.manager_dept AS OFFICE_DEPT_CODE, (SELECT AA.OFFICE_NAME FROM SYS_OFFICE AA, VOP_VESSEL_MANAGEMENT BB WHERE AA.OFFICE_CODE=BB.MANAGER AND BB.VESSEL_ID=V.VESSEL_ID AND BB.MANAGEMENT_TYPE=1 AND ((sysdate >= BB.DEFINITION_DATE AND sysdate <= BB.END_DATE) OR (sysdate >= BB.DEFINITION_DATE AND BB.END_DATE IS NULL)) AND ROWNUM=1) AS manager_name, VW.VESSEL_ETA AS ETA_TIME, VW.VESSEL_DEST AS ETA_PORT_NAME, VW.VESSEL_COURSE FROM ZDWEB.VM_ZYHY2_ALLSHIPDYNAMIC@CSBC_TO_XE1 VW, VOP_VESSEL V, VOP_VESSEL_MANAGEMENT VM WHERE NVL(to_char(VW.VESSEL_MMSI),'无')=to_char(V.MMSI) AND V.VESSEL_ID=VM.VESSEL_ID AND (v.is_delete <> '1' OR v.is_delete IS NULL) AND V.VESSEL_CODE NOT IN (SELECT VESSEL_CODE FROM TSA_VESSEL_EXCLUDE) AND NVL(VM.IS_DELETE,'0')<>'1' AND v.trade_type='0' AND v.mmsi IS NOT NULL AND NVL(V.TETIRED_FLAG,'0')='1' AND VM.MANAGEMENT_TYPE=1 AND ((sysdate >= VM.DEFINITION_DATE AND sysdate <= VM.END_DATE) OR (sysdate >= VM.DEFINITION_DATE AND VM.END_DATE IS NULL))) MM INNER JOIN SYS_OFFICE_RELATION_TREE SORT ON MM.OFFICE_CODE=SORT.CHILD_CODE AND sort.office_code=nvl(V_MANAGER,'ABC') WHERE (I_VESSEL_CODE IS NULL OR MM.VESSEL_CODE IN (SELECT COLUMN_VALUE FROM TABLE(FN_STR2TYTABLE(I_VESSEL_CODE,','))))) LOOP
    FOR M IN (SELECT H.* FROM TSA_AREA H WHERE H.DATE_START <= SYSDATE AND AREA_NAME NOT IN ('其他海区','委内瑞拉监控区','马六甲海峡临时监控区') AND (I_AREA IS NULL OR AREA_ID IN (SELECT COLUMN_VALUE FROM TABLE(FN_STR2TYTABLE(I_AREA,',')))) AND NVL(H.DATE_END, TO_DATE('2999-01-01','YYYY-MM-DD')) >= SYSDATE) LOOP
      IF FN_TSA_IS_IN_HD_AREA(X.LON, X.LAT, M.AREA_ID) THEN
        OBJ_TAB_TYPE.EXTEND;
        OBJ_TAB_TYPE(I) := OBJECTTABTYPE(X.VESSEL_CODE, X.VESSEL_NAME, X.VESSEL_SPEED, X.VESSEL_COURSE, X.SAILING_STATUS, X.VESSEL_ID, X.LON, X.LAT, X.ACTIVITY_DATE, M.AREA_NAME, X.MANAGER_NAME, X.ETA_PORT_NAME, X.ETA_TIME, X.VESSEL_MMSI);
        I := I + 1;
      END IF;
    END LOOP;
  END LOOP;
  select (select nvl(max(SORTCODE)+1,1) from TSA_VESSEL_IN_AREA) INTO V_SORTCODE from dual;
  OPEN OUT_CURSOR FOR SELECT * FROM (SELECT SYS_GUID() AS VESSEL_IN_AREA_ID, AA.VESSEL_CODE "VESSEL_CODE", AA.VESSEL_NAME "VESSEL_NAME", AA.MANAGER_NAME "MANAGER_NAME", FN_GET_VESSEL_MNT_CODE(AA.VESSEL_CODE,SYSDATE(),1,0) AS MANAGER_CODE, FN_GET_VESSEL_MNT_CODE(AA.VESSEL_CODE,SYSDATE(),1,1) AS MANAGER_DEPT_CODE, FN_TSA_ALERT_IS_SEND(AA.VESSEL_CODE) AS IS_SEND, AA.MMSI "MMSI", AA.ACTIVITY_DATE "ACTIVITY_DATE", CASE WHEN AA.LONGTITUDE>0 THEN LPAD(TO_CHAR(TRUNC(ABS(TO_NUMBER(AA.LONGTITUDE)))),3,'0')||LPAD(TO_CHAR(ROUND((ABS((TO_NUMBER(AA.LONGTITUDE)-TO_NUMBER(TRUNC(AA.LONGTITUDE))))*60),0)),2,'0')||'E' ELSE LPAD(TO_CHAR(TRUNC(ABS(TO_NUMBER(AA.LONGTITUDE)))),3,'0')||LPAD(TO_CHAR(ROUND((ABS((TO_NUMBER(AA.LONGTITUDE)-TO_NUMBER(TRUNC(AA.LONGTITUDE))))*60),0)),2,'0')||'W' END AS "LONGTITUDE", CASE WHEN AA.LATITUDE>0 THEN LPAD(TO_CHAR(TRUNC(ABS(TO_NUMBER(AA.LATITUDE)))),2,'0')||LPAD(TO_CHAR(ROUND((ABS((TO_NUMBER(AA.LATITUDE)-TO_NUMBER(TRUNC(AA.LATITUDE))))*60),0)),2,'0')||'N' ELSE LPAD(TO_CHAR(TRUNC(ABS(TO_NUMBER(AA.LATITUDE)))),2,'0')||LPAD(TO_CHAR(ROUND((ABS((TO_NUMBER(AA.LATITUDE)-TO_NUMBER(TRUNC(AA.LATITUDE))))*60),0)),2,'0')||'S' END AS "LATITUDE", AA.VESSEL_SPPED "VESSEL_SPPED", AA.VESSEL_COURSE "VESSEL_COURSE", FN_TSA_GET_MAX_RATE(AA.MMSI,V_DURATION) AS "AVERAGE_SPPED", CASE WHEN NVL(AA.SAILING_STATUS,'0')='1' THEN '在航' ELSE '非在航' END AS "SAILING_STATUS", AA.AREA_NAME "AREA_NAME", AA.ETA_PORT_NAME "ETA_PORT_NAME", AA.ETA_TIME "ETA_TIME", V_SORTCODE AS SORTCODE, 'AUTO' AS created_by_user, sysdate AS created_dtm_loc, 0 AS record_version, 0 AS principal_group_code FROM TABLE(OBJ_TAB_TYPE) AA WHERE FN_TSA_GET_MAX_RATE(AA.MMSI,V_DURATION)<=TO_NUMBER(NVL(V_SOG,'10')) UNION ALL SELECT SYS_GUID() AS VESSEL_IN_AREA_ID, 'RUN......' AS VESSEL_CODE, '仍在执行' AS VESSEL_NAME, '00000000' AS SAFETY_COMPANY, '080200000000' AS MANAGER_CODE, '00000000' AS MANAGER_DEPT_CODE, 99 AS IS_SEND, '00000000' AS MMSI, sysdate AS POSI_DATE, '' AS POSI_LONG, '' AS POSI_LAT, '' AS VESSEL_SPPED, '' AS VESSEL_COURSE, 0 AS AVERAGE_SPPED, '' AS SAILING_STATUS, '' AS AREA_NAME, '' AS ETA_PORT_NAME, '' AS ETA_TIME, V_SORTCODE AS SORTCODE, 'AUTO' AS created_by_user, sysdate AS created_dtm_loc, 0 AS record_version, 0 AS principal_group_code FROM dual);
EXCEPTION WHEN OTHERS THEN DBMS_OUTPUT.PUT_LINE('错误: ' || SQLERRM);
END SP_TSA_WARNING3;

CREATE OR REPLACE PROCEDURE SP_FEE_CSC_DAILY_JOB
IS
  V_FLAG VARCHAR2(50);
BEGIN
  SELECT OPEN_MODE INTO V_FLAG FROM V$DATABASE;
  IF V_FLAG = 'READ WRITE' THEN
    P_LOG_EXCEPTION('开始时间:' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*更新上远业务口径船舶信息*/
    /*delete from FMM_CSCL_BUSI_CALIBER where 1=1;*/
    EXECUTE IMMEDIATE 'truncate table FMM_CSCL_BUSI_CALIBER';
    INSERT INTO FMM_CSCL_BUSI_CALIBER SELECT * FROM VOP_VESSEL_CSCL_BUSI_CALIBER;
    COMMIT;
    P_LOG_EXCEPTION('FMM_CSCL_BUSI_CALIBER完成' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*更新上远预估费用*/
    /*delete from fmm_csc_maintenance_esti;*/
    EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_esti';
    INSERT INTO fmm_csc_maintenance_esti SELECT * FROM vw_csc_maintenance_esti;
    COMMIT;
    P_LOG_EXCEPTION('fmm_csc_maintenance_esti 完成' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*更新所有订单预估费用*/
    /*delete from fmm_csc_maintenance_orders;*/
    EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_orders';
    INSERT INTO fmm_csc_maintenance_orders SELECT * FROM VW_CSC_MAINTENANCE_ORDERS;
    COMMIT;
    P_LOG_EXCEPTION('fmm_csc_maintenance_orders 完成' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*更新上远账单费用*/
    /*delete from fmm_csc_maintenance_invoice;*/
    EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_invoice';
    INSERT INTO fmm_csc_maintenance_invoice SELECT * FROM vw_csc_maintenance_invoice;
    COMMIT;
    INSERT INTO fmm_csc_maintenance_invoice
      SELECT TEMP.VESSEL_CODE, TEMP.SUPPLIER_CODE, TEMP.PAYER, NULL AS PORT_CODE, TEMP.INVOICE_STATUS, TEMP.INVOICE_SORT, TEMP.ORDER_NO, TEMP.PAYMENT_NO, TEMP.INVOICE_SORT_DETAIL, TEMP.INVOICE_DETAIL_ID, TEMP.BASE_AMOUNT_NOTAX, TEMP.INVOICE_DATE, TEMP.CURRENCY, TEMP.DTL_AMOUNT_NOTAX, TEMP.INVOICE_NUM, TEMP.PAYEE, TEMP.ACTIVITY_DATE, TEMP.FI_RECEIVEDATE, TEMP.INPUT_DATE, TEMP.UPDATE_TIME, TEMP.BASE_AMOUNT, TEMP.AMOUNT, 0 AS MISC_AMOUNT, 0 AS BASE_MISC_AMOUNT,
      NVL(PKG_OAS.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, NVL(TEMP.ACTIVITY_DATE, SYSDATE), 'VEL_MANAGE_BODY'),
          PKG_OAS.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, SYSDATE, 'VEL_MANAGE_BODY')) AS COMPANY_CODE,
      TEMP.SEND_FIN_DATE, TEMP.APPLY_TYPE, '2' AS BUSI_TYPE, TEMP.IS_ENERGY, TEMP.TYPE_NAME, TEMP.ORDER_CURRENCY, TEMP.EXCHANGE_RATE, NULL AS BILL_NO,
      NVL(PKG_OAS.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, NVL(TEMP.ACTIVITY_DATE, SYSDATE), 'COST_BODY'),
          PKG_OAS.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, SYSDATE, 'COST_BODY')) AS OWNERSHIP_BODY,
      TEMP.OPRT_WAY, TEMP.USD_AMOUNT
    FROM (
      SELECT I.VESSEL_CODE,
             SUBSTR(I.PO_NUMBER, 1, REPLACE(INSTR(I.PO_NUMBER, '-', -1) - 1, -1, LENGTH(I.PO_NUMBER))) AS ORDER_NO,
             BFM.CONFIRM_DATE AS ORDER_DATE,
             T.DISPLAY_VALUE_CN AS TYPE_NAME,
             BFM.CURRENCY AS ORDER_CURRENCY,
             BFM.AMOUNT AS TOTAL_FEE,
             NVL(BFM.SUPPLIER, I.PAYEE) AS SUPPLIER_CODE,
             'HY' AS APPLY_TYPE,
             I.PAYER,
             I.INVOICE_STATUS,
             'FY33' AS INVOICE_SORT,
             (CASE WHEN I.INVOICE_SORT_DETAIL = 'FY03001' THEN 'FY33002'
                   WHEN I.INVOICE_SORT_DETAIL = 'FY03002' THEN 'FY33003'
                   WHEN I.INVOICE_SORT_DETAIL = 'FY03003' THEN 'FY33004'
                   WHEN I.INVOICE_SORT_DETAIL = 'FY03004' THEN 'FY33005'
                   WHEN I.INVOICE_SORT_DETAIL IN ('FY03005','FY03006','FY03007') THEN 'FY33006'
                   ELSE 'FY33006'
              END) AS INVOICE_SORT_DETAIL,
             'RLF' AS GROUP_SORT,
             (CASE WHEN I.INVOICE_SORT_DETAIL = 'FY03001' THEN 'YZ30003'
                   WHEN I.INVOICE_SORT_DETAIL = 'FY03002' THEN 'YZ30001'
                   WHEN I.INVOICE_SORT_DETAIL = 'FY03003' THEN 'YZ30002'
                   WHEN I.INVOICE_SORT_DETAIL IN ('FY03004','FY03005','FY03006','FY03007') THEN 'YZ30004'
                   ELSE 'YZ30004'
              END) AS GROUP_SORT_DETAIL,
             I.INVOICE_DETAIL_ID,
             NVL(I.BASE_AMOUNT, I.DTL_AMOUNT * NVL(I.RMB_EXCHANGE_RATE, 1)) / (1 + NVL(I.TAX_RATE, 0)) AS BASE_AMOUNT_NOTAX,
             I.INVOICE_DATE, I.CURRENCY,
             I.DTL_AMOUNT / (1 + I.TAX_RATE) AS DTL_AMOUNT_NOTAX,
             I.INVOICE_NUM, I.PAYEE,
             (CASE WHEN I.COMPANY_CODE IN ('08020000','04000000','02000000','62000000') THEN I.CREATED_DTM_LOC
                   WHEN I.PAYER LIKE '64%' THEN I.CREATED_DTM_LOC
                   ELSE BFM.SUPPLY_DATE
              END) AS ACTIVITY_DATE,
             I.SEND_FIN_DATE AS FI_RECEIVEDATE,
             NULL AS IS_ENERGY,
             I.CREATED_DTM_LOC AS INPUT_DATE,
             NVL(NVL(I.UPDATED_DTM_LOC, I.CREATED_DTM_LOC), SYSDATE - 1) AS UPDATE_TIME,
             '3' AS rpt_type,
             NVL(I.BASE_AMOUNT, I.DTL_AMOUNT * NVL(I.RMB_EXCHANGE_RATE, 1)) AS BASE_AMOUNT,
             I.DTL_AMOUNT AS AMOUNT,
             I.COMPANY_CODE,
             NVL(I.SEND_FIN_DATE, I.INVOICE_DATE) AS SEND_FIN_DATE,
             ROW_NUMBER() OVER (PARTITION BY I.INVOICE_DETAIL_ID ORDER BY BFM.SUPPLY_DATE DESC) rn,
             I.PAYMENT_NO,
             I.RMB_EXCHANGE_RATE AS EXCHANGE_RATE,
             '1' AS OPRT_WAY,
             ROUND(I.DTL_AMOUNT * FN_GET_EXCHANGE_RATE(I.CURRENCY, 'USD', BFM.SUPPLY_DATE), 2) AS USD_AMOUNT
      FROM BFM_INVOICE_DETAIL I
      LEFT JOIN BFM_PURCHASE_ORDER BFM ON SUBSTR(I.PO_NUMBER, 1, REPLACE(INSTR(I.PO_NUMBER, '-', -1) - 1, -1, LENGTH(I.PO_NUMBER))) = BFM.PO_NUMBER
      LEFT JOIN BFM_OIL_STORAGE BOS ON BOS.OPERATE = '10' AND SUBSTR(BOS.PO_NUMBER, 1, REPLACE(INSTR(BOS.PO_NUMBER, '-', -1) - 1, -1, LENGTH(BOS.PO_NUMBER))) = BFM.PO_NUMBER AND BOS.OIL_TYPE = '002'
      LEFT JOIN CDM_CODEDICT T ON T.CODE_TYPE = 'BFM_FILL_TYPE' AND BFM.FILL_OIL_TYPE = T.CODE_VALUE
      LEFT JOIN SAP_JA0012_MTR_MIDDLE M ON BOS.PO_NUMBER = M.TRD_ORDER
      WHERE I.INVOICE_SORT = 'FY03' AND NVL(I.IS_DELETE, '0') <> '1' AND I.PAYER LIKE '64%'
    ) TEMP
    WHERE RN = 1;
    P_LOG_EXCEPTION('fmm_csc_maintenance_invoice 完成' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*根据主数据更新备件信息表是否库存标记*/
    UPDATE SPS_PARTS_INFO SP
    SET SP.IF_STORAGED = NULL
    WHERE EXISTS (
      SELECT TRIM(t.group_code)
      FROM SPS_PARTS_MASTER_DATA t
      WHERE t.if_storaged IS NULL AND TRIM(t.group_code) = SP.group_code
    )
    AND (SP.VESSEL_CODE LIKE 'S%' OR SP.VESSEL_CODE LIKE 'E%');
    /*更新上远虚拟预算数据*/
    EXECUTE IMMEDIATE 'truncate table fmm_csc_virtual_budget';
    INSERT INTO fmm_csc_virtual_budget SELECT * FROM vw_csc_virtual_budget;
    COMMIT;
    P_LOG_EXCEPTION('fmm_csc_virtual_budget 完成' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    /*更新集运备件申领设备ID为空的记录*/
    UPDATE SPS_APPLY_main SAM
    SET (SAM.EQUIPMENT_INFO_ID, SAM.EQUIPMENT_CODE) =
      (SELECT SAD.EQUIPMENT_ID, SAD.EQUIP_CODE
       FROM SPS_APPLY_DETAIL SAD
       WHERE SAD.APPLY_MAIN_ID = SAM.APPLY_MAIN_ID AND ROWNUM = 1)
    WHERE SAM.APPLY_MAIN_ID IN (
      SELECT SAM.APPLY_MAIN_ID FROM SPS_APPLY_main SAM
      WHERE SAM.EQUIPMENT_INFO_ID IS NULL
        AND SAM.VESSEL_CODE IN (
          SELECT VV.VESSEL_CODE FROM DSS_VOP_VESSEL_INFO VV
          WHERE VV.VESMAN_CODE = '68280000'
            AND NVL(VV.TETIRED_FLAG, '1') = '1'
            AND NVL(VV.TRADE_TYPE, '0') = '0'
        )
    );
    /*更新集运备件询价设备ID为空的记录*/
    UPDATE SPS_ENQUIRE_MAIN SS
    SET (SS.EQUIPMENT_INFO_ID, SS.EQUIPMENT_CODE) =
      (SELECT SED.EQUIPMENT_ID, SED.EQUIP_CODE
       FROM SPS_ENQUIRE_DETAIL SED
       WHERE SED.ENQUIRE_MAIN_ID = SS.ENQUIRE_MAIN_ID AND ROWNUM = 1)
    WHERE SS.ENQUIRE_MAIN_ID IN (
      SELECT SEM.ENQUIRE_MAIN_ID FROM SPS_ENQUIRE_MAIN SEM
      WHERE SEM.EQUIPMENT_INFO_ID IS NULL
        AND SEM.VESSEL_CODE IN (
          SELECT VV.VESSEL_CODE FROM DSS_VOP_VESSEL_INFO VV
          WHERE VV.VESMAN_CODE = '68280000'
            AND NVL(VV.TETIRED_FLAG, '1') = '1'
            AND NVL(VV.TRADE_TYPE, '0') = '0'
        )
    );
    COMMIT;
    P_LOG_EXCEPTION(SQLCODE || SQLERRM || 'insert 结束时间:' || SYSDATE, 'SP_FEE_CSC_DAILY_JOB');
    COMMIT;
  END IF;
END SP_FEE_CSC_DAILY_JOB;

CREATE OR REPLACE FUNCTION validate_context() RETURNS void AS $$
DECLARE
  l_type integer;
  l_owner integer;
  l_ltype text := 'FIRST';
  l_status integer;
  l_compile_sql text;
  l_dbo_name text;
BEGIN
  SELECT 0 INTO l_owner;
  FOR c1 IN (
    SELECT dbo_name, dbo_type
    FROM ctxsys.dr$dbo
    WHERE dbo_type <> 'PUBLIC SYNONYM'
    ORDER BY dbo_type, dbo_name
  ) LOOP
    IF c1.dbo_type <> l_ltype THEN
      SELECT CASE c1.dbo_type
        WHEN 'INDEX' THEN 1
        WHEN 'TABLE' THEN 2
        WHEN 'VIEW' THEN 4
        WHEN 'SEQUENCE' THEN 6
        WHEN 'PROCEDURE' THEN 7
        WHEN 'FUNCTION' THEN 8
        WHEN 'PACKAGE' THEN 9
        WHEN 'PACKAGE BODY' THEN 11
        WHEN 'TYPE' THEN 13
        WHEN 'TYPE BODY' THEN 14
        WHEN 'LIBRARY' THEN 22
        WHEN 'INDEXTYPE' THEN 32
        WHEN 'OPERATOR' THEN 33
        ELSE 0
      END INTO l_type;
      l_ltype := c1.dbo_type;
    END IF;

    l_status := -1;
    FOR c2 IN (
      SELECT status
      FROM sys.obj$
      WHERE owner# = l_owner
        AND name = c1.dbo_name
        AND type# = l_type
    ) LOOP
      l_status := c2.status;
    END LOOP;

    IF l_status <> 1 THEN
      l_dbo_name := quote_ident(c1.dbo_name);
      l_compile_sql := CASE c1.dbo_type
        WHEN 'VIEW' THEN 'ALTER VIEW ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'PROCEDURE' THEN 'ALTER PROCEDURE ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'FUNCTION' THEN 'ALTER FUNCTION ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'PACKAGE' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'PACKAGE BODY' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE BODY'
        WHEN 'TYPE' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'TYPE BODY' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE BODY'
        WHEN 'INDEXTYPE' THEN 'ALTER INDEXTYPE ctxsys.' || l_dbo_name || ' COMPILE'
        WHEN 'OPERATOR' THEN 'ALTER OPERATOR ctxsys.' || l_dbo_name || ' COMPILE'
        ELSE NULL
      END;

      IF l_compile_sql IS NULL THEN
        RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
        RETURN;
      ELSE
        BEGIN
          EXECUTE l_compile_sql;
        EXCEPTION
          WHEN OTHERS THEN
            RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
            RETURN;
        END;
      END IF;
    END IF;
  END LOOP;

  RETURN;
EXCEPTION
  WHEN OTHERS THEN
    RAISE;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id integer, p_adjustment_type varchar, p_adjustment_date date DEFAULT current_date) RETURNS void AS
$BODY$
DECLARE
  v_batch_size integer := 1000;
  v_offset integer := 0;
  v_batch_count integer;
  v_total_processed integer := 0;
  v_error_count integer := 0;
  rec RECORD;
BEGIN
  SAVEPOINT bulk_update_start;

  LOOP
    v_batch_count := 0;
    FOR rec IN
      SELECT product_id,
             quantity_on_hand AS current_qty,
             CASE p_adjustment_type
               WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
               WHEN 'DAMAGE' THEN -damaged_quantity
               WHEN 'RETURN' THEN returned_quantity
               ELSE 0
             END AS adjustment_qty
      FROM inventory_staging
      WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
      ORDER BY product_id
      LIMIT v_batch_size OFFSET v_offset
    LOOP
      v_batch_count := v_batch_count + 1;
      BEGIN
        UPDATE inventory
        SET quantity_on_hand = quantity_on_hand + rec.adjustment_qty,
            last_adjustment_date = p_adjustment_date,
            last_adjustment_type = p_adjustment_type
        WHERE product_id = rec.product_id AND warehouse_id = p_warehouse_id;

        IF NOT FOUND THEN
          INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
          VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
        END IF;

        INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
        VALUES (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, current_user);

        v_total_processed := v_total_processed + 1;

      EXCEPTION WHEN OTHERS THEN
        v_error_count := v_error_count + 1;
        INSERT INTO error_log (error_date, error_message, product_id)
        VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, rec.product_id);
      END;
    END LOOP;

    EXIT WHEN v_batch_count = 0;
    v_offset := v_offset + v_batch_size;
  END LOOP;

  UPDATE inventory_staging SET status = 'PROCESSED', processed_date = CURRENT_TIMESTAMP
  WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';

  RAISE NOTICE 'Bulk inventory update completed. Processed: %', v_total_processed;
  RETURN;
EXCEPTION
  WHEN OTHERS THEN
    ROLLBACK TO SAVEPOINT bulk_update_start;
    RAISE;
END;
$BODY$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL)
RETURNS void AS
$$
DECLARE
  v_report_id integer;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_report_sql text;
  v_start_date date;
  v_end_date date;
  rec RECORD;
  v_rep_count integer := 0;
BEGIN
  v_start_date := make_date(p_year, ((p_quarter - 1) * 3) + 1, 1);
  v_end_date := (make_date(p_year, p_quarter * 3, 1) + interval '1 month' - interval '1 day')::date;

  SELECT nextval('sales_report_seq') INTO v_report_id;

  v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) AS total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';

  FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
     INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
     VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);

     v_total_sales := v_total_sales + rec.total_sales;
     v_total_commission := v_total_commission + rec.commission_amount;
     v_rep_count := v_rep_count + 1;
  END LOOP;

  INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
  VALUES (v_report_id, p_year, p_quarter, NOW(), v_rep_count);

  UPDATE sales_report_header
  SET total_sales = v_total_sales,
      total_commission = v_total_commission
  WHERE report_id = v_report_id;

  RETURN;
END;
$$ LANGUAGE plpgsql;

CREATE PROCEDURE SP_BulkInventoryUpdate(
  p_WarehouseID INTEGER,
  p_AdjustmentType VARCHAR(50),
  p_AdjustmentDate TIMESTAMP WITHOUT TIME ZONE DEFAULT NULL
)
LANGUAGE plpgsql
AS $$
DECLARE
  v_ProductID INTEGER;
  v_CurrentQty INTEGER;
  v_AdjustmentQty INTEGER;
  v_NewQty INTEGER;

  v_TotalProcessed INTEGER := 0;
  v_ErrorCount INTEGER := 0;
  v_ErrorMessage TEXT;
  v_AdjustmentDate TIMESTAMP WITHOUT TIME ZONE;
BEGIN
  IF p_AdjustmentDate IS NULL THEN
    v_AdjustmentDate := CURRENT_TIMESTAMP;
  ELSE
    v_AdjustmentDate := p_AdjustmentDate;
  END IF;

  FOR rec IN
    SELECT st.productid, st.quantityonhand,
      CASE p_AdjustmentType
        WHEN 'RECOUNT' THEN st.physicalcount - st.quantityonhand
        WHEN 'DAMAGE' THEN -st.damagedquantity
        WHEN 'RETURN' THEN st.returnedquantity
        ELSE 0
      END AS adjustmentqty
    FROM InventoryStaging st
    WHERE st.warehouseid = p_WarehouseID AND st.status = 'PENDING'
    ORDER BY st.productid
  LOOP
    v_ProductID := rec.productid;
    v_CurrentQty := rec.quantityonhand;
    v_AdjustmentQty := rec.adjustmentqty;

    BEGIN
      v_NewQty := v_CurrentQty + v_AdjustmentQty;

      IF EXISTS (SELECT 1 FROM Inventory inv WHERE inv.ProductID = v_ProductID AND inv.WarehouseID = p_WarehouseID) THEN
        UPDATE Inventory
        SET QuantityOnHand = v_NewQty,
            LastAdjustmentDate = v_AdjustmentDate,
            LastAdjustmentType = p_AdjustmentType
        WHERE ProductID = v_ProductID AND WarehouseID = p_WarehouseID;
      ELSE
        INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
        VALUES (v_ProductID, p_WarehouseID, CASE WHEN v_NewQty < 0 THEN 0 ELSE v_NewQty END, v_AdjustmentDate, p_AdjustmentType);
      END IF;

      INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
      VALUES (v_ProductID, p_WarehouseID, v_AdjustmentDate, p_AdjustmentType, v_CurrentQty, v_AdjustmentQty, v_NewQty, current_user);

      v_TotalProcessed := v_TotalProcessed + 1;
    EXCEPTION WHEN OTHERS THEN
      v_ErrorCount := v_ErrorCount + 1;
      INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
      VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, v_ProductID);
    END;
  END LOOP;

  UPDATE InventoryStaging
  SET Status = 'PROCESSED', ProcessedDate = CURRENT_TIMESTAMP
  WHERE WarehouseID = p_WarehouseID AND Status = 'PENDING';

  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_TotalProcessed, v_ErrorCount;
EXCEPTION WHEN OTHERS THEN
  v_ErrorMessage := SQLERRM;
  RAISE EXCEPTION 'Bulk inventory update failed: %', v_ErrorMessage;
END;
$$;

CREATE PROCEDURE SP_ProcessPayroll(IN p_pay_period_start TIMESTAMP, IN p_pay_period_end TIMESTAMP, IN p_department_id INTEGER DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
  v_payroll_id INTEGER;
  v_employee_id INTEGER;
  v_hourly_rate NUMERIC(10,2);
  v_regular_hours NUMERIC(5,2);
  v_overtime_hours NUMERIC(5,2);
  v_gross_pay NUMERIC(10,2);
  v_tax_deduction NUMERIC(10,2);
  v_net_pay NUMERIC(10,2);
  v_total_gross NUMERIC(15,2) := 0;
  v_total_net NUMERIC(15,2) := 0;
BEGIN
  INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
  VALUES (p_pay_period_start, p_pay_period_end, p_department_id, CURRENT_TIMESTAMP, 'PROCESSING')
  RETURNING PayrollID INTO v_payroll_id;

  FOR v_employee_id, v_hourly_rate, v_regular_hours, v_overtime_hours IN
      SELECT e.EmployeeID, e.HourlyRate, COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
             COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
      FROM Employees e
      LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN p_pay_period_start AND p_pay_period_end
      WHERE e.Status = 'ACTIVE' AND (p_department_id IS NULL OR e.DepartmentID = p_department_id)
      GROUP BY e.EmployeeID, e.HourlyRate
      ORDER BY e.EmployeeID
  LOOP
    v_gross_pay := (v_regular_hours * v_hourly_rate) + (v_overtime_hours * v_hourly_rate * 1.5);
    v_tax_deduction := v_gross_pay * 0.20;
    v_net_pay := v_gross_pay - v_tax_deduction;

    INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
    VALUES (v_payroll_id, v_employee_id, v_hourly_rate, v_regular_hours, v_overtime_hours, v_gross_pay, v_tax_deduction, v_net_pay);

    v_total_gross := v_total_gross + v_gross_pay;
    v_total_net := v_total_net + v_net_pay;
  END LOOP;

  UPDATE PayrollHeader
  SET TotalGrossPay = v_total_gross, TotalNetPay = v_total_net, Status = 'COMPLETED', CompletedDate = CURRENT_TIMESTAMP
  WHERE PayrollID = v_payroll_id;

  RAISE NOTICE 'Payroll processing completed. Payroll ID: %', v_payroll_id;
EXCEPTION
  WHEN OTHERS THEN
    UPDATE PayrollHeader SET Status = 'FAILED', ErrorMessage = SQLERRM WHERE PayrollID = COALESCE(v_payroll_id, -1);
    RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
END;
$$;

CREATE PROCEDURE SP_CleanupOldLogs(IN p_RetentionDays INTEGER DEFAULT 90)
LANGUAGE plpgsql
AS
$$
DECLARE
  v_cutoff_date TIMESTAMP;
  v_deleted_count BIGINT := 0;
  v_tmp BIGINT;
BEGIN
  v_cutoff_date := NOW() - MAKE_INTERVAL(days => p_RetentionDays);

  DELETE FROM ApplicationLogs WHERE LogDate < v_cutoff_date;
  GET DIAGNOSTICS v_deleted_count = ROW_COUNT;

  DELETE FROM ErrorLogs WHERE ErrorDate < v_cutoff_date;
  GET DIAGNOSTICS v_tmp = ROW_COUNT;
  v_deleted_count := v_deleted_count + v_tmp;

  DELETE FROM AuditLogs WHERE AuditDate < v_cutoff_date;
  GET DIAGNOSTICS v_tmp = ROW_COUNT;
  v_deleted_count := v_deleted_count + v_tmp;

  INSERT INTO CleanupHistory (CleanupDate, TableName, RecordsDeleted, RetentionDays)
  VALUES (NOW(), 'MULTIPLE_LOG_TABLES', v_deleted_count, p_RetentionDays);

  RAISE NOTICE 'Cleanup completed. % records deleted.', v_deleted_count;
EXCEPTION
  WHEN OTHERS THEN
    RAISE EXCEPTION 'Log cleanup failed: %', SQLERRM;
END;
$$;

CREATE PROCEDURE SP_DataMigration(
  p_SourceTable TEXT,
  p_TargetTable TEXT,
  p_BatchSize INTEGER DEFAULT 10000
)
LANGUAGE plpgsql
AS $$
DECLARE
  v_SQL TEXT;
  v_SourceCount INTEGER;
  v_TargetCount INTEGER;
  v_BatchCount INTEGER := 0;
  v_MigrationID INTEGER;
  v_ErrorMessage TEXT;
  v_RowCount INTEGER;
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
  VALUES (p_SourceTable, p_TargetTable, CURRENT_TIMESTAMP, 'RUNNING')
  RETURNING MigrationID INTO v_MigrationID;

  v_SQL := 'SELECT COUNT(*) FROM ' || quote_ident(p_SourceTable);
  EXECUTE v_SQL INTO v_SourceCount;

  UPDATE MigrationLog
  SET SourceCount = v_SourceCount
  WHERE MigrationID = v_MigrationID;

  v_SQL := 'DELETE FROM ' || quote_ident(p_TargetTable);
  EXECUTE v_SQL;

  WHILE v_BatchCount < v_SourceCount LOOP
    v_SQL := 'INSERT INTO ' || quote_ident(p_TargetTable) ||
             ' SELECT * FROM ' || quote_ident(p_SourceTable) ||
             ' WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(p_TargetTable) || ' t WHERE t.ID = ' ||
             quote_ident(p_SourceTable) || '.ID)' ||
             ' LIMIT ' || p_BatchSize::TEXT;

    EXECUTE v_SQL;
    GET DIAGNOSTICS v_RowCount = ROW_COUNT;
    IF v_RowCount = 0 THEN
      EXIT;
    END IF;
    v_BatchCount := v_BatchCount + v_RowCount;
  END LOOP;

  v_SQL := 'SELECT COUNT(*) FROM ' || quote_ident(p_TargetTable);
  EXECUTE v_SQL INTO v_TargetCount;

  UPDATE MigrationLog
  SET EndTime = CURRENT_TIMESTAMP,
      TargetCount = v_TargetCount,
      Status = CASE WHEN v_SourceCount = v_TargetCount THEN 'SUCCESS' ELSE 'WARNING' END
  WHERE MigrationID = v_MigrationID;

  RAISE NOTICE 'Migration completed for %: Source: %, Target: %', p_SourceTable, v_SourceCount, v_TargetCount;
EXCEPTION WHEN OTHERS THEN
  v_ErrorMessage := SQLERRM;
  UPDATE MigrationLog
  SET EndTime = CURRENT_TIMESTAMP,
      Status = 'FAILED',
      ErrorMessage = v_ErrorMessage
  WHERE MigrationID = v_MigrationID;
  RAISE EXCEPTION 'Data migration failed: %', v_ErrorMessage;
END;
$$;

CREATE PROCEDURE SP_UpdateProductCategories(
  OldCategoryID INT,
  NewCategoryID INT,
  EffectiveDate TIMESTAMP WITHOUT TIME ZONE DEFAULT NULL
)
LANGUAGE plpgsql
AS $$
DECLARE
  ProductCount INT;
  UpdatedCount INT := 0;
  rec RECORD;
  EffectiveDateTs TIMESTAMP WITHOUT TIME ZONE;
BEGIN
  IF EffectiveDate IS NULL THEN
    EffectiveDateTs := CURRENT_TIMESTAMP;
  ELSE
    EffectiveDateTs := EffectiveDate;
  END IF;

  IF OldCategoryID = NewCategoryID THEN
    RAISE EXCEPTION 'Old and new category IDs cannot be the same';
  END IF;

  SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
  IF ProductCount = 0 THEN
    RAISE NOTICE 'No active products found in category %', OldCategoryID;
    RETURN;
  END IF;

  FOR rec IN SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE'
  LOOP
    BEGIN
      INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
      VALUES (rec.ProductID, OldCategoryID, NewCategoryID, EffectiveDateTs, CURRENT_USER);
      UPDATE Products
      SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDateTs, LastModifiedBy = CURRENT_USER
      WHERE ProductID = rec.ProductID;
      UpdatedCount := UpdatedCount + 1;
    EXCEPTION WHEN OTHERS THEN
      INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
      VALUES (rec.ProductID, SQLERRM, CURRENT_TIMESTAMP);
    END;
  END LOOP;

  UPDATE ProductCategories
  SET LastUpdateDate = EffectiveDateTs
  WHERE CategoryID IN (OldCategoryID, NewCategoryID);

  RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE GETEMPLOYEESTATS(p_dept_id IN NUMBER, p_result OUT CLOB) IS
  TYPE stats_rec IS RECORD (total_emp NUMBER, avg_salary NUMBER, max_salary NUMBER, active_projects NUMBER);
  TYPE p_result_rec IS RECORD (employee_id NUMBER, last_name VARCHAR2(50), salary NUMBER(8,2));
  v_stats stats_rec;
  v_result p_result_rec;
  v_cursor SYS_REFCURSOR;
  v_sql VARCHAR2(1000);
  v_dept_name VARCHAR2(50);
BEGIN
  SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;
  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary FROM EMPLOYEES WHERE department_id = p_dept_id;
  SELECT COUNT(*) INTO v_stats.active_projects FROM PROJECTS WHERE department_id = p_dept_id AND status = 'ACTIVE';
  v_sql := 'SELECT * FROM (SELECT employee_id, last_name, salary FROM employees WHERE department_id = :1 ORDER BY salary DESC) WHERE ROWNUM <= 5';
  OPEN v_cursor FOR v_sql USING p_dept_id;
  p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_stats.total_emp || ',  "salary_stats": {    "average": ' || v_stats.avg_salary || ',    "max": ' || v_stats.max_salary || '  },  "active_projects": ' || v_stats.active_projects || ',  "top_earners": [';
  LOOP
    FETCH v_cursor INTO v_result;
    EXIT WHEN v_cursor%NOTFOUND;
    p_result := p_result || '    {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
  END LOOP;
  p_result := RTRIM(p_result, ',') || '  ]}';
  CLOSE v_cursor;
EXCEPTION
  WHEN NO_DATA_FOUND THEN p_result := '{"error": "Department not found"}';
  WHEN OTHERS THEN p_result := '{"error": "' || SQLERRM || '"}';
END;

