CREATE PROCEDURE usp_UpdateOrderStatus(
  IN p_CustomerID INT,
  IN p_OldStatus VARCHAR(20),
  IN p_NewStatus VARCHAR(20),
  INOUT p_RowsUpdated INT,
  OUT p_ErrorMessage VARCHAR(200)
)
LANGUAGE plpgsql
AS $$
DECLARE
  v_rows_updated INT;
  v_sqlstate TEXT;
  v_message TEXT;
  rec RECORD;
  ProcessingFee DECIMAL(10,2) := 0;
BEGIN
  IF p_OldStatus = p_NewStatus THEN
    p_ErrorMessage := '新旧状态不能相同';
    p_RowsUpdated := 0;
    RETURN;
  END IF;

  BEGIN
    UPDATE Orders
      SET Status = p_NewStatus, LastUpdated = NOW()
    WHERE CustomerID = p_CustomerID
      AND Status = p_OldStatus
      AND OrderDate > NOW() - INTERVAL '3 months';
    GET DIAGNOSTICS v_rows_updated := ROW_COUNT;
    p_RowsUpdated := v_rows_updated;

    IF v_rows_updated = 0 THEN
      p_ErrorMessage := '未找到符合条件的订单';
      RETURN;
    END IF;

    INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
      SELECT OrderID, p_OldStatus, p_NewStatus, NOW()
      FROM Orders
      WHERE CustomerID = p_CustomerID AND Status = p_NewStatus;

    ProcessingFee := 0;
    FOR rec IN SELECT Fee FROM OrderDetails WHERE OrderID IN (
        SELECT OrderID FROM Orders WHERE CustomerID = p_CustomerID AND Status = p_NewStatus
    )
    LOOP
      ProcessingFee := ProcessingFee * 0.95;
    END LOOP;

    p_ErrorMessage := '更新成功';
    RETURN;
  EXCEPTION WHEN OTHERS THEN
    GET STACKED DIAGNOSTICS v_sqlstate := RETURNED_SQLSTATE, v_message := MESSAGE_TEXT;
    p_ErrorMessage := COALESCE(v_message, 'Unknown error') || ' (Line: ' || COALESCE(v_sqlstate, '') || ')';
    RETURN;
  END;
END;
$$;

