CREATE OR REPLACE PROCEDURE usp_updateorderstatus(
  p_customer_id INT,
  p_old_status VARCHAR(20),
  p_new_status VARCHAR(20),
  OUT p_rows_updated INT,
  OUT p_error_message VARCHAR(200)
)
LANGUAGE plpgsql
AS
$$
DECLARE
  v_rows_updated INT;
  v_processing_fee DECIMAL(10,2) := 0;
  v_rec RECORD;
BEGIN
  IF p_old_status = p_new_status THEN
    p_error_message := '新旧状态不能相同';
    p_rows_updated := 0;
    RETURN;
  END IF;

  UPDATE Orders
  SET Status = p_new_status,
      LastUpdated = NOW()
  WHERE CustomerID = p_customer_id
    AND Status = p_old_status
    AND OrderDate > NOW() - INTERVAL '3 months';

  GET DIAGNOSTICS v_rows_updated := ROW_COUNT;
  p_rows_updated := v_rows_updated;

  IF v_rows_updated = 0 THEN
    p_error_message := '未找到符合条件的订单';
    RETURN;
  END IF;

  INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
  SELECT OrderID, p_old_status, p_new_status, NOW()
  FROM Orders
  WHERE CustomerID = p_customer_id AND Status = p_new_status;

  FOR v_rec IN
    SELECT Fee
    FROM OrderDetails
    WHERE OrderID IN (
      SELECT OrderID FROM Orders WHERE CustomerID = p_customer_id AND Status = p_new_status
    )
  LOOP
    v_processing_fee := v_processing_fee * 0.95;
  END LOOP;

  p_error_message := '更新成功';
  RETURN;
EXCEPTION WHEN OTHERS THEN
  p_error_message := SQLERRM;
  RETURN;
END;
$$;

