CREATE OR REPLACE FUNCTION validate_context()
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
l_type int;
l_owner int;
l_ltype varchar(30) := 'FIRST';
l_status int;
l_compile_sql varchar(2000);
l_dbo_name varchar(32);
BEGIN
SELECT usesysid INTO l_owner FROM pg_catalog.pg_user WHERE usename = 'ctxsys';
FOR c1 IN
  SELECT dbo_name, dbo_type
  FROM ctxsys."dr$dbo"
  WHERE dbo_type != 'PUBLIC SYNONYM'
  ORDER BY dbo_type, dbo_name
LOOP
  IF c1.dbo_type != l_ltype THEN
    SELECT CASE c1.dbo_type
      WHEN 'INDEX' THEN 1
      WHEN 'TABLE' THEN 2
      WHEN 'VIEW' THEN 4
      WHEN 'SEQUENCE' THEN 6
      WHEN 'PROCEDURE' THEN 7
      WHEN 'FUNCTION' THEN 8
      WHEN 'PACKAGE' THEN 9
      WHEN 'PACKAGE BODY' THEN 11
      WHEN 'TYPE' THEN 13
      WHEN 'TYPE BODY' THEN 14
      WHEN 'LIBRARY' THEN 22
      WHEN 'INDEXTYPE' THEN 32
      WHEN 'OPERATOR' THEN 33
      ELSE 0
    END INTO l_type;
    l_ltype := c1.dbo_type;
  END IF;
  l_status := -1;
  FOR c2 IN
    SELECT 1
    FROM pg_catalog.pg_class cl
    JOIN pg_catalog.pg_namespace ns ON cl.relnamespace = ns.oid
    WHERE ns.nspname = 'ctxsys'
    AND cl.relname = c1.dbo_name
  LOOP
    l_status := 1;
  END LOOP;
  IF l_status != 1 THEN
    l_dbo_name := quote_ident(c1.dbo_name);
    l_compile_sql := CASE c1.dbo_type
      WHEN 'VIEW' THEN 'CREATE OR REPLACE VIEW ctxsys.' || l_dbo_name || ' AS SELECT 1'
      WHEN 'PROCEDURE' THEN 'CREATE OR REPLACE FUNCTION ctxsys.' || l_dbo_name || '() RETURNS void LANGUAGE plpgsql AS $$ BEGIN END; $$'
      WHEN 'FUNCTION' THEN 'CREATE OR REPLACE FUNCTION ctxsys.' || l_dbo_name || '() RETURNS void LANGUAGE plpgsql AS $$ BEGIN END; $$'
      WHEN 'PACKAGE' THEN null
      WHEN 'PACKAGE BODY' THEN null
      WHEN 'TYPE' THEN null
      WHEN 'TYPE BODY' THEN null
      WHEN 'INDEXTYPE' THEN null
      WHEN 'OPERATOR' THEN null
      ELSE null
    END;
    IF l_compile_sql IS NULL THEN
      RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
      RETURN;
    ELSE
      BEGIN
        EXECUTE l_compile_sql;
      EXCEPTION WHEN OTHERS THEN
        RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
        RETURN;
      END;
    END IF;
  END IF;
END LOOP;
RETURN;
EXCEPTION WHEN OTHERS THEN
RAISE EXCEPTION 'validate_context error';
END;
$$;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL)
RETURNS void AS $$
DECLARE
    v_report_id numeric;
    v_total_sales numeric := 0;
    v_total_commission numeric := 0;
    v_report_sql text;
    v_start_date date;
    v_end_date date;
    rec record;
    v_count int := 0;
BEGIN
    v_start_date := to_date(cast(p_year as text) || '-' || lpad(cast((p_quarter - 1) * 3 + 1 as text), 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + interval '3 month - 1 day')::date;
    SELECT nextval('sales_report_seq') INTO v_report_id;
    v_report_sql := 'SELECT sr.sales_rep_id AS sales_rep_id,
                            sr.sales_rep_name AS sales_rep_name,
                            COALESCE(SUM(s.sale_amount), 0) AS total_sales,
                            sr.commission_rate AS commission_rate,
                            COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount
                     FROM sales_representatives sr
                     LEFT JOIN sales s
                           ON sr.sales_rep_id = s.sales_rep_id
                          AND s.sale_date BETWEEN $1 AND $2
                     WHERE ($3 IS NULL OR sr.sales_rep_id = $3)
                     GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
                     ORDER BY total_sales DESC';
    FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        v_count := v_count + 1;
    END LOOP;
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
         VALUES (v_report_id, p_year, p_quarter, now(), v_count);
    v_total_sales := 0;
    v_total_commission := 0;
    FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
             VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
    END LOOP;
    UPDATE sales_report_header
       SET total_sales = v_total_sales,
           total_commission = v_total_commission
     WHERE report_id = v_report_id;
    COMMIT;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION WHEN OTHERS THEN
    ROLLBACK;
    RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(
    IN p_warehouseid INT,
    IN p_adjustmenttype VARCHAR(50),
    IN p_adjustmentdate TIMESTAMP DEFAULT NULL
)
AS
$$
DECLARE
    v_productid INT;
    v_currentqty INT;
    v_adjustmentqty INT;
    v_newqty INT;
    v_totalprocessed INT := 0;
    v_errorcount INT := 0;
    v_errormessage TEXT;
BEGIN
    IF p_adjustmentdate IS NULL THEN
        p_adjustmentdate := now();
    END IF;

    BEGIN
        START TRANSACTION;
        FOR rec IN 
            SELECT ProductID,
                   QuantityOnHand,
                   CASE p_adjustmenttype
                     WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                     WHEN 'DAMAGE' THEN -DamagedQuantity
                     WHEN 'RETURN' THEN ReturnedQuantity
                     ELSE 0
                   END AS AdjustmentQty
            FROM InventoryStaging
            WHERE WarehouseID = p_warehouseid
              AND Status = 'PENDING'
            ORDER BY ProductID
        LOOP
            v_productid := rec.ProductID;
            v_currentqty := rec.QuantityOnHand;
            v_adjustmentqty := rec.AdjustmentQty;
            BEGIN
                v_newqty := v_currentqty + v_adjustmentqty;
                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = v_productid AND WarehouseID = p_warehouseid) THEN
                    UPDATE Inventory
                    SET QuantityOnHand = v_newqty,
                        LastAdjustmentDate = p_adjustmentdate,
                        LastAdjustmentType = p_adjustmenttype
                    WHERE ProductID = v_productid
                      AND WarehouseID = p_warehouseid;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (v_productid, p_warehouseid, CASE WHEN v_newqty < 0 THEN 0 ELSE v_newqty END, p_adjustmentdate, p_adjustmenttype);
                END IF;

                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (v_productid, p_warehouseid, p_adjustmentdate, p_adjustmenttype, v_currentqty, v_adjustmentqty, v_newqty, current_user);

                v_totalprocessed := v_totalprocessed + 1;
            EXCEPTION WHEN OTHERS THEN
                v_errorcount := v_errorcount + 1;
                INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                VALUES (now(), 'Inventory update failed: ' || SQLERRM, v_productid);
            END;
        END LOOP;

        UPDATE InventoryStaging
        SET Status = 'PROCESSED',
            ProcessedDate = now()
        WHERE WarehouseID = p_warehouseid
          AND Status = 'PENDING';

        COMMIT;
        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_totalprocessed, v_errorcount;
    EXCEPTION WHEN OTHERS THEN
        v_errormessage := SQLERRM;
        ROLLBACK;
        RAISE EXCEPTION 'Bulk inventory update failed: %', v_errormessage;
    END;
END;
$$
LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(
    p_PayPeriodStart TIMESTAMP,
    p_PayPeriodEnd TIMESTAMP,
    p_DepartmentID INT DEFAULT NULL
)
LANGUAGE plpgsql
AS
$$
DECLARE
    PayrollID INT;
    EmployeeID INT;
    HourlyRate NUMERIC(10,2);
    RegularHours NUMERIC(5,2);
    OvertimeHours NUMERIC(5,2);
    GrossPay NUMERIC(10,2);
    TaxDeduction NUMERIC(10,2);
    NetPay NUMERIC(10,2);
    TotalGross NUMERIC(15,2) := 0;
    TotalNet NUMERIC(15,2) := 0;
BEGIN
    BEGIN
        INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
        VALUES (p_PayPeriodStart, p_PayPeriodEnd, p_DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING')
        RETURNING PayrollID INTO PayrollID;

        FOR rec IN (
            SELECT
                e.EmployeeID AS empid,
                e.HourlyRate AS hr,
                COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
                COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
            FROM Employees e
            LEFT JOIN Timesheet t
                   ON e.EmployeeID = t.EmployeeID
                  AND t.WorkDate BETWEEN p_PayPeriodStart AND p_PayPeriodEnd
            WHERE e.Status = 'ACTIVE'
              AND (p_DepartmentID IS NULL OR e.DepartmentID = p_DepartmentID)
            GROUP BY e.EmployeeID, e.HourlyRate
            ORDER BY e.EmployeeID
        )
        LOOP
            EmployeeID := rec.empid;
            HourlyRate := rec.hr;
            RegularHours := rec.RegularHours;
            OvertimeHours := rec.OvertimeHours;

            GrossPay := (RegularHours * HourlyRate) + (OvertimeHours * HourlyRate * 1.5);
            TaxDeduction := GrossPay * 0.20;
            NetPay := GrossPay - TaxDeduction;

            INSERT INTO PayrollDetail (
                PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours,
                GrossPay, TaxDeduction, NetPay
            )
            VALUES (
                PayrollID, EmployeeID, HourlyRate, RegularHours,
                OvertimeHours, GrossPay, TaxDeduction, NetPay
            );

            TotalGross := TotalGross + GrossPay;
            TotalNet := TotalNet + NetPay;
        END LOOP;

        UPDATE PayrollHeader
        SET TotalGrossPay = TotalGross,
            TotalNetPay = TotalNet,
            Status = 'COMPLETED',
            CompletedDate = CURRENT_TIMESTAMP
        WHERE PayrollID = PayrollID;

        RAISE NOTICE 'Payroll processing completed. Payroll ID: %', PayrollID;
    EXCEPTION
        WHEN OTHERS THEN
            UPDATE PayrollHeader
            SET Status = 'FAILED',
                ErrorMessage = SQLERRM
            WHERE PayrollID = PayrollID;
            RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_CleanupOldLogs(IN p_retentionDays INT DEFAULT 90)
LANGUAGE plpgsql
AS
$$
DECLARE
    v_CutoffDate TIMESTAMP;
    v_DeletedCount INT := 0;
    v_TempCount INT;
BEGIN
    v_CutoffDate := CURRENT_TIMESTAMP - p_retentionDays * INTERVAL '1 DAY';

    DELETE FROM ApplicationLogs WHERE LogDate < v_CutoffDate;
    GET DIAGNOSTICS v_TempCount = ROW_COUNT;
    v_DeletedCount := v_DeletedCount + v_TempCount;

    DELETE FROM ErrorLogs WHERE ErrorDate < v_CutoffDate;
    GET DIAGNOSTICS v_TempCount = ROW_COUNT;
    v_DeletedCount := v_DeletedCount + v_TempCount;

    DELETE FROM AuditLogs WHERE AuditDate < v_CutoffDate;
    GET DIAGNOSTICS v_TempCount = ROW_COUNT;
    v_DeletedCount := v_DeletedCount + v_TempCount;

    INSERT INTO CleanupHistory (CleanupDate, TableName, RecordsDeleted, RetentionDays)
    VALUES (CURRENT_TIMESTAMP, 'MULTIPLE_LOG_TABLES', v_DeletedCount, p_retentionDays);

    RAISE NOTICE 'Cleanup completed. % records deleted.', v_DeletedCount;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Log cleanup failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_DataMigration(
    p_sourcetable VARCHAR(128),
    p_targettable VARCHAR(128),
    p_batchsize INT DEFAULT 10000
)
LANGUAGE plpgsql
AS
$$
DECLARE
    v_sql TEXT;
    v_sourcecount INT;
    v_targetcount INT;
    v_batchcount INT := 0;
    v_migrationid INT;
    v_errormessage TEXT;
    v_rowcount INT;
BEGIN
    BEGIN
        INSERT INTO MigrationLog(SourceTable, TargetTable, StartTime, Status)
        VALUES (p_sourcetable, p_targettable, NOW(), 'RUNNING')
        RETURNING MigrationID INTO v_migrationid;

        v_sql := FORMAT('SELECT COUNT(*) FROM %I', p_sourcetable);
        EXECUTE v_sql INTO v_sourcecount;

        UPDATE MigrationLog SET SourceCount = v_sourcecount WHERE MigrationID = v_migrationid;

        v_sql := FORMAT('DELETE FROM %I', p_targettable);
        EXECUTE v_sql;

        WHILE v_batchcount < v_sourcecount LOOP
            v_sql := FORMAT('INSERT INTO %I SELECT * FROM %I s WHERE NOT EXISTS (SELECT 1 FROM %I t WHERE t.ID = s.ID) LIMIT %s',
                            p_targettable, p_sourcetable, p_targettable, p_batchsize);
            EXECUTE v_sql;
            GET DIAGNOSTICS v_rowcount = ROW_COUNT;
            IF v_rowcount = 0 THEN
                EXIT;
            END IF;
            v_batchcount := v_batchcount + v_rowcount;
        END LOOP;

        v_sql := FORMAT('SELECT COUNT(*) FROM %I', p_targettable);
        EXECUTE v_sql INTO v_targetcount;

        UPDATE MigrationLog
        SET EndTime = NOW(),
            TargetCount = v_targetcount,
            Status = CASE WHEN v_sourcecount = v_targetcount THEN 'SUCCESS' ELSE 'WARNING' END
        WHERE MigrationID = v_migrationid;

        RAISE NOTICE 'Migration completed for %. Source: %, Target: %', p_sourcetable, v_sourcecount, v_targetcount;
    EXCEPTION
        WHEN OTHERS THEN
            v_errormessage := SQLERRM;
            UPDATE MigrationLog
            SET EndTime = NOW(),
                Status = 'FAILED',
                ErrorMessage = v_errormessage
            WHERE MigrationID = v_migrationid;
            RAISE EXCEPTION 'Data migration failed: %', v_errormessage;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(
   IN p_oldcategoryid INT,
   IN p_newcategoryid INT,
   IN p_effectivedate TIMESTAMP DEFAULT NULL
)
LANGUAGE plpgsql
AS $$
DECLARE
   v_effectivedate TIMESTAMP;
   v_productcount INT;
   v_updatedcount INT := 0;
   rec RECORD;
BEGIN
   IF p_effectivedate IS NULL THEN
      v_effectivedate := CURRENT_TIMESTAMP;
   ELSE
      v_effectivedate := p_effectivedate;
   END IF;

   IF p_oldcategoryid = p_newcategoryid THEN
      RAISE EXCEPTION 'Old and new category IDs cannot be the same';
   END IF;

   SELECT COUNT(*)
   INTO v_productcount
   FROM Products
   WHERE CategoryID = p_oldcategoryid
     AND Status = 'ACTIVE';

   IF v_productcount = 0 THEN
      RAISE NOTICE 'No active products found in category %', p_oldcategoryid;
      RETURN;
   END IF;

   FOR rec IN
      SELECT ProductID, ProductName
      FROM Products
      WHERE CategoryID = p_oldcategoryid
        AND Status = 'ACTIVE'
   LOOP
      BEGIN
         INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
         VALUES (rec.ProductID, p_oldcategoryid, p_newcategoryid, v_effectivedate, CURRENT_USER);

         UPDATE Products
         SET CategoryID = p_newcategoryid,
             LastModifiedDate = v_effectivedate,
             LastModifiedBy = CURRENT_USER
         WHERE ProductID = rec.ProductID;

         v_updatedcount := v_updatedcount + 1;
      EXCEPTION WHEN OTHERS THEN
         INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
         VALUES (rec.ProductID, SQLERRM, CURRENT_TIMESTAMP);
      END;
   END LOOP;

   UPDATE ProductCategories
   SET LastUpdateDate = v_effectivedate
   WHERE CategoryID IN (p_oldcategoryid, p_newcategoryid);

   RAISE NOTICE 'Category update completed. % products updated out of % total.', v_updatedcount, v_productcount;
END;
$$;

CREATE OR REPLACE PROCEDURE GETEMPLOYEESTATS(p_dept_id IN NUMBER, p_result OUT CLOB)
IS
  TYPE stats_rec IS RECORD (
    total_emp NUMBER,
    avg_salary NUMBER,
    max_salary NUMBER,
    active_projects NUMBER
  );
  TYPE p_result_rec IS RECORD (
    employee_id NUMBER,
    last_name VARCHAR2(50),
    salary NUMBER(8,2)
  );
  v_stats stats_rec;
  v_result p_result_rec;
  v_cursor SYS_REFCURSOR;
  v_sql VARCHAR2(1000);
  v_dept_name VARCHAR2(50);
BEGIN
  SELECT department_name
    INTO v_dept_name
    FROM DEPARTMENTS
   WHERE department_id = p_dept_id;

  SELECT COUNT(*),
         AVG(salary),
         MAX(salary)
    INTO v_stats.total_emp,
         v_stats.avg_salary,
         v_stats.max_salary
    FROM EMPLOYEES
   WHERE department_id = p_dept_id;

  SELECT COUNT(*)
    INTO v_stats.active_projects
    FROM PROJECTS
   WHERE department_id = p_dept_id
     AND status = 'ACTIVE';

  v_sql := 'SELECT * FROM (SELECT employee_id, last_name, salary FROM employees WHERE department_id = :1 ORDER BY salary DESC) WHERE ROWNUM <= 5';
  OPEN v_cursor FOR v_sql USING p_dept_id;

  p_result := '{  "department": "' || v_dept_name || '",  "total_employees": '
           || v_stats.total_emp
           || ',  "salary_stats": {    "average": '
           || v_stats.avg_salary
           || ',    "max": '
           || v_stats.max_salary
           || '  },  "active_projects": '
           || v_stats.active_projects
           || ',  "top_earners": [';

  LOOP
    FETCH v_cursor INTO v_result;
    EXIT WHEN v_cursor%NOTFOUND;
    p_result :=
      p_result
      || '    {"id": '
      || v_result.employee_id
      || ', "name": "'
      || v_result.last_name
      || '", "salary": '
      || v_result.salary
      || '},';
  END LOOP;

  p_result := RTRIM(p_result, ',') || '  ]}';
  CLOSE v_cursor;

EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
  WHEN OTHERS THEN
    p_result := '{"error": "' || SQLERRM || '"}';
END;
/

