CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id integer) RETURNS text
LANGUAGE plpgsql
AS $$
DECLARE
    v_dept_name text;
    v_total_emp integer;
    v_avg_salary numeric(10,2);
    v_max_salary numeric(10,2);
    v_active_projects integer;
    v_employee_id integer;
    v_last_name text;
    v_salary numeric(8,2);
    p_result text;
BEGIN
    SELECT department_name INTO v_dept_name
    FROM departments
    WHERE department_id = p_dept_id;
    IF NOT FOUND THEN
        p_result := '{"error": "Department not found"}';
        RETURN p_result;
    END IF;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    SELECT COUNT(*)
    INTO v_active_projects
    FROM projects
    WHERE department_id = p_dept_id AND status = 'ACTIVE';

    p_result := '{"department":"' || v_dept_name || '","total_employees":' || v_total_emp
         || ',"salary_stats":{"average":' || COALESCE(v_avg_salary,0)
         || ',"max":' || COALESCE(v_max_salary,0)
         || '},"active_projects":' || v_active_projects
         || ',"top_earners":[';

    FOR v_employee_id, v_last_name, v_salary IN
        SELECT employee_id, last_name, salary
        FROM employees
        WHERE department_id = p_dept_id
        ORDER BY salary DESC
        LIMIT 5
    LOOP
        p_result := p_result || '{"id":' || v_employee_id
                            || ',"name":"' || v_last_name
                            || '","salary":' || v_salary || '},';
    END LOOP;

    p_result := REGEXP_REPLACE(p_result, ',$', '');
    p_result := p_result || ']}';
    RETURN p_result;
EXCEPTION
    WHEN OTHERS THEN
        p_result := '{"error": "Unexpected error"}';
        RETURN p_result;
END;
$$;

CREATE OR REPLACE PROCEDURE usp_UpdateOrderStatus(
   p_customerid INT,
   p_oldstatus VARCHAR(20),
   p_newstatus VARCHAR(20),
   OUT p_rowsupdated INT,
   OUT p_errormessage VARCHAR(200)
)
AS
$$
DECLARE
   v_trancount INT := 0;
   v_processingfee DECIMAL(10,2) := 0;
BEGIN
   IF p_oldstatus = p_newstatus THEN
      p_errormessage := '新旧状态不能相同';
      p_rowsupdated := 400;
      RETURN;
   END IF;

   IF v_trancount = 0 THEN
      EXECUTE 'START TRANSACTION';
   END IF;

   EXECUTE format(
     'UPDATE Orders
      SET Status = %L, LastUpdated = CURRENT_TIMESTAMP
      WHERE CustomerID = %s AND Status = %L
        AND OrderDate > CURRENT_TIMESTAMP - INTERVAL ''3 months''',
       p_newstatus, p_customerid, p_oldstatus
   );
   GET DIAGNOSTICS p_rowsupdated = ROW_COUNT;

   IF p_rowsupdated = 0 THEN
      p_errormessage := '未找到符合条件的订单';
      p_rowsupdated := 404;
      IF v_trancount = 0 THEN
         EXECUTE 'ROLLBACK';
      END IF;
      RETURN;
   END IF;

   EXECUTE format(
     'INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
      SELECT OrderID, %L, %L, CURRENT_TIMESTAMP
      FROM Orders
      WHERE CustomerID = %s AND Status = %L',
      p_oldstatus, p_newstatus, p_customerid, p_newstatus
   );

   FOR v_processingfee IN
     EXECUTE format(
       'SELECT Fee
        FROM OrderDetails
        WHERE OrderID IN (
          SELECT OrderID
          FROM Orders
          WHERE CustomerID = %s AND Status = %L
        )',
       p_customerid, p_newstatus
     )
   LOOP
      v_processingfee := v_processingfee * 0.95;
   END LOOP;

   IF v_trancount = 0 THEN
      EXECUTE 'COMMIT';
   END IF;

   p_errormessage := '更新成功';
   p_rowsupdated := 200;
   RETURN;

EXCEPTION
   WHEN OTHERS THEN
      IF v_trancount = 0 THEN
         EXECUTE 'ROLLBACK';
      END IF;
      p_errormessage := SQLERRM;
      p_rowsupdated := 500;
      RETURN;
END;
$$ LANGUAGE plpgsql;

