CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id integer, p_percent numeric, p_min_dept integer DEFAULT NULL, p_max_dept integer DEFAULT NULL, OUT p_result text) LANGUAGE plpgsql AS $BODY$
DECLARE
    v_dept_name text;
    v_total_emp bigint;
    v_avg_salary numeric;
    v_max_salary numeric;
    v_rows_updated integer := 0;
    v_total_budget numeric := 0;
    dept_rec RECORD;
    v_result_rec RECORD;
    updated_count integer;
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    IF NOT FOUND THEN
        p_result := '{"error": "Department not found"}';
        RETURN;
    END IF;

    SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM employees WHERE department_id = p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp || ', "salary_stats": { "average": ' || COALESCE(v_avg_salary,0) || ', "max": ' || COALESCE(v_max_salary,0) || ' }, "top_earners": [';
    FOR v_result_rec IN SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5
    LOOP
        p_result := p_result || ' {"id": ' || v_result_rec.employee_id || ', "name": "' || v_result_rec.last_name || '", "salary": ' || v_result_rec.salary || '},';
    END LOOP;
    p_result := RTRIM(p_result, ',') || ' ] }';

    FOR dept_rec IN
        SELECT department_id, budget FROM departments
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL) AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE employees SET salary = COALESCE(salary,3000) * (1 + p_percent/100) WHERE department_id = dept_rec.department_id;
        GET DIAGNOSTICS updated_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + updated_count;

        SELECT SUM(salary) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;
        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

EXCEPTION WHEN OTHERS THEN
    p_result := '{"error": "' || replace(SQLERRM, '"', '\"') || '"}';
END;
$BODY$

CREATE OR REPLACE FUNCTION checkandupdatesalaries(
    p_dept_id integer,
    p_percent numeric,
    p_min_dept integer DEFAULT NULL,
    p_max_dept integer DEFAULT NULL
) RETURNS text AS $$
DECLARE
    v_stats RECORD;
    v_emp RECORD;
    v_dept_name text;
    v_rows_updated integer := 0;
    v_total_budget numeric := 0;
    v_sql text;
    p_result text := '';
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    SELECT COUNT(*) AS total_emp, AVG(salary) AS avg_salary, MAX(salary) AS max_salary INTO v_stats FROM employees WHERE department_id = p_dept_id;
    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    p_result := format('{"department": "%s", "total_employees": %s, "salary_stats": {"average": %s, "max": %s}, "top_earners": [',
        v_dept_name, v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary);
    FOR v_emp IN EXECUTE v_sql USING p_dept_id LOOP
        p_result := p_result || format('{"id": %s, "name": "%s", "salary": %s},', v_emp.employee_id, v_emp.last_name, v_emp.salary);
    END LOOP;
    p_result := regexp_replace(p_result, ',\s*$', '') || ']}';
    FOR rec IN SELECT department_id, budget FROM departments WHERE (department_id >= COALESCE(p_min_dept, department_id)) AND (department_id <= COALESCE(p_max_dept, department_id)) LOOP
        UPDATE employees SET salary = COALESCE(salary, 3000) * (1 + p_percent/100) WHERE department_id = rec.department_id;
        GET DIAGNOSTICS v_rows_updated = ROW_COUNT;
        SELECT SUM(salary) INTO v_total_budget FROM employees WHERE department_id = rec.department_id;
        IF v_total_budget > rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', rec.department_id;
        END IF;
    END LOOP;
    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, clock_timestamp();
    RETURN p_result;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        RETURN '{"error": "Department not found"}';
    WHEN OTHERS THEN
        RETURN format('{"error": "%s"}', SQLERRM);
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id integer, p_percent numeric, p_min_dept integer DEFAULT NULL, p_max_dept integer DEFAULT NULL, OUT p_result text) AS $$
DECLARE
  v_dept_name text;
  v_total_emp integer;
  v_avg_salary numeric;
  v_max_salary numeric;
  v_rows_updated integer := 0;
  v_total_budget numeric := 0;
  emp_rec record;
  dept_rec record;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM employees WHERE department_id = p_dept_id;
  p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp || ', "salary_stats": { "average": ' || v_avg_salary || ', "max": ' || v_max_salary || ' }, "top_earners": [';
  FOR emp_rec IN SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5 LOOP
    p_result := p_result || ' {"id": ' || emp_rec.employee_id || ', "name": "' || emp_rec.last_name || '", "salary": ' || emp_rec.salary || '},';
  END LOOP;
  p_result := rtrim(p_result, ',') || ' ] }';
  FOR dept_rec IN SELECT department_id, budget FROM departments WHERE (department_id >= p_min_dept OR p_min_dept IS NULL) AND (department_id <= p_max_dept OR p_max_dept IS NULL) LOOP
    UPDATE employees SET salary = COALESCE(salary, 3000) * (1 + p_percent/100) WHERE department_id = dept_rec.department_id;
    GET DIAGNOSTICS v_rows_updated = ROW_COUNT;
    SELECT SUM(salary) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;
    IF v_total_budget > dept_rec.budget THEN
      RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
    END IF;
  END LOOP;
  RAISE NOTICE 'Updated % records', v_rows_updated;
  RETURN;
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN;
  WHEN OTHERS THEN
    p_result := '{"error": "' || SQLERRM || '"}';
    RETURN;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id integer, p_adjustment_type varchar, p_adjustment_date timestamp default now()) RETURNS void AS $$
DECLARE
    v_total_processed integer := 0;
    v_error_count integer := 0;
    rec_inventory record;
BEGIN
    FOR rec_inventory IN
        SELECT product_id,
               quantity_on_hand AS current_qty,
               CASE p_adjustment_type
                    WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                    WHEN 'DAMAGE' THEN -damaged_quantity
                    WHEN 'RETURN' THEN returned_quantity
                    ELSE 0
               END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
          AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            UPDATE inventory
               SET quantity_on_hand = quantity_on_hand + rec_inventory.adjustment_qty,
                   last_adjustment_date = p_adjustment_date,
                   last_adjustment_type = p_adjustment_type
             WHERE product_id = rec_inventory.product_id
               AND warehouse_id = p_warehouse_id;
            IF NOT FOUND THEN
                INSERT INTO inventory(product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (rec_inventory.product_id, p_warehouse_id, GREATEST(0, rec_inventory.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;
            INSERT INTO inventory_audit_log(product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (rec_inventory.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec_inventory.current_qty, rec_inventory.adjustment_qty, rec_inventory.current_qty + rec_inventory.adjustment_qty, current_user);
            v_total_processed := v_total_processed + 1;
        EXCEPTION WHEN OTHERS THEN
            v_error_count := v_error_count + 1;
            INSERT INTO error_log(error_date, error_message, product_id)
            VALUES (now(), 'Inventory update failed: ' || SQLERRM, rec_inventory.product_id);
        END;
    END LOOP;
    UPDATE inventory_staging
       SET status = 'PROCESSED',
           processed_date = now()
     WHERE warehouse_id = p_warehouse_id
       AND status = 'PENDING';
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id integer, p_adjustment_type varchar, p_adjustment_date timestamp default now()) RETURNS void AS $$
DECLARE
    rec RECORD;
    v_total_processed integer := 0;
    v_error_count integer := 0;
    CURSOR c_inventory IS
        SELECT product_id, quantity_on_hand AS current_qty,
            CASE p_adjustment_type
                WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                WHEN 'DAMAGE' THEN -damaged_quantity
                WHEN 'RETURN' THEN returned_quantity
                ELSE 0
            END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
        ORDER BY product_id;
BEGIN
    OPEN c_inventory;
    LOOP
        FETCH c_inventory INTO rec;
        EXIT WHEN NOT FOUND;
        BEGIN
            UPDATE inventory inv
            SET quantity_on_hand = inv.quantity_on_hand + rec.adjustment_qty,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type
            WHERE inv.product_id = rec.product_id AND inv.warehouse_id = p_warehouse_id;
            IF NOT FOUND THEN
                INSERT INTO inventory(product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES(rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;
            INSERT INTO inventory_audit_log(product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES(rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, current_user);
            v_total_processed := v_total_processed + 1;
        EXCEPTION WHEN OTHERS THEN
            v_error_count := v_error_count + 1;
            INSERT INTO error_log(error_date, error_message, product_id)
            VALUES(now(), 'Inventory update failed: ' || SQLERRM, rec.product_id);
        END;
    END LOOP;
    CLOSE c_inventory;
    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = now()
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL) RETURNS void AS $$
DECLARE
  v_report_id integer;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_count integer := 0;
  v_start_date date;
  v_end_date date;
  v_sales_rec record;
BEGIN
  v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text,2,'0') || '-01','YYYY-MM-DD');
  v_end_date := (date_trunc('month', v_start_date + interval '3 month') - interval '1 day')::date;
  SELECT nextval('sales_report_seq') INTO v_report_id;
  FOR v_sales_rec IN EXECUTE
    'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount),0) AS total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount),0)*sr.commission_rate AS commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC'
    USING v_start_date, v_end_date, p_sales_rep_id
  LOOP
    INSERT INTO sales_report_detail(report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, v_sales_rec.sales_rep_id, v_sales_rec.sales_rep_name, v_sales_rec.total_sales, v_sales_rec.commission_rate, v_sales_rec.commission_amount);
    v_total_sales := v_total_sales + v_sales_rec.total_sales;
    v_total_commission := v_total_commission + v_sales_rec.commission_amount;
    v_count := v_count + 1;
  END LOOP;
  INSERT INTO sales_report_header(report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, now(), v_count);
  UPDATE sales_report_header
    SET total_sales = v_total_sales, total_commission = v_total_commission
    WHERE report_id = v_report_id;
  RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL)
RETURNS void AS $$
DECLARE
    v_report_id integer;
    v_total_sales numeric := 0;
    v_total_commission numeric := 0;
    v_start_date date;
    v_end_date date;
    rec record;
    v_total_reps integer := 0;
BEGIN
    v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + interval '3 months' - interval '1 day')::date;
    v_report_id := nextval('sales_report_seq');
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, now(), 0);
    FOR rec IN
        SELECT sr.sales_rep_id,
               sr.sales_rep_name,
               coalesce(sum(s.sale_amount), 0) AS total_sales,
               sr.commission_rate,
               coalesce(sum(s.sale_amount), 0) * sr.commission_rate AS commission_amount
        FROM sales_representatives sr
        LEFT JOIN sales s
          ON sr.sales_rep_id = s.sales_rep_id
         AND s.sale_date BETWEEN v_start_date AND v_end_date
        WHERE p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id
        GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
        ORDER BY total_sales DESC
    LOOP
        v_total_reps := v_total_reps + 1;
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
    END LOOP;
    UPDATE sales_report_header
    SET total_reps = v_total_reps,
        total_sales = v_total_sales,
        total_commission = v_total_commission
    WHERE report_id = v_report_id;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL) RETURNS void AS $$
DECLARE
  v_report_id integer;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_start_date date;
  v_end_date date;
  v_count integer := 0;
  v_sales_rec record;
  v_report_sql text;
BEGIN
  v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter-1)*3+1)::text,2,'0') || '-01', 'YYYY-MM-DD');
  v_end_date := (v_start_date + interval '3 months' - interval '1 day')::date;
  v_report_id := nextval('sales_report_seq');
  v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount),0) AS total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount),0) * sr.commission_rate AS commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';
  FOR v_sales_rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
    INSERT INTO sales_report_detail(report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, v_sales_rec.sales_rep_id, v_sales_rec.sales_rep_name, v_sales_rec.total_sales, v_sales_rec.commission_rate, v_sales_rec.commission_amount);
    v_count := v_count + 1;
    v_total_sales := v_total_sales + v_sales_rec.total_sales;
    v_total_commission := v_total_commission + v_sales_rec.commission_amount;
  END LOOP;
  INSERT INTO sales_report_header(report_id, report_year, report_quarter, created_date, total_reps, total_sales, total_commission)
  VALUES (v_report_id, p_year, p_quarter, now(), v_count, v_total_sales, v_total_commission);
EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE RT_Tang_YW_MainC_InitAwb60Finish() LANGUAGE plpgsql AS $$
DECLARE
  period60        INTEGER := -60;
  period180       INTEGER := -180;
  yw_op_date      TIMESTAMP;
  yw_start_time   TIMESTAMP := now();
  tran_error      INTEGER := 0;
  awb_yw_date60   TIMESTAMP;
  awb_yw_date180  TIMESTAMP;
BEGIN
  SELECT max(Yw_Op_Date) INTO yw_op_date FROM Tbl_Yw_Log WHERE optresult = 'RUN';
  IF yw_op_date IS NULL THEN
    yw_op_date := now();
  END IF;
  awb_yw_date60 := yw_op_date + make_interval(days => period60);
  awb_yw_date180 := yw_op_date + make_interval(days => period180);
  SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
  tran_error := 0;
  BEGIN
    START TRANSACTION;
    DELETE FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp
     WHERE (AwbPrefix || rtrim(AwbNo) || AwbPostfix) IN (
       SELECT AwbPrefix || rtrim(AwbNo) || AwbPostfix
         FROM Tbl_Cargo
        WHERE (AwbPrefix || rtrim(AwbNo) || AwbPostfix) IN (
                SELECT AwbPrefix || rtrim(AwbNo) || AwbPostfix
                  FROM Tbl_Awb_Basic
                 WHERE Op_Date < to_char(awb_yw_date60,'YYYY-MM-DD')
                   AND Op_Date > to_char(awb_yw_date180,'YYYY-MM-DD')
              )
          AND FinishFlag <> 0
          AND Flight_Dep = Flight_Dest
        GROUP BY AwbPrefix, AwbNo, AwbPostfix
     );
    INSERT INTO Cargo2YWTemp.Tbl_Awb_Basic_Temp(AwbPrefix, AwbNo, AwbPostfix, YwTreate)
    SELECT DISTINCT AwbPrefix, AwbNo, AwbPostfix, 1
      FROM (
        SELECT AwbPrefix, AwbNo, AwbPostfix, min(FinishFlag) AS TmpFinishFlag
          FROM Tbl_Cargo
         WHERE (AwbPrefix || rtrim(AwbNo) || AwbPostfix) IN (
                 SELECT AwbPrefix || rtrim(AwbNo) || AwbPostfix
                   FROM Tbl_Awb_Basic
                  WHERE Op_Date < to_char(awb_yw_date60,'YYYY-MM-DD')
                    AND Op_Date > to_char(awb_yw_date180,'YYYY-MM-DD')
               )
           AND FinishFlag <> 0
           AND Flight_Dep = Flight_Dest
         GROUP BY AwbPrefix, AwbNo, AwbPostfix
      ) AS Tbl_Temp
     WHERE TmpFinishFlag > 0;
    tran_error := tran_error + 0;
    COMMIT;
  EXCEPTION WHEN OTHERS THEN
    ROLLBACK;
    tran_error := tran_error + 1;
    INSERT INTO TBL_SP_ErrLog(ErrTable, ErrDate) VALUES('Tbl_Awb_Basic_Temp', now());
    RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
  END;
  IF tran_error = 0 THEN
    tran_error := 0;
    BEGIN
      START TRANSACTION;
      UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp t
         SET AwbDate = a.Op_Date,
             AwbDest  = substring(a.routing, char_length(a.routing) - 2, 3),
             Piece    = a.Piece
        FROM Tbl_Awb_Basic a
       WHERE a.AwbNo = t.AwbNo
         AND a.AwbPrefix = t.AwbPrefix
         AND a.AwbPostfix = t.AwbPostfix;
      tran_error := tran_error + 0;
      COMMIT;
    EXCEPTION WHEN OTHERS THEN
      ROLLBACK;
      tran_error := tran_error + 1;
      INSERT INTO TBL_SP_ErrLog(ErrTable, ErrDate) VALUES('Tbl_Awb_Basic_Temp', now());
      RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
  END IF;
  IF tran_error = 0 THEN
    tran_error := 0;
    BEGIN
      START TRANSACTION;
      UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp bt
         SET YwTreate = 0
        FROM (
          SELECT sum(Piece) AS Piece, AwbPrefix, AwbNo, AwbPostfix
            FROM (
              SELECT c.Piece, c.AwbPrefix, c.AwbNo, c.AwbPostfix
                FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp t
                JOIN Tbl_Cargo c
                  ON c.Flight_Dep = t.AwbDest
                 AND c.Flight_Dest = t.AwbDest
                 AND c.AwbNo = t.AwbNo
                 AND c.AwbPrefix = t.AwbPrefix
                 AND c.AwbPostfix = t.AwbPostfix
            ) b
           GROUP BY AwbPrefix, AwbNo, AwbPostfix
        ) a
       WHERE a.AwbNo = bt.AwbNo
         AND a.AwbPrefix = bt.AwbPrefix
         AND a.AwbPostfix = bt.AwbPostfix
         AND bt.Piece = a.Piece;
      tran_error := tran_error + 0;
      COMMIT;
    EXCEPTION WHEN OTHERS THEN
      ROLLBACK;
      tran_error := tran_error + 1;
      INSERT INTO TBL_SP_ErrLog(ErrTable, ErrDate) VALUES('Tbl_Awb_Basic_Temp', now());
      RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
  END IF;
  IF tran_error = 0 THEN
    tran_error := 0;
    BEGIN
      START TRANSACTION;
      DELETE FROM Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp
       WHERE CCANo IN (SELECT DISTINCT AwbNo FROM Tbl_Awb_Basic_Temp);
      INSERT INTO Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp(CCAPrefix, CCANo, CCAPostfix)
      SELECT DISTINCT CCAPrefix, CCANo, CCAPostfix
        FROM Tbl_Cargo_ChargeCorrectionDetail a
        JOIN Cargo2YWTemp.Tbl_Awb_Basic_Temp t
          ON a.AwbPrefix = t.AwbPrefix
         AND a.AwbNo = t.AwbNo
