CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(
    WarehouseID INT,
    AdjustmentType VARCHAR(50),
    AdjustmentDate TIMESTAMP DEFAULT NULL
)
LANGUAGE plpgsql
AS
$$
DECLARE
    v_AdjustmentDate TIMESTAMP := AdjustmentDate;
    ProductID INT;
    CurrentQty INT;
    AdjustmentQty INT;
    NewQty INT;
    TotalProcessed INT := 0;
    ErrorCount INT := 0;
    ErrorMessage VARCHAR(4000);
    inventory_cursor REFCURSOR;
BEGIN
    IF v_AdjustmentDate IS NULL THEN
        v_AdjustmentDate := CURRENT_TIMESTAMP;
    END IF;
    START TRANSACTION;
    BEGIN
        OPEN inventory_cursor FOR
            SELECT I.ProductID,
                   I.QuantityOnHand,
                   CASE AdjustmentType
                       WHEN 'RECOUNT' THEN I.PhysicalCount - I.QuantityOnHand
                       WHEN 'DAMAGE' THEN -I.DamagedQuantity
                       WHEN 'RETURN' THEN I.ReturnedQuantity
                       ELSE 0
                   END AS AdjustmentQty
              FROM InventoryStaging I
             WHERE I.WarehouseID = WarehouseID
               AND I.Status = 'PENDING'
             ORDER BY I.ProductID;
        LOOP
            FETCH inventory_cursor INTO ProductID, CurrentQty, AdjustmentQty;
            EXIT WHEN NOT FOUND;
            BEGIN
                NewQty := CurrentQty + AdjustmentQty;
                IF EXISTS (SELECT 1 FROM Inventory Inv WHERE Inv.ProductID = ProductID AND Inv.WarehouseID = WarehouseID) THEN
                    UPDATE Inventory Inv
                       SET QuantityOnHand = NewQty,
                           LastAdjustmentDate = v_AdjustmentDate,
                           LastAdjustmentType = AdjustmentType
                     WHERE Inv.ProductID = ProductID
                       AND Inv.WarehouseID = WarehouseID;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (ProductID, WarehouseID,
                            CASE WHEN NewQty < 0 THEN 0 ELSE NewQty END,
                            v_AdjustmentDate, AdjustmentType);
                END IF;
                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (ProductID, WarehouseID, v_AdjustmentDate, AdjustmentType, CurrentQty, AdjustmentQty, NewQty, SESSION_USER);
                TotalProcessed := TotalProcessed + 1;
            EXCEPTION WHEN OTHERS THEN
                ErrorCount := ErrorCount + 1;
                INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, ProductID);
            END;
        END LOOP;
        CLOSE inventory_cursor;
        UPDATE InventoryStaging S
           SET Status = 'PROCESSED',
               ProcessedDate = CURRENT_TIMESTAMP
         WHERE S.WarehouseID = WarehouseID
           AND S.Status = 'PENDING';
        COMMIT;
        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', TotalProcessed, ErrorCount;
    EXCEPTION WHEN OTHERS THEN
        ErrorMessage := SQLERRM;
        ROLLBACK;
        BEGIN
            CLOSE inventory_cursor;
        EXCEPTION WHEN OTHERS THEN
            NULL;
        END;
        RAISE EXCEPTION 'Bulk inventory update failed: %', ErrorMessage;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_bulkinventoryupdate(
    p_warehouseid integer,
    p_adjustmenttype varchar(50),
    p_adjustmentdate timestamp default NULL
)
LANGUAGE plpgsql
AS
$$
DECLARE
    rec RECORD;
    newqty integer;
    totalprocessed integer := 0;
    errorcount integer := 0;
    error_message varchar(4000);
BEGIN
    IF p_adjustmentdate IS NULL THEN
        p_adjustmentdate := current_timestamp;
    END IF;
    START TRANSACTION;
    FOR rec IN
        SELECT productid,
               quantityonhand,
               CASE p_adjustmenttype
                   WHEN 'RECOUNT' THEN physicalcount - quantityonhand
                   WHEN 'DAMAGE' THEN -damagedquantity
                   WHEN 'RETURN' THEN returnedquantity
                   ELSE 0
               END AS adjustmentqty
        FROM inventoreystaging
        WHERE warehouseid = p_warehouseid
          AND status = 'PENDING'
        ORDER BY productid
    LOOP
        BEGIN
            newqty := rec.quantityonhand + rec.adjustmentqty;
            IF EXISTS (
                SELECT 1
                FROM inventory
                WHERE productid = rec.productid
                  AND warehouseid = p_warehouseid
            ) THEN
                UPDATE inventory
                SET quantityonhand = newqty,
                    lastadjustmentdate = p_adjustmentdate,
                    lastadjustmenttype = p_adjustmenttype
                WHERE productid = rec.productid
                  AND warehouseid = p_warehouseid;
            ELSE
                INSERT INTO inventory (
                    productid,
                    warehouseid,
                    quantityonhand,
                    lastadjustmentdate,
                    lastadjustmenttype
                ) VALUES (
                    rec.productid,
                    p_warehouseid,
                    CASE WHEN newqty < 0 THEN 0 ELSE newqty END,
                    p_adjustmentdate,
                    p_adjustmenttype
                );
            END IF;
            INSERT INTO inventoryauditlog (
                productid,
                warehouseid,
                adjustmentdate,
                adjustmenttype,
                oldquantity,
                adjustmentquantity,
                newquantity,
                createdby
            ) VALUES (
                rec.productid,
                p_warehouseid,
                p_adjustmentdate,
                p_adjustmenttype,
                rec.quantityonhand,
                rec.adjustmentqty,
                newqty,
                session_user
            );
            totalprocessed := totalprocessed + 1;
        EXCEPTION WHEN OTHERS THEN
            errorcount := errorcount + 1;
            INSERT INTO errorlog (
                errordate,
                errormessage,
                productid
            ) VALUES (
                current_timestamp,
                'Inventory update failed: ' || SQLERRM,
                rec.productid
            );
        END;
    END LOOP;
    UPDATE inventoreystaging
    SET status = 'PROCESSED',
        processeddate = current_timestamp
    WHERE warehouseid = p_warehouseid
      AND status = 'PENDING';
    COMMIT;
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %',
                 totalprocessed, errorcount;
EXCEPTION WHEN OTHERS THEN
    error_message := SQLERRM;
    ROLLBACK;
    RAISE EXCEPTION 'Bulk inventory update failed: %', error_message;
END;
$$;

CREATE PROCEDURE SP_ProcessPayroll(payperiodstart timestamp, payperiodend timestamp, departmentid integer default null)
LANGUAGE plpgsql
AS
$$
DECLARE
  payrollid integer;
  employeeid integer;
  hourlyrate numeric(10,2);
  regularhours numeric(5,2);
  overtimehours numeric(5,2);
  grosspay numeric(10,2);
  taxdeduction numeric(10,2);
  netpay numeric(10,2);
  totalgross numeric(15,2) := 0;
  totalnet numeric(15,2) := 0;
  employee_cursor refcursor;
BEGIN
  INSERT INTO PayrollHeader(PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
  VALUES (payperiodstart, payperiodend, departmentid, now(), 'PROCESSING')
  RETURNING PayrollID INTO payrollid;
  OPEN employee_cursor FOR
    SELECT e.EmployeeID, e.HourlyRate,
      COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0),
      COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0)
    FROM Employees e
    LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN payperiodstart AND payperiodend
    WHERE e.Status = 'ACTIVE' AND (departmentid IS NULL OR e.DepartmentID = departmentid)
    GROUP BY e.EmployeeID, e.HourlyRate
    ORDER BY e.EmployeeID;
  LOOP
    FETCH employee_cursor INTO employeeid, hourlyrate, regularhours, overtimehours;
    EXIT WHEN NOT FOUND;
    grosspay := (regularhours * hourlyrate) + (overtimehours * hourlyrate * 1.5);
    taxdeduction := grosspay * 0.20;
    netpay := grosspay - taxdeduction;
    INSERT INTO PayrollDetail(PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
    VALUES (payrollid, employeeid, hourlyrate, regularhours, overtimehours, grosspay, taxdeduction, netpay);
    totalgross := totalgross + grosspay;
    totalnet := totalnet + netpay;
  END LOOP;
  CLOSE employee_cursor;
  UPDATE PayrollHeader
  SET TotalGrossPay = totalgross,
      TotalNetPay = totalnet,
      Status = 'COMPLETED',
      CompletedDate = now()
  WHERE PayrollID = payrollid;
  RAISE NOTICE 'Payroll processing completed. Payroll ID: %', payrollid;
EXCEPTION WHEN OTHERS THEN
  BEGIN
    CLOSE employee_cursor;
  EXCEPTION WHEN OTHERS THEN END;
  UPDATE PayrollHeader
  SET Status = 'FAILED',
      ErrorMessage = SQLERRM
  WHERE PayrollID = payrollid;
  RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(payperiodstart timestamp, payperiodend timestamp, departmentid int DEFAULT NULL)
LANGUAGE plpgsql
AS
$$
DECLARE
    payrollid int;
    employeeid int;
    hourlyrate numeric(10,2);
    regularhours numeric(5,2);
    overtimehours numeric(5,2);
    grosspay numeric(10,2);
    taxdeduction numeric(10,2);
    netpay numeric(10,2);
    totalgross numeric(15,2) := 0;
    totalnet numeric(15,2) := 0;
    rec record;
BEGIN
    INSERT INTO PayrollHeader(PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (payperiodstart, payperiodend, departmentid, CURRENT_TIMESTAMP, 'PROCESSING')
    RETURNING PayrollID INTO payrollid;

    FOR rec IN
        SELECT e.EmployeeID, e.HourlyRate,
               COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
               COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN payperiodstart AND payperiodend
        WHERE e.Status = 'ACTIVE' AND (departmentid IS NULL OR e.DepartmentID = departmentid)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID
    LOOP
        employeeid := rec.employeeid;
        hourlyrate := rec.hourlyrate;
        regularhours := rec.regularhours;
        overtimehours := rec.overtimehours;

        grosspay := (regularhours * hourlyrate) + (overtimehours * hourlyrate * 1.5);
        taxdeduction := grosspay * 0.20;
        netpay := grosspay - taxdeduction;

        INSERT INTO PayrollDetail(PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (payrollid, employeeid, hourlyrate, regularhours, overtimehours, grosspay, taxdeduction, netpay);

        totalgross := totalgross + grosspay;
        totalnet := totalnet + netpay;
    END LOOP;

    UPDATE PayrollHeader
    SET TotalGrossPay = totalgross, TotalNetPay = totalnet, Status = 'COMPLETED', CompletedDate = CURRENT_TIMESTAMP
    WHERE PayrollID = payrollid;

    RAISE NOTICE 'Payroll processing completed. Payroll ID: %', payrollid;
EXCEPTION
    WHEN OTHERS THEN
        UPDATE PayrollHeader
        SET Status = 'FAILED', ErrorMessage = SQLERRM
        WHERE PayrollID = payrollid;
        RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
END;
$$;

