CREATE OR REPLACE PROCEDURE SP_DataMigration(source_table VARCHAR(128), target_table VARCHAR(128), batch_size INT DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE
  v_sql TEXT;
  v_source_count INT;
  v_target_count INT;
  v_batch_count INT := 0;
  v_row_count INT;
  v_migration_id INT;
  v_error_message TEXT;
BEGIN
  INSERT INTO MigrationLog(SourceTable, TargetTable, StartTime, Status)
    VALUES (source_table, target_table, CURRENT_TIMESTAMP, 'RUNNING')
    RETURNING MigrationID INTO v_migration_id;
  v_sql := format('SELECT COUNT(*) FROM %I', source_table);
  EXECUTE v_sql INTO v_source_count;
  UPDATE MigrationLog SET SourceCount = v_source_count WHERE MigrationID = v_migration_id;
  v_sql := format('DELETE FROM %I', target_table);
  EXECUTE v_sql;
  WHILE v_batch_count < v_source_count LOOP
    v_sql := format(
      'INSERT INTO %I SELECT * FROM %I s WHERE NOT EXISTS (SELECT 1 FROM %I t WHERE t.ID = s.ID) LIMIT %s',
      target_table, source_table, target_table, batch_size
    );
    EXECUTE v_sql;
    GET DIAGNOSTICS v_row_count = ROW_COUNT;
    IF v_row_count = 0 THEN
      EXIT;
    END IF;
    v_batch_count := v_batch_count + v_row_count;
  END LOOP;
  v_sql := format('SELECT COUNT(*) FROM %I', target_table);
  EXECUTE v_sql INTO v_target_count;
  UPDATE MigrationLog
    SET EndTime = CURRENT_TIMESTAMP,
        TargetCount = v_target_count,
        Status = CASE WHEN v_source_count = v_target_count THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = v_migration_id;
  RAISE NOTICE 'Migration completed for %, Source: %, Target: %', source_table, v_source_count, v_target_count;
EXCEPTION
  WHEN OTHERS THEN
    v_error_message := SQLERRM;
    UPDATE MigrationLog
      SET EndTime = CURRENT_TIMESTAMP,
          Status = 'FAILED',
          ErrorMessage = v_error_message
      WHERE MigrationID = v_migration_id;
    RAISE EXCEPTION 'Data migration failed: %', v_error_message;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_DataMigration(source_table varchar, target_table varchar, batch_size integer DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE
    source_count integer;
    target_count integer;
    batch_count integer := 0;
    migration_id integer;
    v_rowcount integer;
    error_message text;
BEGIN
    INSERT INTO MigrationLog(SourceTable, TargetTable, StartTime, Status)
    VALUES (source_table, target_table, CURRENT_TIMESTAMP, 'RUNNING')
    RETURNING MigrationID INTO migration_id;
    EXECUTE format('SELECT count(*) FROM %I', source_table) INTO source_count;
    UPDATE MigrationLog SET SourceCount = source_count WHERE MigrationID = migration_id;
    EXECUTE format('DELETE FROM %I', target_table);
    WHILE batch_count < source_count LOOP
        EXECUTE format('INSERT INTO %I SELECT * FROM %I s WHERE NOT EXISTS (SELECT 1 FROM %I t WHERE t.ID = s.ID) LIMIT %s', target_table, source_table, target_table, batch_size);
        GET DIAGNOSTICS v_rowcount = ROW_COUNT;
        EXIT WHEN v_rowcount = 0;
        batch_count := batch_count + v_rowcount;
    END LOOP;
    EXECUTE format('SELECT count(*) FROM %I', target_table) INTO target_count;
    UPDATE MigrationLog
    SET EndTime = CURRENT_TIMESTAMP,
        TargetCount = target_count,
        Status = CASE WHEN source_count = target_count THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = migration_id;
    RAISE NOTICE 'Migration completed for %: Source: %, Target: %', source_table, source_count, target_count;
EXCEPTION WHEN OTHERS THEN
    GET STACKED DIAGNOSTICS error_message = MESSAGE_TEXT;
    UPDATE MigrationLog
    SET EndTime = CURRENT_TIMESTAMP,
        Status = 'FAILED',
        ErrorMessage = error_message
    WHERE MigrationID = migration_id;
    RAISE EXCEPTION 'Data migration failed: %', error_message;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_DataMigration(
    IN p_SourceTable VARCHAR(128),
    IN p_TargetTable VARCHAR(128),
    IN p_BatchSize INT DEFAULT 10000
)
LANGUAGE plpgsql
AS
$$
DECLARE
    v_sql TEXT;
    v_SourceCount INT;
    v_TargetCount INT;
    v_BatchCount INT := 0;
    v_MigrationID INT;
    v_ErrorMessage TEXT;
    v_inserted INT;
BEGIN
    INSERT INTO MigrationLog(SourceTable, TargetTable, StartTime, Status)
    VALUES (p_SourceTable, p_TargetTable, NOW(), 'RUNNING')
    RETURNING MigrationID INTO v_MigrationID;

    v_sql := 'SELECT COUNT(*) FROM ' || quote_ident(p_SourceTable);
    EXECUTE v_sql INTO v_SourceCount;

    UPDATE MigrationLog
    SET SourceCount = v_SourceCount
    WHERE MigrationID = v_MigrationID;

    v_sql := 'DELETE FROM ' || quote_ident(p_TargetTable);
    EXECUTE v_sql;

    WHILE v_BatchCount < v_SourceCount LOOP
        v_sql := 'INSERT INTO ' || quote_ident(p_TargetTable) ||
                 ' SELECT * FROM ' || quote_ident(p_SourceTable) || ' s ' ||
                 'WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(p_TargetTable) || ' t WHERE t.id = s.id)' ||
                 ' LIMIT ' || p_BatchSize;
        EXECUTE v_sql;
        GET DIAGNOSTICS v_inserted = ROW_COUNT;
        IF v_inserted = 0 THEN
            EXIT;
        END IF;
        v_BatchCount := v_BatchCount + v_inserted;
    END LOOP;

    v_sql := 'SELECT COUNT(*) FROM ' || quote_ident(p_TargetTable);
    EXECUTE v_sql INTO v_TargetCount;

    UPDATE MigrationLog
    SET EndTime = NOW(),
        TargetCount = v_TargetCount,
        Status = CASE WHEN v_SourceCount = v_TargetCount THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = v_MigrationID;

    RAISE NOTICE 'Migration completed for %, Source: %, Target: %', p_SourceTable, v_SourceCount, v_TargetCount;
EXCEPTION
    WHEN OTHERS THEN
        v_ErrorMessage := SQLERRM;
        UPDATE MigrationLog
        SET EndTime = NOW(),
            Status = 'FAILED',
            ErrorMessage = v_ErrorMessage
        WHERE MigrationID = v_MigrationID;
        RAISE EXCEPTION 'Data migration failed: %', v_ErrorMessage;
END;
$$;

CREATE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INT, IN NewCategoryID INT, IN EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS
$$
DECLARE
  vEffectiveDate TIMESTAMP := EffectiveDate;
  ProductCount INT;
  UpdatedCount INT := 0;
  ProductID INT;
  ProductName VARCHAR(100);
  product_cursor REFCURSOR;
BEGIN
  IF vEffectiveDate IS NULL THEN
    vEffectiveDate := CURRENT_TIMESTAMP;
  END IF;

  IF OldCategoryID = NewCategoryID THEN
    RAISE EXCEPTION 'Old and new category IDs cannot be the same';
  END IF;

  SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
  IF ProductCount = 0 THEN
    RAISE NOTICE 'No active products found in category %', OldCategoryID;
    RETURN;
  END IF;

  OPEN product_cursor FOR SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

  LOOP
    FETCH product_cursor INTO ProductID, ProductName;
    EXIT WHEN NOT FOUND;
    BEGIN
      INSERT INTO ProductCategoryHistory(ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
      VALUES (ProductID, OldCategoryID, NewCategoryID, vEffectiveDate, CURRENT_USER);
      UPDATE Products
      SET CategoryID = NewCategoryID, LastModifiedDate = vEffectiveDate, LastModifiedBy = CURRENT_USER
      WHERE ProductID = ProductID;
      UpdatedCount := UpdatedCount + 1;
    EXCEPTION WHEN OTHERS THEN
      INSERT INTO CategoryUpdateErrors(ProductID, ErrorMessage, ErrorDate)
      VALUES (ProductID, SQLERRM, CURRENT_TIMESTAMP);
    END;
  END LOOP;

  CLOSE product_cursor;

  UPDATE ProductCategories
  SET LastUpdateDate = vEffectiveDate
  WHERE CategoryID = ANY(ARRAY[OldCategoryID, NewCategoryID]);

  RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_updateproductcategories(oldcategoryid integer, newcategoryid integer, effectivedate timestamp default null)
LANGUAGE plpgsql
AS $$
DECLARE
    productcount integer;
    updatedcount integer := 0;
    productid integer;
    productname varchar(100);
    product_cursor CURSOR FOR
        SELECT productid, productname
        FROM products
        WHERE categoryid = oldcategoryid
          AND status = 'ACTIVE';
BEGIN
    IF effectivedate IS NULL THEN
        effectivedate := now();
    END IF;
    IF oldcategoryid = newcategoryid THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;
    SELECT count(*) INTO productcount
    FROM products
    WHERE categoryid = oldcategoryid
      AND status = 'ACTIVE';
    IF productcount = 0 THEN
        RAISE NOTICE 'No active products found in category %', oldcategoryid;
        RETURN;
    END IF;
    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO productid, productname;
        EXIT WHEN NOT FOUND;
        BEGIN
            INSERT INTO productcategoryhistory(productid, oldcategoryid, newcategoryid, changedate, changedby)
            VALUES (productid, oldcategoryid, newcategoryid, effectivedate, session_user);
            UPDATE products
            SET categoryid = newcategoryid,
                lastmodifieddate = effectivedate,
                lastmodifiedby = session_user
            WHERE productid = productid;
            updatedcount := updatedcount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO categoryupdateerrors(productid, errormessage, errordate)
            VALUES (productid, SQLERRM, now());
        END;
    END LOOP;
    CLOSE product_cursor;
    UPDATE productcategories
    SET lastupdatedate = effectivedate
    WHERE categoryid IN (oldcategoryid, newcategoryid);
    RAISE NOTICE 'Category update completed. % products updated out of % total.', updatedcount, productcount;
EXCEPTION WHEN OTHERS THEN
    BEGIN
        CLOSE product_cursor;
    EXCEPTION WHEN OTHERS THEN
        NULL;
    END;
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE PROCEDURE SP_UpdateProductCategories(
    IN OldCategoryID INT,
    IN NewCategoryID INT,
    IN EffectiveDate TIMESTAMP DEFAULT NULL
)
LANGUAGE plpgsql
AS $$
DECLARE
    vEffectiveDate TIMESTAMP := EffectiveDate;
    ProductCount INT;
    UpdatedCount INT := 0;
    rec RECORD;
BEGIN
    IF vEffectiveDate IS NULL THEN
        vEffectiveDate := CURRENT_TIMESTAMP;
    END IF;
    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;
    SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;
    FOR rec IN SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE'
    LOOP
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (rec.ProductID, OldCategoryID, NewCategoryID, vEffectiveDate, CURRENT_USER);
            UPDATE Products
            SET CategoryID = NewCategoryID, LastModifiedDate = vEffectiveDate, LastModifiedBy = CURRENT_USER
            WHERE ProductID = rec.ProductID;
            UpdatedCount := UpdatedCount + 1;
        EXCEPTION WHEN OTHERS THEN
            INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
            VALUES (rec.ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;
    UPDATE ProductCategories SET LastUpdateDate = vEffectiveDate WHERE CategoryID IN (OldCategoryID, NewCategoryID);
    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
EXCEPTION WHEN OTHERS THEN
    RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE PROCEDURE GETEMPLOYEESTATS(p_dept_id IN NUMBER, p_result OUT CLOB) IS
  TYPE stats_rec IS RECORD (
    total_emp       NUMBER,
    avg_salary      NUMBER,
    max_salary      NUMBER,
    active_projects NUMBER
  );
  TYPE p_result_rec IS RECORD (
    employee_id NUMBER,
    last_name   VARCHAR2(50),
    salary      NUMBER(8,2)
  );
  v_stats      stats_rec;
  v_result     p_result_rec;
  v_cursor     SYS_REFCURSOR;
  v_sql        VARCHAR2(1000);
  v_dept_name  VARCHAR2(50);
BEGIN
  SELECT department_name
    INTO v_dept_name
    FROM DEPARTMENTS
   WHERE department_id = p_dept_id;
  SELECT COUNT(*),
         AVG(salary),
         MAX(salary)
    INTO v_stats.total_emp,
         v_stats.avg_salary,
         v_stats.max_salary
    FROM EMPLOYEES
   WHERE department_id = p_dept_id;
  SELECT COUNT(*)
    INTO v_stats.active_projects
    FROM PROJECTS
   WHERE department_id = p_dept_id
     AND status = 'ACTIVE';
  v_sql := 'SELECT * FROM (SELECT employee_id, last_name, salary FROM EMPLOYEES WHERE department_id = :1 ORDER BY salary DESC) WHERE ROWNUM <= 5';
  OPEN v_cursor FOR v_sql USING p_dept_id;
  p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_stats.total_emp || ',  "salary_stats": {    "average": ' || v_stats.avg_salary || ',    "max": ' || v_stats.max_salary || '  },  "active_projects": ' || v_stats.active_projects || ',  "top_earners": [';
  LOOP
    FETCH v_cursor INTO v_result;
    EXIT WHEN v_cursor%NOTFOUND;
    p_result := p_result || '    {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
  END LOOP;
  p_result := RTRIM(p_result, ',') || '  ]}';
  CLOSE v_cursor;
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
  WHEN OTHERS THEN
    p_result := '{"error": "' || SQLERRM || '"}';
END;

