CREATE OR REPLACE PROCEDURE p_job_create_bond_info_all()
LANGUAGE plpgsql
AS $$
DECLARE
    colname text;
    dynsql text;
BEGIN
    TRUNCATE tmp_bond_info_all_20010;
    INSERT INTO tmp_bond_info_all_20010(f_val, def_id, id)
    SELECT t1.field_value, t1.field_definition_id, t2.id
    FROM t_object_field_data t1
    JOIN t_pj_bond_info t2 ON t1.parent_id = t2.apply_id
    WHERE t1.object_no = 20010 AND t1.field_value IS NOT NULL;

    TRUNCATE tmp_bond_info_all_20020;
    INSERT INTO tmp_bond_info_all_20020(f_val, def_id, id)
    SELECT t1.field_value, t1.field_definition_id, t2.id
    FROM t_object_field_data t1
    JOIN t_pj_bond_info t2 ON t1.parent_id = t2.temp_bondid
    WHERE t1.object_no = 20020 AND t1.field_value IS NOT NULL;

    TRUNCATE t_pj_bond_info_all;
    INSERT INTO t_pj_bond_info_all(id, temp_bondid, apply_id)
    SELECT id, temp_bondid, apply_id FROM t_pj_bond_info;

    FOR colname IN
        SELECT column_name FROM information_schema.columns
         WHERE table_name = 't_pj_bond_info_all' AND column_name LIKE 'def%'
    LOOP
        dynsql := 'UPDATE t_pj_bond_info_all a SET ' || quote_ident(colname) ||
                  ' = b.f_val FROM tmp_bond_info_all_20020 b WHERE a.id = b.id AND b.def_id = ' ||
                  replace(colname, 'def_', '');
        EXECUTE dynsql;

        dynsql := 'UPDATE t_pj_bond_info_all a SET ' || quote_ident(colname) ||
                  ' = b.f_val FROM tmp_bond_info_all_20010 b WHERE a.id = b.id AND a.' ||
                  quote_ident(colname) || ' IS NULL AND b.def_id = ' ||
                  replace(colname, 'def_', '');
        EXECUTE dynsql;
    END LOOP;
END;
$$;

