CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE)
RETURNS void AS $$
DECLARE
  v_deleted_rows INTEGER;
BEGIN
  WITH deleted_rows AS (
    DELETE FROM active_data
    WHERE record_date < p_archive_date
    RETURNING record_id, record_data
  )
  INSERT INTO archive_table (record_id, record_data)
  SELECT record_id, record_data FROM deleted_rows;

  GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;

  INSERT INTO audit_log (log_message)
  VALUES (v_deleted_rows || ' rows archived.');
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id integer) RETURNS text AS $$
DECLARE
  v_dept_name text;
  v_total_emp integer;
  v_avg_salary numeric;
  v_max_salary numeric;
  v_active_projects integer;
  rec record;
  result text;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  SELECT count(*)::int, avg(salary), max(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM employees WHERE department_id = p_dept_id;
  SELECT count(*)::int INTO v_active_projects FROM projects WHERE department_id = p_dept_id AND status = 'ACTIVE';
  result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || v_avg_salary || ',    "max": ' || v_max_salary || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';
  FOR rec IN SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5 LOOP
    result := result || '    {"id": ' || rec.employee_id || ', "name": "' || rec.last_name || '", "salary": ' || rec.salary || '},';
  END LOOP;
  result := rtrim(result, ',') || '  ]}';
  RETURN result;
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    RETURN '{"error": "Department not found"}';
  WHEN OTHERS THEN
    RETURN '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE usp_updateorderstatus(p_customerid INT,p_oldstatus VARCHAR(20),p_newstatus VARCHAR(20),OUT rows_updated INT,OUT error_message VARCHAR(200)) LANGUAGE plpgsql AS $$
DECLARE
    processing_fee DECIMAL(10,2);
    fee_cursor REFCURSOR;
BEGIN
    rows_updated := 0;
    error_message := '';
    IF p_oldstatus = p_newstatus THEN
        error_message := '新旧状态不能相同';
        RETURN;
    END IF;
    BEGIN
        START TRANSACTION;
        UPDATE orders
        SET status = p_newstatus,
            lastupdated = NOW()
        WHERE customerid = p_customerid
          AND status = p_oldstatus
          AND orderdate > NOW() - INTERVAL '3 months';
        GET DIAGNOSTICS rows_updated = ROW_COUNT;
        IF rows_updated = 0 THEN
            error_message := '未找到符合条件的订单';
            ROLLBACK;
            RETURN;
        END IF;
        INSERT INTO orderlog(orderid,oldstatus,newstatus,changedate)
        SELECT orderid,p_oldstatus,p_newstatus,NOW()
        FROM orders
        WHERE customerid = p_customerid
          AND status = p_newstatus;
        OPEN fee_cursor FOR
            SELECT fee
            FROM orderdetails
            WHERE orderid IN (
                SELECT orderid
                FROM orders
                WHERE customerid = p_customerid
                  AND status = p_newstatus
            );
        LOOP
            FETCH fee_cursor INTO processing_fee;
            EXIT WHEN NOT FOUND;
            processing_fee := processing_fee * 0.95;
        END LOOP;
        CLOSE fee_cursor;
        COMMIT;
        error_message := '更新成功';
    EXCEPTION WHEN OTHERS THEN
        ROLLBACK;
        error_message := SQLERRM;
    END;
END;
$$;

