CREATE OR REPLACE PROCEDURE "LFBB_BVC_VHG_CHECK"( SKI_VESSEL_CODE VARCHAR2, OPL_BCGDK_SWC VARCHAR2, MMS_DEPTH VARCHAR2, SKG_RESPONSIBLE VARCHAR2, UIS_CHECK_BC_NAME VARCHAR2, JKS_CHECK_DATE VARCHAR2 ) IS 
CURSOR CUR(I_BEGIN_DATE DATE,I_END_DATE DATE) IS 
SELECT CWBT_NAME, I.HCJI_CODE, CHECK_NAME, DEPT_NAME, I.DEPT, RESPONSIBLE_PERSON_NAME, I.RESPONSIBLE_PERSON, CHECK_RESULT, I.CHECK_RESULT_NAME, I.BCGDK_SS2, I.CHECK_DATE, I.VESSEL_CODE, I.VESSEL_NAME, I.ROUTINE_CHECK_ITEM_ID, I.REMARK, I.CREATED_BY_USER, I.CREATED_OFFICE, I.CREATED_DTM_LOC, I.CREATED_TIME_ZONE, I.UPDATED_BY_USER, I.UPDATED_OFFICE, I.UPDATED_DTM_LOC, I.UPDATED_TIME_ZONE, I.COMPANY_CODE, I.RECORD_VERSION, I.PRINCIPAL_GROUP_CODE 
FROM VW_R_CHECK_INFO I 
WHERE I.BCGDK_SS2 = OPL_BCGDK_SWC 
AND I.VESSEL_CODE = SKI_VESSEL_CODE 
AND I.CHECK_DATE >= I_BEGIN_DATE 
AND I.CHECK_DATE <= I_END_DATE 
AND (MMS_DEPTH IS NULL OR I.DEPT = MMS_DEPTH) 
AND (SKG_RESPONSIBLE IS NULL OR I.RESPONSIBLE_PERSON = SKG_RESPONSIBLE) 
AND (UIS_CHECK_BC_NAME IS NULL OR (UIS_CHECK_BC_NAME IS NOT NULL AND I.CHECK_NAME LIKE '%' || UIS_CHECK_BC_NAME || '%')) 
AND I.DATA_TYPE = 'S';
INT_COUNT INTEGER;
INT_COUNT1 INTEGER;
VAR_SUFFIX VARCHAR2(2);
DAT_BEGIN DATE;
DAT_END DATE;
BEGIN 
EXECUTE IMMEDIATE 'TRUNCATE TABLE G_R_CHECK_INFO';
IF JKS_CHECK_DATE IS NULL THEN 
DAT_BEGIN := TO_DATE(TO_CHAR(SYSDATE, 'YYYY') || '-01-01', 'YYYY-MM-DD'); 
DAT_END := TO_DATE(TO_CHAR(SYSDATE, 'YYYY') || '-12-31', 'YYYY-MM-DD'); 
ELSIF LENGTH(JKS_CHECK_DATE) = 4 THEN 
DAT_BEGIN := TO_DATE(JKS_CHECK_DATE || '-06-24', 'YYYY-MM-DD'); 
DAT_END := TO_DATE(JKS_CHECK_DATE || '-10-24', 'YYYY-MM-DD'); 
ELSE 
DAT_BEGIN := TO_DATE(JKS_CHECK_DATE || '-03', 'YYYY-MM-DD'); 
DAT_END := LAST_DAY(DAT_BEGIN); 
END IF;
FOR REC IN CUR(DAT_BEGIN, DAT_END) LOOP 
SELECT COUNT(1) INTO INT_COUNT FROM G_R_CHECK_INFO I WHERE I.PID = REC.ROUTINE_CHECK_ITEM_ID;
IF INT_COUNT = 0 THEN 
INSERT INTO G_R_CHECK_INFO (PID, CWBT_NAME, HCJI_CODE, CHECK_NAME, DEPT_NAME, DEPT, RESPONSIBLE_PERSON_NAME, RESPONSIBLE_PERSON, BCGDK_SS2, CHECK_DATE, VESSEL_CODE, VESSEL_NAME, COMPANY_CODE, CREATED_BY_USER, CREATED_OFFICE, CREATED_DTM_LOC, CREATED_TIME_ZONE, UPDATED_BY_USER, UPDATED_OFFICE, UPDATED_DTM_LOC, UPDATED_TIME_ZONE, RECORD_VERSION, PRINCIPAL_GROUP_CODE) 
SELECT REC.ROUTINE_CHECK_ITEM_ID, REC.CWBT_NAME, REC.HCJI_CODE, REC.CHECK_NAME, REC.DEPT_NAME, REC.DEPT, REC.RESPONSIBLE_PERSON_NAME, REC.RESPONSIBLE_PERSON, REC.BCGDK_SS2, TRUNC(REC.CHECK_DATE, 'MM'), REC.VESSEL_CODE, REC.VESSEL_NAME, REC.COMPANY_CODE, REC.CREATED_BY_USER, REC.CREATED_OFFICE, REC.CREATED_DTM_LOC, REC.CREATED_TIME_ZONE, REC.UPDATED_BY_USER, REC.UPDATED_OFFICE, REC.UPDATED_DTM_LOC, REC.UPDATED_TIME_ZONE, REC.RECORD_VERSION, REC.PRINCIPAL_GROUP_CODE FROM DUAL;
END IF;
SELECT CASE OPL_BCGDK_SWC 
WHEN 'A' THEN CAST(TO_CHAR(REC.CHECK_DATE, 'DD') AS NUMBER) 
WHEN 'B' THEN CAST(TO_CHAR(PKG_DATE_UTIL.GET_1ST_M(REC.CHECK_DATE, DECODE(LENGTH(JKS_CHECK_DATE), 4, 'YY', 'MM')), DECODE(LENGTH(JKS_CHECK_DATE), 4, 'WW', 'W')) AS NUMBER) 
WHEN 'C' THEN CAST(TO_CHAR(REC.CHECK_DATE, 'MM') AS NUMBER) 
END INTO VAR_SUFFIX FROM DUAL;
EXECUTE IMMEDIATE 'UPDATE G_R_CHECK_INFO I SET N' || VAR_SUFFIX || ' = :1 WHERE I.PID = :2' USING 
CASE REC.CHECK_RESULT WHEN '0' THEN '√' WHEN '1' THEN '×' WHEN '2' THEN 'O' WHEN '3' THEN '—' END || SUBSTR(REC.REMARK, 1, 50), REC.ROUTINE_CHECK_ITEM_ID;
END LOOP;
SELECT COUNT(*) INTO INT_COUNT1 FROM G_R_CHECK_INFO WHERE VESSEL_CODE = '0336';
COMMIT;
EXCEPTION 
WHEN OTHERS THEN 
ROLLBACK;
DBMS_OUTPUT.ENABLE(10000);
DBMS_OUTPUT.PUT_LINE(SQLERRM);
END;

CREATE OR REPLACE FUNCTION validate_context() RETURNS void AS $$
DECLARE
    l_type int;
    l_owner int;
    l_ltype varchar(30) := 'FIRST';
    l_status int;
    l_compile_sql varchar(2000);
    l_dbo_name varchar(32);
    c1 RECORD;
    c2 RECORD;
BEGIN
    SELECT oid INTO l_owner FROM pg_namespace WHERE nspname = 'ctxsys';
    FOR c1 IN SELECT dbo_name, dbo_type FROM ctxsys.dr_dbo WHERE dbo_type != 'PUBLIC SYNONYM' ORDER BY dbo_type, dbo_name LOOP
        IF (c1.dbo_type != l_ltype) THEN
            SELECT CASE c1.dbo_type
                WHEN 'INDEX' THEN 1
                WHEN 'TABLE' THEN 2
                WHEN 'VIEW' THEN 4
                WHEN 'SEQUENCE' THEN 6
                WHEN 'PROCEDURE' THEN 7
                WHEN 'FUNCTION' THEN 8
                WHEN 'PACKAGE' THEN 9
                WHEN 'PACKAGE BODY' THEN 11
                WHEN 'TYPE' THEN 13
                WHEN 'TYPE BODY' THEN 14
                WHEN 'LIBRARY' THEN 22
                WHEN 'INDEXTYPE' THEN 32
                WHEN 'OPERATOR' THEN 33
                ELSE 0
            END INTO l_type;
            l_ltype := c1.dbo_type;
        END IF;
        l_status := -1;
        FOR c2 IN SELECT 1 AS status FROM pg_class cl JOIN pg_namespace ns ON cl.relnamespace = ns.oid
                  WHERE ns.oid = l_owner AND cl.relname = c1.dbo_name AND
                  CASE c1.dbo_type
                      WHEN 'TABLE' THEN cl.relkind = 'r'
                      WHEN 'VIEW' THEN cl.relkind = 'v'
                      WHEN 'SEQUENCE' THEN cl.relkind = 'S'
                      ELSE false
                  END
        LOOP
            l_status := c2.status;
        END LOOP;
        IF (l_status != 1) THEN
            l_dbo_name := quote_ident(c1.dbo_name);
            l_compile_sql := CASE c1.dbo_type
                WHEN 'VIEW' THEN 'ALTER VIEW ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PROCEDURE' THEN 'ALTER PROCEDURE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'FUNCTION' THEN 'ALTER FUNCTION ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'PACKAGE BODY' THEN 'ALTER PACKAGE ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'TYPE' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'TYPE BODY' THEN 'ALTER TYPE ctxsys.' || l_dbo_name || ' COMPILE BODY'
                WHEN 'INDEXTYPE' THEN 'ALTER INDEXTYPE ctxsys.' || l_dbo_name || ' COMPILE'
                WHEN 'OPERATOR' THEN 'ALTER OPERATOR ctxsys.' || l_dbo_name || ' COMPILE'
                ELSE NULL
            END;
            IF l_compile_sql IS NULL THEN
                RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
                PERFORM dbms_registry_invalid('CONTEXT');
                RETURN;
            ELSE
                BEGIN
                    EXECUTE l_compile_sql;
                EXCEPTION
                    WHEN OTHERS THEN
                        RAISE NOTICE 'FAILED CHECK FOR % %', c1.dbo_type, c1.dbo_name;
                        PERFORM dbms_registry_invalid('CONTEXT');
                        RETURN;
                END;
            END IF;
        END IF;
    END LOOP;
    PERFORM dbms_registry_valid('CONTEXT');
EXCEPTION
    WHEN OTHERS THEN
        PERFORM ctxsys.drue.text_on_stack(SQLERRM, 'validate_context');
        PERFORM dbms_registry_invalid('CONTEXT');
        PERFORM ctxsys.drue.raise();
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id INTEGER, p_adjustment_type VARCHAR, p_adjustment_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP) RETURNS VOID AS $$
DECLARE
    TYPE t_inventory_rec IS RECORD (
        product_id INTEGER,
        current_qty NUMERIC,
        adjustment_qty NUMERIC,
        new_qty NUMERIC
    );
    TYPE t_inventory_tab IS TABLE OF t_inventory_rec INDEX BY INTEGER;
    v_inventory t_inventory_tab;
    v_batch_size CONSTANT INTEGER := 1000;
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    c_inventory REFCURSOR;
    rec RECORD;
BEGIN
    SAVEPOINT bulk_update_start;
    OPEN c_inventory FOR
        SELECT product_id, quantity_on_hand,
               CASE p_adjustment_type
                   WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                   WHEN 'DAMAGE' THEN -damaged_quantity
                   WHEN 'RETURN' THEN returned_quantity
                   ELSE 0
               END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
          AND status = 'PENDING'
        ORDER BY product_id;

    LOOP
        FETCH c_inventory BULK COLLECT INTO v_inventory LIMIT v_batch_size;
        EXIT WHEN array_length(v_inventory, 1) IS NULL OR array_length(v_inventory, 1) = 0;

        FOR i IN 1..array_length(v_inventory, 1) LOOP
            BEGIN
                -- Perform upsert using INSERT ... ON CONFLICT
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (
                    v_inventory[i].product_id,
                    p_warehouse_id,
                    GREATEST(0, v_inventory[i].adjustment_qty),
                    p_adjustment_date,
                    p_adjustment_type
                )
                ON CONFLICT (product_id, warehouse_id)
                DO UPDATE SET
                    quantity_on_hand = inventory.quantity_on_hand + EXCLUDED.quantity_on_hand,
                    last_adjustment_date = EXCLUDED.last_adjustment_date,
                    last_adjustment_type = EXCLUDED.last_adjustment_type;

                INSERT INTO inventory_audit_log (
                    product_id, warehouse_id, adjustment_date, adjustment_type,
                    old_quantity, adjustment_quantity, new_quantity, created_by
                ) VALUES (
                    v_inventory[i].product_id,
                    p_warehouse_id,
                    p_adjustment_date,
                    p_adjustment_type,
                    v_inventory[i].current_qty,
                    v_inventory[i].adjustment_qty,
                    v_inventory[i].current_qty + v_inventory[i].adjustment_qty,
                    USER
                );

                v_total_processed := v_total_processed + 1;
            EXCEPTION
                WHEN OTHERS THEN
                    v_error_count := v_error_count + 1;
                    INSERT INTO error_log (error_date, error_message, product_id)
                    VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, v_inventory[i].product_id);
            END;
        END LOOP;
    END LOOP;

    CLOSE c_inventory;

    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = CURRENT_TIMESTAMP
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';

    COMMIT;
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK TO SAVEPOINT bulk_update_start;
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS VOID AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_report_sql TEXT;
    TYPE t_sales_summary IS RECORD (
        sales_rep_id INTEGER,
        sales_rep_name VARCHAR(100),
        total_sales NUMERIC,
        commission_rate NUMERIC,
        commission_amount NUMERIC
    );
    v_sales_data t_sales_summary[];
    v_start_date DATE;
    v_end_date DATE;
    rec t_sales_summary;
BEGIN
    v_start_date := TO_DATE(p_year || '-' || LPAD(((p_quarter - 1) * 3 + 1)::TEXT, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + INTERVAL '2 months' - INTERVAL '1 day')::DATE;
    SELECT NEXTVAL('sales_report_seq') INTO v_report_id;
    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';
    EXECUTE v_report_sql INTO v_sales_data USING v_start_date, v_end_date, p_sales_rep_id;
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, NOW(), ARRAY_LENGTH(v_sales_data, 1));
    FOREACH rec IN ARRAY v_sales_data
    LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
    END LOOP;
    UPDATE sales_report_header
    SET total_sales = v_total_sales, total_commission = v_total_commission
    WHERE report_id = v_report_id;
    COMMIT;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK;
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(
    WarehouseID INTEGER,
    AdjustmentType VARCHAR(50),
    AdjustmentDate TIMESTAMP DEFAULT NULL
)
AS $$
DECLARE
    ProductID INTEGER;
    CurrentQty INTEGER;
    AdjustmentQty INTEGER;
    NewQty INTEGER;
    TotalProcessed INTEGER := 0;
    ErrorCount INTEGER := 0;
    ErrorMessage VARCHAR(4000);
    inventory_cursor REFCURSOR;
BEGIN
    IF AdjustmentDate IS NULL THEN
        AdjustmentDate := CURRENT_TIMESTAMP;
    END IF;

    OPEN inventory_cursor FOR
        SELECT ProductID, QuantityOnHand,
               CASE AdjustmentType
                   WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                   WHEN 'DAMAGE' THEN -DamagedQuantity
                   WHEN 'RETURN' THEN ReturnedQuantity
                   ELSE 0
               END AS AdjustmentQty
        FROM InventoryStaging
        WHERE WarehouseID = SP_BulkInventoryUpdate.WarehouseID
          AND Status = 'PENDING'
        ORDER BY ProductID;

    BEGIN
        START TRANSACTION;

        LOOP
            FETCH inventory_cursor INTO ProductID, CurrentQty, AdjustmentQty;
            EXIT WHEN NOT FOUND;

            BEGIN
                NewQty := CurrentQty + AdjustmentQty;

                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = ProductID AND WarehouseID = WarehouseID) THEN
                    UPDATE Inventory
                    SET QuantityOnHand = NewQty,
                        LastAdjustmentDate = AdjustmentDate,
                        LastAdjustmentType = AdjustmentType
                    WHERE ProductID = ProductID AND WarehouseID = WarehouseID;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (ProductID, WarehouseID, GREATEST(NewQty, 0), AdjustmentDate, AdjustmentType);
                END IF;

                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, CurrentQty, AdjustmentQty, NewQty, USER);

                TotalProcessed := TotalProcessed + 1;

            EXCEPTION
                WHEN OTHERS THEN
                    ErrorCount := ErrorCount + 1;
                    INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                    VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, ProductID);
            END;
        END LOOP;

        CLOSE inventory_cursor;

        UPDATE InventoryStaging
        SET Status = 'PROCESSED', ProcessedDate = CURRENT_TIMESTAMP
        WHERE WarehouseID = WarehouseID AND Status = 'PENDING';

        COMMIT;
        RAISE INFO 'Bulk inventory update completed. Processed: %, Errors: %', TotalProcessed, ErrorCount;
    EXCEPTION
        WHEN OTHERS THEN
            ErrorMessage := SQLERRM;
            ROLLBACK;
            IF inventory_cursor IS NOT NULL THEN
                CLOSE inventory_cursor;
            END IF;
            RAISE EXCEPTION 'Bulk inventory update failed: %', ErrorMessage;
    END;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(IN PayPeriodStart TIMESTAMP, IN PayPeriodEnd TIMESTAMP, IN DepartmentID INTEGER DEFAULT NULL) AS
DECLARE
    PayrollID INTEGER;
    EmployeeID INTEGER;
    HourlyRate NUMERIC(10,2);
    RegularHours NUMERIC(5,2);
    OvertimeHours NUMERIC(5,2);
    GrossPay NUMERIC(10,2);
    TaxDeduction NUMERIC(10,2);
    NetPay NUMERIC(10,2);
    TotalGross NUMERIC(15,2) := 0;
    TotalNet NUMERIC(15,2) := 0;
    CURSOR employee_cursor IS
        SELECT e.EmployeeID, e.HourlyRate,
               COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
               COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN PayPeriodStart AND PayPeriodEnd
        WHERE e.Status = 'ACTIVE' AND (DepartmentID IS NULL OR e.DepartmentID = DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID;
BEGIN
    SET CURRENT_SCHEMA TO 'public';
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (PayPeriodStart, PayPeriodEnd, DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING');
    PayrollID := CURRVAL('PayrollHeader_PayrollID_seq');
    OPEN employee_cursor;
    LOOP
        FETCH employee_cursor INTO EmployeeID, HourlyRate, RegularHours, OvertimeHours;
        EXIT WHEN NOT FOUND;
        GrossPay := (RegularHours * HourlyRate) + (OvertimeHours * HourlyRate * 1.5);
        TaxDeduction := GrossPay * 0.20;
        NetPay := GrossPay - TaxDeduction;
        INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay);
        TotalGross := TotalGross + GrossPay;
        TotalNet := TotalNet + NetPay;
    END LOOP;
    CLOSE employee_cursor;
    UPDATE PayrollHeader
    SET TotalGrossPay = TotalGross,
        TotalNetPay = TotalNet,
        Status = 'COMPLETED',
        CompletedDate = CURRENT_TIMESTAMP
    WHERE PayrollID = PayrollID;
    RAISE INFO 'Payroll processing completed. Payroll ID: %', PayrollID;
EXCEPTION
    WHEN OTHERS THEN
        IF employee_cursor%ISOPEN THEN
            CLOSE employee_cursor;
        END IF;
        UPDATE PayrollHeader
        SET Status = 'FAILED',
            ErrorMessage = SQLERRM
        WHERE PayrollID = PayrollID;
        RAISE EXCEPTION 'Payroll processing failed: %', SQLERRM;
END;

CREATE OR REPLACE PROCEDURE SP_CleanupOldLogs(RetentionDays IN INT DEFAULT 90)
AS
DECLARE
    CutoffDate TIMESTAMP;
    DeletedCount INT := 0;
    v_error_message TEXT;
BEGIN
    CutoffDate := CURRENT_TIMESTAMP - INTERVAL RetentionDays || ' days';

    BEGIN
        DELETE FROM ApplicationLogs WHERE LogDate < CutoffDate;
        DeletedCount := DeletedCount + SQL%ROWCOUNT;

        DELETE FROM ErrorLogs WHERE ErrorDate < CutoffDate;
        DeletedCount := DeletedCount + SQL%ROWCOUNT;

        DELETE FROM AuditLogs WHERE AuditDate < CutoffDate;
        DeletedCount := DeletedCount + SQL%ROWCOUNT;

        INSERT INTO CleanupHistory (CleanupDate, TableName, RecordsDeleted, RetentionDays)
        VALUES (CURRENT_TIMESTAMP, 'MULTIPLE_LOG_TABLES', DeletedCount, RetentionDays);

        RAISE INFO 'Cleanup completed. % records deleted.', DeletedCount;
    EXCEPTION
        WHEN OTHERS THEN
            GET STACKED DIAGNOSTICS v_error_message = MESSAGE_TEXT;
            RAISE EXCEPTION 'Log cleanup failed: %', v_error_message;
    END;
END;

CREATE OR REPLACE PROCEDURE SP_DataMigration(
    SourceTable VARCHAR(128),
    TargetTable VARCHAR(128),
    BatchSize INT DEFAULT 10000
)
AS
    SQLStmt TEXT;
    SourceCount INT := 0;
    TargetCount INT := 0;
    BatchCount INT := 0;
    MigrationID INT;
    ErrorMessage TEXT;
BEGIN
    SET CURRENT_SCHEMA TO 'public';
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (SourceTable, TargetTable, CURRENT_TIMESTAMP, 'RUNNING');
    MigrationID := CURRVAL('migrationlog_migrationid_seq');

    EXECUTE IMMEDIATE 'SELECT COUNT(*) FROM ' || quote_ident(SourceTable) INTO SourceCount;
    UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;

    EXECUTE IMMEDIATE 'DELETE FROM ' || quote_ident(TargetTable);

    WHILE BatchCount < SourceCount LOOP
        SQLStmt := 'INSERT INTO ' || quote_ident(TargetTable) ||
                   ' SELECT * FROM (SELECT * FROM ' || quote_ident(SourceTable) ||
                   ' WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(TargetTable) ||
                   ' t WHERE t.ID = ' || quote_ident(SourceTable) || '.ID) LIMIT ' || BatchSize || ') AS subquery';
        EXECUTE IMMEDIATE SQLStmt;
        IF ROW_COUNT() = 0 THEN
            EXIT;
        END IF;
        BatchCount := BatchCount + ROW_COUNT();
    END LOOP;

    EXECUTE IMMEDIATE 'SELECT COUNT(*) FROM ' || quote_ident(TargetTable) INTO TargetCount;
    UPDATE MigrationLog
    SET EndTime = CURRENT_TIMESTAMP,
        TargetCount = TargetCount,
        Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = MigrationID;

    RAISE INFO 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;

EXCEPTION
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS ErrorMessage = MESSAGE_TEXT;
        UPDATE MigrationLog
        SET EndTime = CURRENT_TIMESTAMP,
            Status = 'FAILED',
            ErrorMessage = ErrorMessage
        WHERE MigrationID = MigrationID;
        RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN OldCategoryID INT, IN NewCategoryID INT, IN EffectiveDate TIMESTAMP DEFAULT NULL)
AS $$
DECLARE
    ProductCount INT := 0;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName VARCHAR(100);
    product_cursor REFCURSOR;
    v_error_message TEXT;
BEGIN
    SET SESSION CHARACTERISTICS AS TRANSACTION ISOLATION LEVEL READ COMMITTED;
    IF EffectiveDate IS NULL THEN
        EffectiveDate := CURRENT_TIMESTAMP;
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO ProductCount
    FROM Products
    WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor FOR
        SELECT ProductID, ProductName
        FROM Products
        WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    LOOP
        FETCH product_cursor INTO ProductID, ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDate, CURRENT_USER);

            UPDATE Products
            SET CategoryID = NewCategoryID,
                LastModifiedDate = EffectiveDate,
                LastModifiedBy = CURRENT_USER
            WHERE ProductID = ProductID;

            UpdatedCount := UpdatedCount + 1;

        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    CLOSE product_cursor;

    UPDATE ProductCategories
    SET LastUpdateDate = EffectiveDate
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;

EXCEPTION
    WHEN OTHERS THEN
        BEGIN
            IF product_cursor IS NOT NULL THEN
                CLOSE product_cursor;
            END IF;
            RAISE EXCEPTION 'Category update failed: %', SQLERRM;
        END;
END;
