CREATE OR REPLACE PROCEDURE "SP_FEE_CSC_DAILY_JOB" 
 IS V_FLAG varchar2(50); 
 BEGIN 
 select open_mode into V_FLAG from v$database; 
 if V_FLAG = 'READ WRITE' then 
 P_LOG_EXCEPTION('开始时间:' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 EXECUTE IMMEDIATE 'truncate table FMM_CSCL_BUSI_CALIBER'; 
 insert into FMM_CSCL_BUSI_CALIBER select * from VOP_VESSEL_CSCL_BUSI_CALIBER; 
 commit; 
 P_LOG_EXCEPTION('FMM_CSCL_BUSI_CALIBER完成' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_esti'; 
 insert into fmm_csc_maintenance_esti select * from vw_csc_maintenance_esti; 
 commit; 
 P_LOG_EXCEPTION('fmm_csc_maintenance_esti 完成' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_orders'; 
 insert into fmm_csc_maintenance_orders select * from VW_CSC_MAINTENANCE_ORDERS; 
 commit; 
 P_LOG_EXCEPTION('fmm_csc_maintenance_orders 完成' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 EXECUTE IMMEDIATE 'truncate table fmm_csc_maintenance_invoice'; 
 insert into fmm_csc_maintenance_invoice select * from vw_csc_maintenance_invoice; 
 commit; 
 INSERT INTO fmm_csc_maintenance_invoice 
 SELECT TEMP.VESSEL_CODE, TEMP.SUPPLIER_CODE, TEMP.PAYER, NULL AS PORT_CODE, TEMP.INVOICE_STATUS, TEMP.INVOICE_SORT, TEMP.ORDER_NO, TEMP.PAYMENT_NO, TEMP.INVOICE_SORT_DETAIL, TEMP.INVOICE_DETAIL_ID, TEMP.BASE_AMOUNT_NOTAX, TEMP.INVOICE_DATE, TEMP.CURRENCY, TEMP.DTL_AMOUNT_NOTAX, TEMP.INVOICE_NUM, TEMP.PAYEE, TEMP.ACTIVITY_DATE, TEMP.FI_RECEIVEDATE, TEMP.INPUT_DATE, TEMP.UPDATE_TIME, TEMP.BASE_AMOUNT, TEMP.AMOUNT, 0 AS MISC_AMOUNT, 0 AS BASE_MISC_AMOUNT, 
 nvl(pkg_oas.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, NVL(TEMP.ACTIVITY_DATE,SYSDATE),'VEL_MANAGE_BODY'), pkg_oas.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, SYSDATE, 'VEL_MANAGE_BODY') ) AS COMPANY_CODE, 
 TEMP.SEND_FIN_DATE, TEMP.APPLY_TYPE, '2' AS BUSI_TYPE, TEMP.IS_ENERGY, TEMP.TYPE_NAME, TEMP.ORDER_CURRENCY, TEMP.EXCHANGE_RATE, NULL AS BILL_NO, 
 nvl(pkg_oas.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, NVL(TEMP.ACTIVITY_DATE,SYSDATE),'COST_BODY'), pkg_oas.FN_GET_MAN_COM_CODE(TEMP.VESSEL_CODE, SYSDATE, 'COST_BODY') ) AS OWNERSHIP_BODY, TEMP.OPRT_WAY, TEMP.USD_AMOUNT 
 FROM ( 
 select I.VESSEL_CODE, substr(I.PO_NUMBER,1,replace(instr(I.PO_NUMBER,'-',-1)-1,-1,length(I.PO_NUMBER))) AS ORDER_NO, BFM.CONFIRM_DATE AS ORDER_DATE, T.DISPLAY_VALUE_CN AS TYPE_NAME, BFM.CURRENCY AS ORDER_CURRENCY, BFM.AMOUNT AS TOTAL_FEE, NVL(BFM.SUPPLIER,I.PAYEE) AS SUPPLIER_CODE, 'HY' AS APPLY_TYPE, I.PAYER, I.INVOICE_STATUS, 'FY33' AS INVOICE_SORT, 
 (CASE WHEN I.INVOICE_SORT_DETAIL = 'FY03001' THEN 'FY33002' WHEN I.INVOICE_SORT_DETAIL = 'FY03002' THEN 'FY33003' WHEN I.INVOICE_SORT_DETAIL = 'FY03003' THEN 'FY33004' WHEN I.INVOICE_SORT_DETAIL = 'FY03004' THEN 'FY33005' WHEN I.INVOICE_SORT_DETAIL in ('FY03005', 'FY03006', 'FY03007') THEN 'FY33006' ELSE 'FY33006' END) AS INVOICE_SORT_DETAIL, 
 'RLF' AS GROUP_SORT, 
 (CASE WHEN I.INVOICE_SORT_DETAIL = 'FY03001' THEN 'YZ30003' WHEN I.INVOICE_SORT_DETAIL = 'FY03002' THEN 'YZ30001' WHEN I.INVOICE_SORT_DETAIL = 'FY03003' THEN 'YZ30002' WHEN I.INVOICE_SORT_DETAIL in ('FY03004','FY03005', 'FY03006', 'FY03007') THEN 'YZ30004' ELSE 'YZ30004' END) AS GROUP_SORT_DETAIL, 
 I.INVOICE_DETAIL_ID, NVL(I.BASE_AMOUNT,I.DTL_AMOUNT*NVL(I.RMB_EXCHANGE_RATE,1)) / (1 + NVL(I.TAX_RATE,0)) AS BASE_AMOUNT_notax, I.INVOICE_DATE, I.CURRENCY, I.DTL_AMOUNT / (1 + I.TAX_RATE) AS DTL_AMOUNT_notax, I.INVOICE_NUM, I.PAYEE, 
 (CASE WHEN I.COMPANY_CODE IN ('08020000','04000000','02000000','62000000') THEN I.CREATED_DTM_LOC WHEN I.PAYER LIKE '64%' THEN I.CREATED_DTM_LOC ELSE BFM.SUPPLY_DATE END) AS ACTIVITY_DATE, 
 I.SEND_FIN_DATE AS FI_RECEIVEDATE, NULL AS IS_ENERGY, I.CREATED_DTM_LOC AS INPUT_DATE, NVL(NVL(I.UPDATED_DTM_LOC, I.CREATED_DTM_LOC), SYSDATE - 1) AS UPDATE_TIME, '3' as rpt_type, NVL(I.BASE_AMOUNT,I.DTL_AMOUNT*NVL(I.RMB_EXCHANGE_RATE,1)) AS BASE_AMOUNT, I.DTL_AMOUNT AS AMOUNT, I.COMPANY_CODE, NVL(I.SEND_FIN_DATE, I.INVOICE_DATE) AS SEND_FIN_DATE, 
 row_number() over(partition by I.INVOICE_DETAIL_ID order by BFM.SUPPLY_DATE desc) rn, I.PAYMENT_NO, I.RMB_EXCHANGE_RATE AS EXCHANGE_RATE, '1' AS OPRT_WAY, ROUND(I.DTL_AMOUNT*fn_get_exchange_rate(I.CURRENCY,'USD',BFM.SUPPLY_DATE),2) AS USD_AMOUNT 
 FROM bfm_invoice_detail I 
 LEFT JOIN BFM_PURCHASE_ORDER BFM ON substr(I.PO_NUMBER,1,replace(instr(I.PO_NUMBER,'-',-1)-1,-1,length(I.PO_NUMBER))) = BFM.PO_NUMBER 
 LEFT JOIN BFM_OIL_STORAGE BOS ON BOS.operate = '10' AND substr(BOS.PO_NUMBER,1,replace(instr(BOS.PO_NUMBER,'-',-1)-1,-1,length(BOS.PO_NUMBER))) = BFM.PO_NUMBER AND BOS.oil_type = '002' 
 LEFT JOIN CDM_CODEDICT T ON T.code_type = 'BFM_FILL_TYPE' AND BFM.FILL_OIL_TYPE = T.CODE_VALUE 
 LEFT JOIN SAP_JA0012_MTR_MIDDLE M ON BOS.PO_NUMBER = M.TRD_ORDER 
 WHERE I.INVOICE_SORT = 'FY03' AND NVL(I.IS_DELETE,'0')<>'1' AND I.PAYER LIKE '64%' 
 ) TEMP 
 WHERE RN = 1; 
 P_LOG_EXCEPTION('fmm_csc_maintenance_invoice 完成' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 update sps_parts_info sp set sp.if_storaged = null 
 where EXISTS(select trim(group_code) from SPS_PARTS_MASTER_DATA t where t.if_storaged is null AND trim(T.group_code)=SP.GROUP_CODE) 
 AND (SP.VESSEL_CODE LIKE 'S%' OR SP.VESSEL_CODE LIKE 'E%'); 
 EXECUTE IMMEDIATE 'truncate table fmm_csc_virtual_budget'; 
 insert into fmm_csc_virtual_budget select * from vw_csc_virtual_budget; 
 commit; 
 P_LOG_EXCEPTION('fmm_csc_virtual_budget 完成' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 update SPS_APPLY_main sam set (sam.equipment_info_id, sam.equipment_code) = (select sad.equipment_id, sad.equip_code from sps_apply_detail sad where sad.apply_main_id = SAM.APPLY_MAIN_ID and rownum = 1) 
 where sam.apply_main_id IN (select SAM.apply_main_id from SPS_APPLY_main SAM where SAM.EQUIPMENT_INFO_ID is null and SAM.vessel_code in (select vv.vessel_code from dss_vop_vessel_info vv where vv.vesman_code = '68280000' and nvl(vv.tetired_flag, '1') = '1' and nvl(vv.trade_type, '0') = '0')); 
 update SPS_ENQUIRE_MAIN SS set (SS.EQUIPMENT_INFO_ID, SS.EQUIPMENT_CODE) = (select SED.equipment_id, SED.EQUIP_CODE from SPS_ENQUIRE_DETAIL SED where SED.ENQUIRE_MAIN_ID = SS.ENQUIRE_MAIN_ID and rownum = 1) 
 where SS.ENQUIRE_MAIN_ID IN (select SEM.ENQUIRE_MAIN_ID from sps_enquire_main sem where sem.equipment_info_id is null and sem.vessel_code in (select vv.vessel_code from dss_vop_vessel_info vv where vv.vesman_code = '68280000' and nvl(vv.tetired_flag, '1') = '1' and nvl(vv.trade_type, '0') = '0')); 
 commit; 
 P_LOG_EXCEPTION(SQLCODE || SQLERRM || 'insert 结束时间:' || sysdate, 'SP_FEE_CSC_DAILY_JOB'); 
 commit; 
 END IF; 
 END SP_FEE_CSC_DAILY_JOB;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(
    p_dept_id integer,
    p_percent numeric,
    p_min_dept integer DEFAULT NULL,
    p_max_dept integer DEFAULT NULL,
    OUT p_result text
)
LANGUAGE plpgsql
AS $$
DECLARE
    v_stats record;
    v_result record;
    v_cursor refcursor;
    v_sql text;
    v_dept_name varchar(50);
    v_rows_updated integer := 0;
    v_total_budget numeric;
    v_count integer;
    dept_rec record;
BEGIN
    SELECT department_name INTO v_dept_name 
    FROM departments 
    WHERE department_id = p_dept_id;

    SELECT COUNT(*) AS total_emp, AVG(salary) AS avg_salary, MAX(salary) AS max_salary 
    INTO v_stats 
    FROM employees 
    WHERE department_id = p_dept_id;

    v_sql := 'SELECT employee_id, last_name, salary 
              FROM employees 
              WHERE department_id = $1 
              ORDER BY salary DESC 
              LIMIT 5';
    OPEN v_cursor FOR EXECUTE v_sql USING p_dept_id;

    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_stats.total_emp || ', "salary_stats": { "average": ' || v_stats.avg_salary || ', "max": ' || v_stats.max_salary || ' }, "top_earners": [';

    LOOP
        FETCH v_cursor INTO v_result;
        EXIT WHEN NOT FOUND;
        p_result := p_result || ' {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
    END LOOP;
    CLOSE v_cursor;

    p_result := regexp_replace(p_result, ',$', '') || ' ] }';

    FOR dept_rec IN 
        SELECT department_id, budget 
        FROM departments 
        WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
          AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
        UPDATE employees 
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100) 
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_count;

        SELECT SUM(salary) INTO v_total_budget 
        FROM employees 
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
    WHEN OTHERS THEN
        p_result := '{"error": "' || SQLERRM || '"}';
END;
$$;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id integer, p_adjustment_type varchar, p_adjustment_date timestamp DEFAULT now()) RETURNS void AS $$
DECLARE
    v_batch_size CONSTANT integer := 1000;
    v_total_processed integer := 0;
    v_error_count integer := 0;
    v_product_id integer;
    v_current_qty numeric;
    v_adjustment_qty numeric;
    v_product_ids integer[] := '{}';
    v_current_qtys numeric[] := '{}';
    v_adjustment_qtys numeric[] := '{}';
    v_count integer;
    c_inventory CURSOR FOR 
        SELECT product_id, quantity_on_hand, 
            CASE p_adjustment_type 
                WHEN 'RECOUNT' THEN physical_count - quantity_on_hand 
                WHEN 'DAMAGE' THEN -damaged_quantity 
                WHEN 'RETURN' THEN returned_quantity 
                ELSE 0 
            END AS adjustment_qty 
        FROM inventory_staging 
        WHERE warehouse_id = p_warehouse_id AND status = 'PENDING' 
        ORDER BY product_id;
BEGIN
    SAVEPOINT bulk_update_start;
    OPEN c_inventory;
    LOOP
        v_product_ids := '{}';
        v_current_qtys := '{}';
        v_adjustment_qtys := '{}';
        v_count := 0;
        FOR i IN 1..v_batch_size LOOP
            FETCH c_inventory INTO v_product_id, v_current_qty, v_adjustment_qty;
            EXIT WHEN NOT FOUND;
            v_product_ids := array_append(v_product_ids, v_product_id);
            v_current_qtys := array_append(v_current_qtys, v_current_qty);
            v_adjustment_qtys := array_append(v_adjustment_qtys, v_adjustment_qty);
            v_count := v_count + 1;
        END LOOP;
        EXIT WHEN v_count = 0;
        FOR i IN 1..v_count LOOP
            BEGIN
                UPDATE inventory 
                SET quantity_on_hand = quantity_on_hand + v_adjustment_qtys[i],
                    last_adjustment_date = p_adjustment_date,
                    last_adjustment_type = p_adjustment_type
                WHERE product_id = v_product_ids[i] AND warehouse_id = p_warehouse_id;
                IF NOT FOUND THEN
                    INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                    VALUES (v_product_ids[i], p_warehouse_id, GREATEST(0, v_adjustment_qtys[i]), p_adjustment_date, p_adjustment_type);
                END IF;
                INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
                VALUES (v_product_ids[i], p_warehouse_id, p_adjustment_date, p_adjustment_type, v_current_qtys[i], v_adjustment_qtys[i], v_current_qtys[i] + v_adjustment_qtys[i], current_user);
                v_total_processed := v_total_processed + 1;
            EXCEPTION WHEN OTHERS THEN
                v_error_count := v_error_count + 1;
                INSERT INTO error_log (error_date, error_message, product_id)
                VALUES (now(), 'Inventory update failed: unknown error', v_product_ids[i]);
            END;
        END LOOP;
    END LOOP;
    CLOSE c_inventory;
    UPDATE inventory_staging 
    SET status = 'PROCESSED', processed_date = now() 
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';
    COMMIT;
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION WHEN OTHERS THEN
    ROLLBACK TO SAVEPOINT bulk_update_start;
    RAISE EXCEPTION 'Bulk inventory update failed: unknown error';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_VOP_VESSEL_MANAGE_BK1(V_BUSIMAIN_CODE varchar, V_VES_CALIBRE varchar, V_VESSEL_CODE varchar, V_CALL_SIGN varchar, V_BUSI_MAINBODY varchar, V_BUSI_CALIBRE varchar, V_VESSEL_TYPE_CODE varchar, V_COMPANY_CALIBRE varchar, V_VESMAN_CODE varchar, V_MANAGER_CALIBRE varchar, V_TRADE_TYPE varchar, V_TETIRED_FLAG varchar, V_ACCMAN_CODE varchar, V_ACCOUNT_CALIBRE varchar, V_FEE_TYPE varchar, V_FEE_SUBJECT varchar, V_SAFEMAN_CODE varchar, V_SAFE_CALIBRE varchar, V_CORPORATE_CALIBRE varchar, V_TEST_CALIBRE varchar, V_COSTMAN_CODE varchar, V_COST_CALIBRE varchar) RETURNS refcursor AS $$
DECLARE
    ref refcursor;
BEGIN
    OPEN ref FOR SELECT * FROM( SELECT KK.ROW_NO, KK.VESSEL_CODE, /*船舶代码*/ KK.VESSEL_NAME, /*船舶名称（中文名）*/ KK.VESSEL_NAME_EN, /*船舶名称（英文名）*/ KK.ANOTHER_NAME, /*BMS标准船名 0*/ COALESCE(S1.OFFICE_NAME,KK.VESMAN_CODE) AS VES_MANAGER, /*船东*/ KK.SHIP_OWNER_FLAG_NAME, /*船舶产权性质 0*/ KK.VESSEL_TYPE, /*船型*/ KK.AREA_NAME, /*航区*/ KK.SHIPYARD, /*建造国家或地区 0*/ KK.VESSEL_NATIONALITY, /*船旗国(地区)*/ KK.TOTAL_LENGTH, /*总长*/ KK.WIDE, /*型宽*/ KK.TYPE_DEEP, /*型深*/ KK.BARE_SPACING_DRINKING, /*吃水 0*/ KK.SHIP_PORT, /*船籍港*/ KK.TONNAGE, /*总吨（吨）*/ KK.MUTETON, /*净吨（吨）*/ KK.CALCULATELIGHTTON, /*轻吨（吨） 0*/ KK.SPEED, /*航速(节)*/ KK.PANAMA_CANAL_TONNAGE, /*总载重量（吨） 0*/ KK.PANAMA_CANAL_NET_TON, /*净载重量（吨） 0*/ KK.HOST_POWER, /*功 率（千瓦）*/ KK.CONSTRUCTION_DATE, /*建造年月*/ KK.CREATE_YEAR, /*船龄(年)*/ KK.OPERATION_DATE, /*接入时间*/ KK.RETIRED_DATE, /*退租/役船时间*/ KK.CALL_SIGN, /*呼号*/ KK.IMO_NO /*IMO编号*/ FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MB.VESSEL_CODE) AS ROW_NO, MB.VESSEL_CODE, MB.VESSEL_NAME, MB.VESSEL_NAME_EN, MB.ANOTHER_NAME, FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'1') AS VESMAN_CODE, COALESCE(mb.ship_owner_flag,(SELECT c.display_value_cn FROM cdm_codedict c WHERE c.code_type = 'CDM_SHIP_WONER_FLAG' AND c.code_value = MB.ship_owner_flag LIMIT 1)) AS SHIP_OWNER_FLAG_NAME, MB.VESSEL_TYPE, COALESCE(MB.AREA,(SELECT C.DISPLAY_VALUE_CN FROM CDM_CODEDICT C WHERE C.CODE_TYPE = 'CDM_NAVIGATING_ZONE' AND C.CODE_VALUE = MB.AREA LIMIT 1)) AS AREA_NAME, MB.SHIPYARD, COALESCE(MB.VESSEL_NATIONALITY,(SELECT COUNTRY_NAME FROM CDM_COUNTRY WHERE COUNTRY_CODE= MB.VESSEL_NATIONALITY LIMIT 1)) AS VESSEL_NATIONALITY, MB.TOTAL_LENGTH, MB.WIDE, MB.TYPE_DEEP, MB.BARE_SPACING_DRINKING, COALESCE(MB.SHIP_PORT,(SELECT PORT_NAME from CDM_PORT WHERE PORT_CODE=MB.SHIP_PORT LIMIT 1)) AS SHIP_PORT, MB.TONNAGE, MB.MUTETON, MB.CALCULATELIGHTTON, MB.SPEED, MB.PANAMA_CANAL_TONNAGE, MB.PANAMA_CANAL_NET_TON, MB.HOST_POWER, MB.CONSTRUCTION_DATE, EXTRACT(YEAR FROM AGE(CURRENT_TIMESTAMP, MB.CONSTRUCTION_DATE))::int AS CREATE_YEAR, MB.OPERATION_DATE, MB.RETIRED_DATE, MB.CALL_SIGN, MB.IMO_NO FROM VOP_VESSEL MB LEFT JOIN (SELECT string_agg(V.TEST_CALIBRE::text, ',' ORDER BY V.VESSEL_ID) AS TEST_CALIBRE, string_agg(V.FEE_TYPE::text, ',' ORDER BY V.VESSEL_ID) AS FEE_TYPE, string_agg(V.FEE_SUBJECT::text, ',' ORDER BY V.VESSEL_ID) AS FEE_SUBJECT, string_agg(V.CORPORATE_CALIBRE::text, ',' ORDER BY V.VESSEL_ID) AS CORPORATE_CALIBRE, string_agg(V.COMPANY_CALIBRE::text, ',' ORDER BY V.VESSEL_ID) AS COMPANY_CALIBRE, V.VESSEL_ID FROM VOP_VESSEL_STATISTICS_CALIBRE V GROUP BY V.VESSEL_ID) VV ON MB.VESSEL_ID = VV.VESSEL_ID WHERE (V_BUSIMAIN_CODE IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'2') = V_BUSIMAIN_CODE) AND (V_VES_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'1') = V_VES_CALIBRE) AND (V_VESSEL_CODE IS NULL OR MB.VESSEL_CODE = V_VESSEL_CODE) AND (V_CALL_SIGN IS NULL OR MB.CALL_SIGN = V_CALL_SIGN) AND (V_BUSI_MAINBODY IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'2') = V_BUSI_MAINBODY) AND (V_BUSI_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'2') = V_BUSI_CALIBRE) AND (V_VESSEL_TYPE_CODE IS NULL OR MB.VESSEL_TYPE_CODE = VESSEL_TYPE_CODE) AND (V_COMPANY_CALIBRE IS NULL OR VV.COMPANY_CALIBRE = V_COMPANY_CALIBRE) AND (V_VESMAN_CODE IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'1') = V_VESMAN_CODE) AND (V_MANAGER_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'3') = V_MANAGER_CALIBRE) AND (V_TRADE_TYPE IS NULL OR MB.TRADE_TYPE = V_TRADE_TYPE) AND (V_TETIRED_FLAG IS NULL OR MB.TETIRED_FLAG = V_TETIRED_FLAG) AND (V_ACCMAN_CODE IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'4') = V_ACCMAN_CODE) AND (V_ACCOUNT_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'4') = V_ACCOUNT_CALIBRE) AND (V_FEE_TYPE IS NULL OR VV.FEE_TYPE = V_FEE_TYPE) AND (V_FEE_SUBJECT IS NULL OR VV.FEE_SUBJECT = V_FEE_SUBJECT) AND (V_SAFEMAN_CODE IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'5') = V_SAFEMAN_CODE) AND (V_SAFE_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'5') = V_SAFE_CALIBRE) AND (V_CORPORATE_CALIBRE IS NULL OR VV.CORPORATE_CALIBRE = V_CORPORATE_CALIBRE) AND (V_TEST_CALIBRE IS NULL OR VV.TEST_CALIBRE = V_TEST_CALIBRE) AND (V_COSTMAN_CODE IS NULL OR FN_GET_VESSEL_MANAGEMENT(MB.VESSEL_ID,CURRENT_TIMESTAMP,'6') = V_COSTMAN_CODE) AND (V_COST_CALIBRE IS NULL OR FN_GET_VESSEL_MANA_CALIBRE(MB.VESSEL_ID,CURRENT_TIMESTAMP,'6') = V_COST_CALIBRE) )KK LEFT JOIN SYS_OFFICE S1 ON S1.OFFICE_CODE = KK.VESMAN_CODE AND COALESCE(S1.IS_DELETE, '0') <> '1' )A;
    RETURN ref;
EXCEPTION WHEN NO_DATA_FOUND THEN NULL;
WHEN OTHERS THEN RAISE;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE RT_Tang_YW_MainC_InitAwb60Finish() AS $$
DECLARE
    period60 INT;
    period180 INT;
    yw_op_date TIMESTAMP;
    yw_start_time TIMESTAMP;
    awb_yw_date60 TIMESTAMP;
    awb_yw_date180 TIMESTAMP;
BEGIN
    SET TRANSACTION ISOLATION LEVEL READ COMMITTED;
    period60 := -60;
    period180 := -180;
    yw_start_time := CURRENT_TIMESTAMP;
    SELECT MAX(Yw_Op_Date) INTO yw_op_date FROM Tbl_Yw_Log WHERE OPTRESULT = 'RUN';
    IF yw_op_date IS NULL THEN
        yw_op_date := CURRENT_TIMESTAMP;
    END IF;
    awb_yw_date60 := yw_op_date + (period60 * INTERVAL '1 day');
    awb_yw_date180 := yw_op_date + (period180 * INTERVAL '1 day');
    BEGIN
        DELETE FROM Tbl_Awb_Basic_Temp 
        WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
            SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix 
            FROM Tbl_Cargo 
            WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix 
                FROM Tbl_Awb_Basic 
                WHERE Op_Date < TO_CHAR(awb_yw_date60, 'YYYY-MM-DD') 
                AND Op_Date > TO_CHAR(awb_yw_date180, 'YYYY-MM-DD')
            ) 
            AND FinishFlag != 0 
            AND Flight_Dep = Flight_Dest 
            GROUP BY AwbPrefix, AwbNo, AwbPostfix
        );
        INSERT INTO Tbl_Awb_Basic_Temp (AwbPrefix, AwbNo, AwbPostfix, YwTreate)
        SELECT DISTINCT AwbPrefix, AwbNo, AwbPostfix, 1 
        FROM (
            SELECT AwbPrefix, AwbNo, AwbPostfix, MIN(FinishFlag) AS TmpFinishFlag 
            FROM Tbl_Cargo 
            WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix 
                FROM Tbl_Awb_Basic 
                WHERE Op_Date < TO_CHAR(awb_yw_date60, 'YYYY-MM-DD') 
                AND Op_Date > TO_CHAR(awb_yw_date180, 'YYYY-MM-DD')
            ) 
            AND FinishFlag != 0 
            AND Flight_Dep = Flight_Dest 
            GROUP BY AwbPrefix, AwbNo, AwbPostfix
        ) Tbl_Temp 
        WHERE TmpFinishFlag > 0;
    EXCEPTION
        WHEN OTHERS THEN
            INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    BEGIN
        UPDATE Tbl_Awb_Basic_Temp t 
        SET AwbDate = a.Op_Date, 
            AwbDest = SUBSTRING(a.routing FROM LENGTH(a.routing) - 2 FOR 3), 
            Piece = a.Piece 
        FROM Tbl_Awb_Basic a 
        WHERE a.AwbNo = t.AwbNo 
        AND a.AwbPrefix = t.AwbPrefix 
        AND a.AwbPostfix = t.AwbPostfix;
    EXCEPTION
        WHEN OTHERS THEN
            INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    BEGIN
        UPDATE Tbl_Awb_Basic_Temp bt 
        SET YwTreate = 0 
        FROM (
            SELECT SUM(Piece) AS Piece, AwbPrefix, AwbNo, AwbPostfix 
            FROM (
                SELECT c.Piece, c.AwbPrefix, c.AwbNo, c.AwbPostfix 
                FROM Tbl_Awb_Basic_Temp t 
                JOIN Tbl_Cargo c 
                ON c.Flight_Dep = t.AwbDest 
                AND c.Flight_Dest = t.AwbDest 
                AND c.AwbNo = t.AwbNo 
                AND c.AwbPrefix = t.AwbPrefix 
                AND c.AwbPostfix = t.AwbPostfix
            ) b 
            GROUP BY AwbPrefix, AwbNo, AwbPostfix
        ) a 
        WHERE a.AwbNo = bt.AwbNo 
        AND a.AwbPrefix = bt.AwbPrefix 
        AND a.AwbPostfix = bt.AwbPostfix 
        AND bt.Piece = a.Piece;
    EXCEPTION
        WHEN OTHERS THEN
            INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
            RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;
    BEGIN
        DELETE FROM Tbl_Cargo_ChargeCorrection_Temp 
        WHERE CCANo IN (SELECT DISTINCT AwbNo FROM Tbl_Awb_Basic_Temp);
        INSERT INTO Tbl_Cargo_ChargeCorrection_Temp (CCAPrefix, CCANo, CCAPostfix)
        SELECT DISTINCT CCAPrefix, CCANo, CCAPostfix 
        FROM Tbl_Cargo_ChargeCorrectionDetail a 
        JOIN Tbl_Awb_Basic_Temp t 
        ON a.AwbPrefix = t.AwbPrefix 
