CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(p_WarehouseID INT, p_AdjustmentType NVARCHAR2(50), p_AdjustmentDate TIMESTAMP DEFAULT NULL) AS $$
DECLARE
    product_id INT;
    current_qty INT;
    adjustment_qty INT;
    new_qty INT;
    total_processed INT := 0;
    error_count INT := 0;
    error_message TEXT;
    cursor_opened BOOLEAN := false;
    inventory_cursor CURSOR FOR
        SELECT ProductID, QuantityOnHand,
               CASE p_AdjustmentType
                   WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                   WHEN 'DAMAGE' THEN -DamagedQuantity
                   WHEN 'RETURN' THEN ReturnedQuantity
                   ELSE 0
               END AS AdjustmentQty
        FROM InventoryStaging
        WHERE WarehouseID = p_WarehouseID AND Status = 'PENDING'
        ORDER BY ProductID;
BEGIN
    IF p_AdjustmentDate IS NULL THEN
        p_AdjustmentDate := NOW();
    END IF;
    BEGIN
        OPEN inventory_cursor;
        cursor_opened := true;
        LOOP
            FETCH inventory_cursor INTO product_id, current_qty, adjustment_qty;
            EXIT WHEN NOT FOUND;
            BEGIN
                new_qty := current_qty + adjustment_qty;
                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = product_id AND WarehouseID = p_WarehouseID) THEN
                    UPDATE Inventory
                    SET QuantityOnHand = new_qty,
                        LastAdjustmentDate = p_AdjustmentDate,
                        LastAdjustmentType = p_AdjustmentType
                    WHERE ProductID = product_id AND WarehouseID = p_WarehouseID;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (product_id, p_WarehouseID,
                            CASE WHEN new_qty < 0 THEN 0 ELSE new_qty END,
                            p_AdjustmentDate, p_AdjustmentType);
                END IF;
                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (product_id, p_WarehouseID, p_AdjustmentDate, p_AdjustmentType, current_qty, adjustment_qty, new_qty, CURRENT_USER);
                total_processed := total_processed + 1;
            EXCEPTION
                WHEN OTHERS THEN
                    error_count := error_count + 1;
                    INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                    VALUES (NOW(), 'Inventory update failed: ' || SQLERRM, product_id);
            END;
        END LOOP;
        CLOSE inventory_cursor;
        cursor_opened := false;
        UPDATE InventoryStaging
        SET Status = 'PROCESSED', ProcessedDate = NOW()
        WHERE WarehouseID = p_WarehouseID AND Status = 'PENDING';
        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', total_processed, error_count;
    EXCEPTION
        WHEN OTHERS THEN
            error_message := SQLERRM;
            IF cursor_opened THEN
                CLOSE inventory_cursor;
                cursor_opened := false;
            END IF;
            RAISE EXCEPTION 'Bulk inventory update failed: %', error_message;
    END;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(PayPeriodStart TIMESTAMP, PayPeriodEnd TIMESTAMP, DepartmentID INT DEFAULT NULL)
AS $$
DECLARE
    PayrollID INT;
    EmployeeID INT;
    HourlyRate DECIMAL(10,2);
    RegularHours DECIMAL(5,2);
    OvertimeHours DECIMAL(5,2);
    GrossPay DECIMAL(10,2);
    TaxDeduction DECIMAL(10,2);
    NetPay DECIMAL(10,2);
    TotalGross DECIMAL(15,2) := 0;
    TotalNet DECIMAL(15,2) := 0;
    cursor_open BOOLEAN := false;
    employee_cursor CURSOR FOR
        SELECT e.EmployeeID, e.HourlyRate, COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0), COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0)
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN PayPeriodStart AND PayPeriodEnd
        WHERE e.Status = 'ACTIVE' AND (DepartmentID IS NULL OR e.DepartmentID = DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID;
    err_msg TEXT;
BEGIN
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (PayPeriodStart, PayPeriodEnd, DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING')
    RETURNING PayrollID INTO PayrollID;
    OPEN employee_cursor;
    cursor_open := true;
    LOOP
        FETCH employee_cursor INTO EmployeeID, HourlyRate, RegularHours, OvertimeHours;
        EXIT WHEN NOT FOUND;
        GrossPay := (RegularHours * HourlyRate) + (OvertimeHours * HourlyRate * 1.5);
        TaxDeduction := GrossPay * 0.20;
        NetPay := GrossPay - TaxDeduction;
        INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay);
        TotalGross := TotalGross + GrossPay;
        TotalNet := TotalNet + NetPay;
    END LOOP;
    CLOSE employee_cursor;
    cursor_open := false;
    UPDATE PayrollHeader SET TotalGrossPay = TotalGross, TotalNetPay = TotalNet, Status = 'COMPLETED', CompletedDate = CURRENT_TIMESTAMP WHERE PayrollID = PayrollID;
    RAISE NOTICE 'Payroll processing completed. Payroll ID: %', PayrollID;
EXCEPTION
    WHEN OTHERS THEN
        err_msg := SQLERRM;
        IF cursor_open THEN
            CLOSE employee_cursor;
            cursor_open := false;
        END IF;
        UPDATE PayrollHeader SET Status = 'FAILED', ErrorMessage = err_msg WHERE PayrollID = PayrollID;
        RAISE EXCEPTION 'Payroll processing failed: %', err_msg;
END;
$$ LANGUAGE plpgsql;

