CREATE OR REPLACE PROCEDURE SP_DataMigration(source_table VARCHAR(128), target_table VARCHAR(128), batch_size INT DEFAULT 10000)
AS $$
DECLARE
    source_count INT := 0;
    target_count INT := 0;
    batch_count INT := 0;
    migration_id INT;
    error_message TEXT;
    sql_text TEXT;
    rows_affected INT;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (source_table, target_table, CURRENT_TIMESTAMP, 'RUNNING')
    RETURNING MigrationID INTO migration_id;

    EXECUTE format('SELECT COUNT(*) FROM %I', source_table) INTO source_count;
    UPDATE MigrationLog SET SourceCount = source_count WHERE MigrationID = migration_id;

    EXECUTE format('DELETE FROM %I', target_table);

    WHILE batch_count < source_count LOOP
        sql_text := format('INSERT INTO %I SELECT * FROM %I s WHERE NOT EXISTS (SELECT 1 FROM %I t WHERE t.ID = s.ID) LIMIT %s', 
                          target_table, source_table, target_table, batch_size);
        EXECUTE sql_text;
        GET DIAGNOSTICS rows_affected = ROW_COUNT;
        IF rows_affected = 0 THEN
            EXIT;
        END IF;
        batch_count := batch_count + rows_affected;
    END LOOP;

    EXECUTE format('SELECT COUNT(*) FROM %I', target_table) INTO target_count;
    UPDATE MigrationLog 
    SET EndTime = CURRENT_TIMESTAMP, 
        TargetCount = target_count, 
        Status = CASE WHEN source_count = target_count THEN 'SUCCESS' ELSE 'WARNING' END 
    WHERE MigrationID = migration_id;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', source_table, source_count, target_count;

EXCEPTION
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS error_message = MESSAGE_TEXT;
        UPDATE MigrationLog 
        SET EndTime = CURRENT_TIMESTAMP, 
            Status = 'FAILED', 
            ErrorMessage = error_message 
        WHERE MigrationID = migration_id;
        RAISE EXCEPTION 'Data migration failed: %', error_message;
END;
$$ LANGUAGE plpgsql;

