CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE) RETURNS void AS $$
DECLARE
  v_deleted_rows INTEGER;
BEGIN
  WITH deleted AS (
    DELETE FROM active_data
    WHERE record_date < p_archive_date
    RETURNING record_id, record_data
  )
  INSERT INTO archive_table (record_id, record_data)
  SELECT record_id, record_data FROM deleted;
  GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;
  INSERT INTO audit_log (log_message)
  VALUES (v_deleted_rows || ' rows archived.');
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE usp_UpdateOrderStatus(
    IN CustomerID INT,
    IN OldStatus VARCHAR(20),
    IN NewStatus VARCHAR(20),
    OUT RowsUpdated INT,
    OUT ErrorMessage VARCHAR(200)
)
AS $$
DECLARE
    OrderCount INT;
    TranCount INT := 0;
    ProcessingFee DECIMAL(10,2);
    fee_cursor REFCURSOR;
BEGIN
    IF OldStatus = NewStatus THEN
        ErrorMessage := '新旧状态不能相同';
        RETURN 400;
    END IF;
    TranCount := 0;
    BEGIN
        IF TranCount = 0 THEN
            START TRANSACTION;
        END IF;
        UPDATE Orders SET Status = NewStatus, LastUpdated = CURRENT_TIMESTAMP WHERE CustomerID = CustomerID AND Status = OldStatus AND OrderDate > CURRENT_DATE - INTERVAL '3 months';
        GET DIAGNOSTICS RowsUpdated = ROW_COUNT;
        IF RowsUpdated = 0 THEN
            ErrorMessage := '未找到符合条件的订单';
            RETURN 404;
        END IF;
        INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate) SELECT OrderID, OldStatus, NewStatus, CURRENT_TIMESTAMP FROM Orders WHERE CustomerID = CustomerID AND Status = NewStatus;
        ProcessingFee := 0;
        OPEN fee_cursor FOR SELECT Fee FROM OrderDetails WHERE OrderID IN (SELECT OrderID FROM Orders WHERE CustomerID = CustomerID AND Status = NewStatus);
        LOOP
            FETCH fee_cursor INTO ProcessingFee;
            EXIT WHEN NOT FOUND;
            ProcessingFee := ProcessingFee * 0.95;
        END LOOP;
        CLOSE fee_cursor;
        IF TranCount = 0 THEN
            COMMIT;
        END IF;
        ErrorMessage := '更新成功';
        RETURN 200;
    EXCEPTION
        WHEN OTHERS THEN
            IF TranCount = 0 AND (SELECT COUNT(*) FROM pg_stat_activity WHERE state = 'active') > 0 THEN
                ROLLBACK;
            END IF;
            ErrorMessage := SQLERRM || ' (Line:0)';
            RETURN 500;
    END;
END;
$$ LANGUAGE plpgsql;

