CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id NUMERIC, p_adjustment_type VARCHAR, p_adjustment_date DATE DEFAULT CURRENT_DATE) RETURNS VOID AS $$
DECLARE
  v_inventory RECORD;
  v_batch_size CONSTANT NUMERIC := 1000;
  v_total_processed NUMERIC := 0;
  v_error_count NUMERIC := 0;
  c_inventory CURSOR FOR SELECT product_id, quantity_on_hand, CASE p_adjustment_type WHEN 'RECOUNT' THEN physical_count - quantity_on_hand WHEN 'DAMAGE' THEN -damaged_quantity WHEN 'RETURN' THEN returned_quantity ELSE 0 END AS adjustment_qty FROM inventory_staging WHERE warehouse_id = p_warehouse_id AND status = 'PENDING' ORDER BY product_id;
BEGIN
  SAVEPOINT bulk_update_start;
  FOR v_inventory IN c_inventory LOOP
    BEGIN
      UPDATE inventory SET quantity_on_hand = quantity_on_hand + v_inventory.adjustment_qty, last_adjustment_date = p_adjustment_date, last_adjustment_type = p_adjustment_type WHERE product_id = v_inventory.product_id AND warehouse_id = p_warehouse_id;
      IF NOT FOUND THEN
        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type) VALUES (v_inventory.product_id, p_warehouse_id, GREATEST(0, v_inventory.adjustment_qty), p_adjustment_date, p_adjustment_type);
      END IF;
      INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by) VALUES (v_inventory.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_inventory.quantity_on_hand, v_inventory.adjustment_qty, v_inventory.quantity_on_hand + v_inventory.adjustment_qty, CURRENT_USER);
      v_total_processed := v_total_processed + 1;
    EXCEPTION WHEN OTHERS THEN
      v_error_count := v_error_count + 1;
      INSERT INTO error_log (error_date, error_message, product_id) VALUES (CURRENT_DATE, 'Inventory update failed: ' || SQLERRM, v_inventory.product_id);
    END;
  END LOOP;
  UPDATE inventory_staging SET status = 'PROCESSED', processed_date = CURRENT_DATE WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';
  COMMIT;
  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION WHEN OTHERS THEN
  ROLLBACK TO SAVEPOINT bulk_update_start;
  RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id NUMERIC, p_adjustment_type VARCHAR, p_adjustment_date DATE DEFAULT CURRENT_DATE) RETURNS VOID AS $$
DECLARE
  v_inventory RECORD;
  v_batch_size CONSTANT NUMERIC := 1000;
  v_total_processed NUMERIC := 0;
  v_error_count NUMERIC := 0;
  c_inventory CURSOR FOR
    SELECT product_id, quantity_on_hand,
           CASE p_adjustment_type
             WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
             WHEN 'DAMAGE' THEN -damaged_quantity
             WHEN 'RETURN' THEN returned_quantity
             ELSE 0
           END AS adjustment_qty
    FROM inventory_staging
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
    ORDER BY product_id;
BEGIN
  FOR v_inventory IN c_inventory LOOP
    BEGIN
      IF EXISTS (SELECT 1 FROM inventory WHERE product_id = v_inventory.product_id AND warehouse_id = p_warehouse_id) THEN
        UPDATE inventory
        SET quantity_on_hand = quantity_on_hand + v_inventory.adjustment_qty,
            last_adjustment_date = p_adjustment_date,
            last_adjustment_type = p_adjustment_type
        WHERE product_id = v_inventory.product_id AND warehouse_id = p_warehouse_id;
      ELSE
        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
        VALUES (v_inventory.product_id, p_warehouse_id, GREATEST(0, v_inventory.adjustment_qty), p_adjustment_date, p_adjustment_type);
      END IF;
      INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
      VALUES (v_inventory.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_inventory.quantity_on_hand, v_inventory.adjustment_qty, v_inventory.quantity_on_hand + v_inventory.adjustment_qty, CURRENT_USER);
      v_total_processed := v_total_processed + 1;
    EXCEPTION WHEN OTHERS THEN
      v_error_count := v_error_count + 1;
      INSERT INTO error_log (error_date, error_message, product_id)
      VALUES (CURRENT_DATE, 'Inventory update failed: ' || SQLERRM, v_inventory.product_id);
    END;
  END LOOP;
  UPDATE inventory_staging
  SET status = 'PROCESSED', processed_date = CURRENT_DATE
  WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';
  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION WHEN OTHERS THEN
  RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION SP_GENERATE_SALES_REPORT(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS VOID AS $$
DECLARE
  v_report_id INTEGER;
  v_total_sales NUMERIC := 0;
  v_total_commission NUMERIC := 0;
  v_report_sql TEXT;
  v_sales_data RECORD;
  v_start_date DATE;
  v_end_date DATE;
BEGIN
  v_start_date := TO_DATE(p_year || '-' || LPAD(((p_quarter - 1) * 3 + 1)::TEXT, 2, '0') || '-01', 'YYYY-MM-DD');
  v_end_date := (DATE_TRUNC('MONTH', v_start_date) + INTERVAL '3 months' - INTERVAL '1 day')::DATE;
  v_report_id := NEXTVAL('sales_report_seq');
  v_report_sql := '
    SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount
    FROM sales_representatives sr
    LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2
    WHERE ($3 IS NULL OR sr.sales_rep_id = $3)
    GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
    ORDER BY total_sales DESC';
  FOR v_sales_data IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
    INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, v_sales_data.sales_rep_id, v_sales_data.sales_rep_name, v_sales_data.total_sales, v_sales_data.commission_rate, v_sales_data.commission_amount);
    v_total_sales := v_total_sales + v_sales_data.total_sales;
