    FROM CARGO2.TBL_Flight
    WHERE flightdate >= to_timestamp(sFdate2, 'YYYY-MM-DD HH24:MI:SS')
      AND flightdate <= to_timestamp(sFdate1, 'YYYY-MM-DD HH24:MI:SS');

    CALL TangForUllageArrCapture();

    DELETE FROM CARGO2.tbl_YW_ullage
    WHERE FlightDate >= to_timestamp(sFdate2, 'YYYY-MM-DD HH24:MI:SS')
      AND FlightDate <= to_timestamp(sFdate1, 'YYYY-MM-DD HH24:MI:SS');

    INSERT INTO CARGO2.tbl_YW_ullage
    (OLDWEIGHT,OLDVOLUME,
     FlightNo,FlightDate,Flight_Dep,Flight_Dest,RESER,AWBCITY,Goods,
     OP_ID,
     Book_Id,AwbPrefix,AwbNo,AwbPostFix,OP_DATE,
     OLDBOOKWEIGHT,OLDBOOKVOLUME,NEWBOOKWEIGHT,
     NEWBOOKVOLUME,NEWWEIGHT,NEWVOLUME)
    SELECT OLDWEIGHT,OLDVOLUME,
           FlightNo,FlightDate,Flight_Dep,Flight_Dest,RESER,AWBCITY,Goods,
           OP_ID,
           Book_Id,AwbPrefix,AwbNo,AwbPostFix,OP_DATE,
           OLDBOOKWEIGHT,OLDBOOKVOLUME,NEWBOOKWEIGHT,
           NEWBOOKVOLUME,NEWWEIGHT,NEWVOLUME
    FROM CARGO2.tbl_ullage
    WHERE flightdate >= to_timestamp(sFdate2, 'YYYY-MM-DD HH24:MI:SS')
      AND flightdate <= to_timestamp(sFdate1, 'YYYY-MM-DD HH24:MI:SS');

    DELETE FROM CARGO2.TBL_YW_ULLAGE_ARR
    WHERE FlightDate >= to_timestamp(sFdate2, 'YYYY-MM-DD HH24:MI:SS')
      AND FlightDate <= to_timestamp(sFdate1, 'YYYY-MM-DD HH24:MI:SS');

    UPDATE TBL_YW_LOG SET ENDTIME=now(), OPTResult='REPORTSUC'
    WHERE LOG_ID = iden;

EXCEPTION
    WHEN OTHERS THEN
        UPDATE TBL_YW_LOG SET ENDTIME=now(), OPTResult='REPORTERR'
        WHERE LOG_ID = iden;
END;
/

CREATE OR REPLACE PROCEDURE SP_DataMigration(IN SourceTable VARCHAR(128), IN TargetTable VARCHAR(128), IN BatchSize INT DEFAULT 10000) AS
DECLARE
  SQL TEXT;
  SourceCount INT;
  TargetCount INT;
  BatchCount INT := 0;
  MigrationID INT;
  ErrorMessage VARCHAR(4000);
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, NOW(), 'RUNNING');
  MigrationID := CURRVAL('MigrationLog_MigrationID_seq');
  SQL := 'SELECT COUNT(*) FROM ' || quote_ident(SourceTable);
  EXECUTE IMMEDIATE SQL INTO SourceCount;
  UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = MigrationID;
  SQL := 'DELETE FROM ' || quote_ident(TargetTable);
  EXECUTE IMMEDIATE SQL;
  WHILE BatchCount < SourceCount LOOP
    SQL := 'INSERT INTO ' || quote_ident(TargetTable) || ' SELECT * FROM ' || quote_ident(SourceTable) || ' WHERE ID NOT IN (SELECT ID FROM ' || quote_ident(TargetTable) || ') LIMIT ' || BatchSize;
    EXECUTE IMMEDIATE SQL;
    IF SQL%ROWCOUNT = 0 THEN EXIT; END IF;
    BatchCount := BatchCount + SQL%ROWCOUNT;
  END LOOP;
  SQL := 'SELECT COUNT(*) FROM ' || quote_ident(TargetTable);
  EXECUTE IMMEDIATE SQL INTO TargetCount;
  UPDATE MigrationLog SET EndTime = NOW(), TargetCount = TargetCount, Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = MigrationID;
  RAISE NOTICE 'Migration completed for %. Source: %, Target: %', SourceTable, SourceCount, TargetCount;
EXCEPTION
  WHEN OTHERS THEN
    ErrorMessage := SQLERRM;
    UPDATE MigrationLog SET EndTime = NOW(), Status = 'FAILED', ErrorMessage = ErrorMessage WHERE MigrationID = MigrationID;
    RAISE EXCEPTION 'Data migration failed: %', ErrorMessage;
END;
/

CREATE OR REPLACE PROCEDURE SP_DataMigration(SourceTable IN NVARCHAR2, TargetTable IN NVARCHAR2, BatchSize IN INT DEFAULT 10000) AS
  SQL_STR VARCHAR2(4000);
  SourceCount INT;
  TargetCount INT;
  BatchCount INT := 0;
  MigrationID INT;
  ErrorMessage VARCHAR2(4000);
BEGIN
  INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status) VALUES (SourceTable, TargetTable, SYSDATE, 'RUNNING');
  SELECT MAX(MigrationID) INTO MigrationID FROM MigrationLog WHERE SourceTable = SP_DataMigration.SourceTable AND TargetTable = SP_DataMigration.TargetTable AND ROWNUM = 1;
  SQL_STR := 'SELECT COUNT(*) FROM ' || SourceTable;
  EXECUTE IMMEDIATE SQL_STR INTO SourceCount;
  UPDATE MigrationLog SET SourceCount = SourceCount WHERE MigrationID = SP_DataMigration.MigrationID;
  SQL_STR := 'DELETE FROM ' || TargetTable;
  EXECUTE IMMEDIATE SQL_STR;
  WHILE BatchCount < SourceCount LOOP
    SQL_STR := 'INSERT INTO ' || TargetTable || ' SELECT * FROM (SELECT * FROM ' || SourceTable || ' WHERE NOT EXISTS (SELECT 1 FROM ' || TargetTable || ' t WHERE t.ID = ' || SourceTable || '.ID)) WHERE ROWNUM <= ' || BatchSize;
    EXECUTE IMMEDIATE SQL_STR;
    IF SQL%ROWCOUNT = 0 THEN
      EXIT;
    END IF;
    BatchCount := BatchCount + SQL%ROWCOUNT;
  END LOOP;
  SQL_STR := 'SELECT COUNT(*) FROM ' || TargetTable;
  EXECUTE IMMEDIATE SQL_STR INTO TargetCount;
  UPDATE MigrationLog SET EndTime = SYSDATE, TargetCount = TargetCount, Status = CASE WHEN SourceCount = TargetCount THEN 'SUCCESS' ELSE 'WARNING' END WHERE MigrationID = SP_DataMigration.MigrationID;
  DBMS_OUTPUT.PUT_LINE('Migration completed for ' || SourceTable || '. Source: ' || SourceCount || ', Target: ' || TargetCount);
EXCEPTION
  WHEN OTHERS THEN
    ErrorMessage := SQLERRM;
    UPDATE MigrationLog SET EndTime = SYSDATE, Status = 'FAILED', ErrorMessage = ErrorMessage WHERE MigrationID = SP_DataMigration.MigrationID;
    RAISE_APPLICATION_ERROR(-20001, 'Data migration failed: ' || ErrorMessage);
END;

