CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL) AS
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName VARCHAR(100);
    CURSOR product_cursor IS
        SELECT ProductID, ProductName
        FROM Products
        WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := CURRENT_TIMESTAMP;
    END IF;

    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO ProductCount
    FROM Products
    WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';

    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;

    FOR rec IN product_cursor LOOP
        ProductID := rec.ProductID;
        ProductName := rec.ProductName;
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDate, CURRENT_USER);

            UPDATE Products
            SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDate, LastModifiedBy = CURRENT_USER
            WHERE ProductID = ProductID;

            UpdatedCount := UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;

    UPDATE ProductCategories
    SET LastUpdateDate = EffectiveDate
    WHERE CategoryID IN (OldCategoryID, NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories (OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL) AS 
$$
DECLARE
    ProductCount INT;
    UpdatedCount INT := 0;
    ProductID INT;
    ProductName VARCHAR(100);
    rec RECORD;
BEGIN
    IF EffectiveDate IS NULL THEN
        EffectiveDate := now();
    END IF;
    IF OldCategoryID = NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;
    SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
    IF ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', OldCategoryID;
        RETURN;
    END IF;
    FOR rec IN SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE'
    LOOP
        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (rec.ProductID, OldCategoryID, NewCategoryID, EffectiveDate, current_user);
            UPDATE Products SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDate, LastModifiedBy = current_user
            WHERE ProductID = rec.ProductID;
            UpdatedCount := UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (rec.ProductID, SQLERRM, now());
        END;
    END LOOP;
    UPDATE ProductCategories SET LastUpdateDate = EffectiveDate WHERE CategoryID IN (OldCategoryID, NewCategoryID);
    RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(OldCategoryID INT, NewCategoryID INT, EffectiveDate TIMESTAMP DEFAULT NULL) AS
DECLARE
ProductCount INT;
UpdatedCount INT := 0;
ProductID INT;
ProductName VARCHAR(100);
CURSOR product_cursor IS
SELECT ProductID, ProductName FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
BEGIN
IF EffectiveDate IS NULL THEN
EffectiveDate := NOW();
END IF;
IF OldCategoryID = NewCategoryID THEN
RAISE EXCEPTION 'Old and new category IDs cannot be the same';
END IF;
SELECT COUNT(*) INTO ProductCount FROM Products WHERE CategoryID = OldCategoryID AND Status = 'ACTIVE';
IF ProductCount = 0 THEN
RAISE NOTICE 'No active products found in category %', OldCategoryID;
RETURN;
END IF;
OPEN product_cursor;
LOOP
FETCH product_cursor INTO ProductID, ProductName;
EXIT WHEN product_cursor%NOTFOUND;
BEGIN
INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
VALUES (ProductID, OldCategoryID, NewCategoryID, EffectiveDate, CURRENT_USER);
UPDATE Products SET CategoryID = NewCategoryID, LastModifiedDate = EffectiveDate, LastModifiedBy = CURRENT_USER
WHERE ProductID = ProductID;
UpdatedCount := UpdatedCount + 1;
EXCEPTION
WHEN OTHERS THEN
INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
VALUES (ProductID, SQLERRM, NOW());
END;
END LOOP;
CLOSE product_cursor;
UPDATE ProductCategories SET LastUpdateDate = EffectiveDate WHERE CategoryID IN (OldCategoryID, NewCategoryID);
RAISE NOTICE 'Category update completed. % products updated out of % total.', UpdatedCount, ProductCount;
EXCEPTION
WHEN OTHERS THEN
IF product_cursor%ISOPEN THEN
CLOSE product_cursor;
END IF;
RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
/

CREATE OR REPLACE PROCEDURE p_job_create_bond_info_all AS
BEGIN
  TRUNCATE TABLE tmp_bond_info_all_20010;
  INSERT INTO tmp_bond_info_all_20010(f_val,def_id,id)
    SELECT field_value,FIELD_DEFINITION_ID,t2.ID
    FROM T_OBJECT_FIELD_DATA t1,T_PJ_Bond_Info t2
    WHERE t1.OBJECT_NO = 20010 AND t1.FIELD_VALUE IS NOT NULL AND t1.parent_id=t2.APPLY_ID;

  TRUNCATE TABLE tmp_bond_info_all_20020;
  INSERT INTO tmp_bond_info_all_20020(f_val,def_id,id)
    SELECT field_value,FIELD_DEFINITION_ID,t2.id AS bondId
    FROM T_OBJECT_FIELD_DATA t1,T_PJ_Bond_Info t2
    WHERE t1.OBJECT_NO = 20020 AND t1.FIELD_VALUE IS NOT NULL AND t1.PARENT_ID=t2.TEMP_BONDID;

  TRUNCATE TABLE t_pj_bond_info_all;
  INSERT INTO t_pj_bond_info_all(id,temp_bondid,apply_id)
    SELECT t1.ID,t1.TEMP_BONDID,t1.APPLY_ID
    FROM T_PJ_Bond_Info t1
    WHERE 1=1;

  FOR colrec IN
    SELECT attname AS name
    FROM pg_attribute
    WHERE attrelid = (SELECT oid FROM pg_class WHERE relname='t_pj_bond_info_all')
      AND attname LIKE 'def%'
  LOOP
    EXECUTE IMMEDIATE 'UPDATE t_pj_bond_info_all a SET '||colrec.name||'=b.f_val FROM tmp_bond_info_all_20020 b WHERE a.id=b.id AND b.def_id='||replace(colrec.name,'def_','');
    EXECUTE IMMEDIATE 'UPDATE t_pj_bond_info_all a SET '||colrec.name||'=b.f_val FROM tmp_bond_info_all_20010 b WHERE a.id=b.id AND a.'||colrec.name||' IS NULL AND b.def_id='||replace(colrec.name,'def_','');
  END LOOP;
END;
/

CREATE OR REPLACE FUNCTION GETEMPLOYEESTATS(p_dept_id INTEGER, OUT p_result TEXT) AS $$
DECLARE
  v_dept_name VARCHAR(50);
  v_total_emp INTEGER;
  v_avg_salary NUMERIC;
  v_max_salary NUMERIC;
  v_active_projects INTEGER;
  v_rec RECORD;
  v_first BOOLEAN := true;
BEGIN
  SELECT department_name INTO v_dept_name FROM DEPARTMENTS WHERE department_id = p_dept_id;
  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM EMPLOYEES WHERE department_id = p_dept_id;
  SELECT COUNT(*) INTO v_active_projects FROM PROJECTS WHERE department_id = p_dept_id AND status = 'ACTIVE';
  p_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || v_avg_salary || ',    "max": ' || v_max_salary || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';
  FOR v_rec IN SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5 LOOP
    IF NOT v_first THEN
      p_result := p_result || ',';
    END IF;
    p_result := p_result || '    {"id": ' || v_rec.employee_id || ', "name": "' || v_rec.last_name || '", "salary": ' || v_rec.salary || '}';
    v_first := false;
  END LOOP;
  p_result := p_result || '  ]}';
EXCEPTION
  WHEN NO_DATA_FOUND THEN
    p_result := '{"error": "Department not found"}';
  WHEN OTHERS THEN
    p_result := '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION usp_UpdateOrderStatus(CustomerID INT, OldStatus VARCHAR(20), NewStatus VARCHAR(20), OUT RowsUpdated INT, OUT ErrorMessage VARCHAR(200), OUT ReturnCode INT) RETURNS RECORD AS
$$
DECLARE
    OrderCount INT;
    TranCount INT;
BEGIN
    TranCount := txid_current()::INT;
    SET NOCOUNT ON;
    IF OldStatus = NewStatus THEN
        ErrorMessage := '新旧状态不能相同';
        ReturnCode := 400;
        RETURN;
    END IF;
    IF TranCount = 0 THEN
        START TRANSACTION;
    END IF;
    BEGIN
        UPDATE Orders SET Status = NewStatus, LastUpdated = NOW() WHERE CustomerID = CustomerID AND Status = OldStatus AND OrderDate > NOW() - INTERVAL '3 months';
        RowsUpdated := SQL%ROWCOUNT;
        IF RowsUpdated = 0 THEN
            ErrorMessage := '未找到符合条件的订单';
            ReturnCode := 404;
            RETURN;
        END IF;
        INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate) SELECT OrderID, OldStatus, NewStatus, NOW() FROM Orders WHERE CustomerID = CustomerID AND Status = NewStatus;
        FOR ProcessingFee IN (SELECT Fee FROM OrderDetails WHERE OrderID IN (SELECT OrderID FROM Orders WHERE CustomerID = CustomerID AND Status = NewStatus)) LOOP
            ProcessingFee := ProcessingFee * 0.95;
        END LOOP;
        IF TranCount = 0 THEN
            COMMIT;
        END IF;
        ErrorMessage := '更新成功';
        ReturnCode := 200;
    EXCEPTION
        WHEN OTHERS THEN
            IF TranCount = 0 THEN
                ROLLBACK;
            END IF;
            ErrorMessage := SQLERRM || ' (Line:' || PG_EXCEPTION_LINE_NUMBER || ')';
            ReturnCode := 500;
    END;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION trg_audit_emp_sal_fn() RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO emp_audit(emp_id, old_sal, new_sal) VALUES (OLD.empno, OLD.sal, NEW.sal);
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trg_audit_emp_sal BEFORE UPDATE OF sal ON emp FOR EACH ROW EXECUTE PROCEDURE trg_audit_emp_sal_fn();

DO $$  
DECLARE  
    v_cur REFCURSOR;  
BEGIN  
    OPEN v_cur FOR SELECT * FROM emp;  
END $$;

