CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id INTEGER)
RETURNS TEXT AS $$
DECLARE
    v_dept_name VARCHAR(50);
    v_total_emp INTEGER;
    v_avg_salary NUMERIC;
    v_max_salary NUMERIC;
    v_active_projects INTEGER;
    v_result RECORD;
    v_cursor REFCURSOR;
    v_json TEXT;
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    IF NOT FOUND THEN
        RETURN '{"error": "Department not found"}';
    END IF;

    SELECT COUNT(*), AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees WHERE department_id = p_dept_id;

    SELECT COUNT(*) INTO v_active_projects
    FROM projects WHERE department_id = p_dept_id AND status = 'ACTIVE';

    OPEN v_cursor FOR
        SELECT employee_id, last_name, salary
        FROM employees
        WHERE department_id = p_dept_id
        ORDER BY salary DESC
        LIMIT 5;

    v_json := '{  "department": "' || v_dept_name || '",  "total_employees": ' || COALESCE(v_total_emp::TEXT, '0') || ',  "salary_stats": {    "average": ' || COALESCE(v_avg_salary::TEXT, '0') || ',    "max": ' || COALESCE(v_max_salary::TEXT, '0') || '  },  "active_projects": ' || COALESCE(v_active_projects::TEXT, '0') || ',  "top_earners": [';

    LOOP
        FETCH v_cursor INTO v_result;
        EXIT WHEN NOT FOUND;
        v_json := v_json || '{"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
    END LOOP;

    IF v_json LIKE '%, "top_earners": [%' THEN
        v_json := left(v_json, -2) || ']';
    ELSE
        v_json := v_json || ']';
    END IF;

    v_json := v_json || '}}';

    CLOSE v_cursor;

    RETURN v_json;

EXCEPTION WHEN OTHERS THEN
    RETURN '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE PROCEDURE usp_UpdateOrderStatus (IN customerid INT, IN oldstatus VARCHAR(20), IN newstatus VARCHAR(20), OUT rowsupdated INT, OUT errormessage VARCHAR(200))
AS
$$
DECLARE
    ordercount INT;
    trancount INT := 0;
    processingfee DECIMAL(10,2) := 0;
    fee_cursor CURSOR FOR SELECT Fee FROM OrderDetails WHERE OrderID IN (SELECT OrderID FROM Orders WHERE CustomerID = customerid AND Status = newstatus);
BEGIN
    SET NOCOUNT ON;

    IF oldstatus = newstatus THEN
        errormessage := '新旧状态不能相同';
        RETURN 400;
    END IF;

    BEGIN TRY
        UPDATE Orders SET Status = newstatus, LastUpdated = CURRENT_TIMESTAMP WHERE CustomerID = customerid AND Status = oldstatus AND OrderDate > (CURRENT_TIMESTAMP - INTERVAL '3 month');
        GET DIAGNOSTICS rowsupdated = ROW_COUNT;

        IF rowsupdated = 0 THEN
            errormessage := '未找到符合条件的订单';
            RETURN 404;
        END IF;

        INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
        SELECT OrderID, oldstatus, newstatus, CURRENT_TIMESTAMP
        FROM Orders
        WHERE CustomerID = customerid AND Status = newstatus;

        OPEN fee_cursor;
        LOOP
            FETCH fee_cursor INTO processingfee;
            EXIT WHEN NOT FOUND;
            processingfee := processingfee * 0.95;
        END LOOP;
        CLOSE fee_cursor;

        errormessage := '更新成功';
        RETURN 200;

    EXCEPTION
        WHEN OTHERS THEN
            IF trancount = 0 AND CURRENT_TRANSACTION > 0 THEN
                ROLLBACK;
            END IF;
            errormessage := SQLERRM || ' (Line:' || SQLSTATE || ')';
            RETURN 500;
    END TRY;
END;
$$ LANGUAGE plpgsql;

