CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id integer, p_percent numeric, p_min_dept integer DEFAULT NULL, p_max_dept integer DEFAULT NULL) RETURNS text AS $$
DECLARE
    v_stats RECORD;
    v_result RECORD;
    v_dept_name varchar(50);
    v_rows_updated integer := 0;
    v_total_budget numeric;
    v_row_count integer;
    v_error_message text;
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_stats.total_emp, v_stats.avg_salary, v_stats.max_salary FROM employees WHERE department_id = p_dept_id;
    p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_stats.total_emp || ', "salary_stats": { "average": ' || v_stats.avg_salary || ', "max": ' || v_stats.max_salary || ' }, "top_earners": [';
    FOR v_result IN SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5 LOOP
        p_result := p_result || ' {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
    END LOOP;
    p_result := rtrim(p_result, ',') || ' ] }';
    FOR dept_rec IN SELECT department_id, budget FROM departments WHERE (department_id >= p_min_dept OR p_min_dept IS NULL) AND (department_id <= p_max_dept OR p_max_dept IS NULL) LOOP
        UPDATE employees SET salary = COALESCE(salary, 3000) * (1 + p_percent/100) WHERE department_id = dept_rec.department_id;
        GET DIAGNOSTICS v_row_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_row_count;
        SELECT SUM(salary) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;
        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;
    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, CURRENT_TIMESTAMP;
    RETURN p_result;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        p_result := '{"error": "Department not found"}';
        RETURN p_result;
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS v_error_message = MESSAGE_TEXT;
        p_result := '{"error": "' || v_error_message || '"}';
        RETURN p_result;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id integer, p_percent numeric, p_min_dept integer DEFAULT NULL, p_max_dept integer DEFAULT NULL) RETURNS text AS $$
DECLARE
    v_dept_name departments.department_name%TYPE;
    v_stats_total_emp integer;
    v_stats_avg_salary numeric;
    v_stats_max_salary numeric;
    v_rows_updated integer := 0;
    v_total_budget numeric;
    v_row_count integer;
    result_text text;
BEGIN
    SELECT department_name INTO v_dept_name 
    FROM departments 
    WHERE department_id = p_dept_id;

    SELECT COUNT(*), AVG(salary), MAX(salary) 
    INTO v_stats_total_emp, v_stats_avg_salary, v_stats_max_salary
    FROM employees 
    WHERE department_id = p_dept_id;

    result_text := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_stats_total_emp || ', "salary_stats": { "average": ' || v_stats_avg_salary || ', "max": ' || v_stats_max_salary || ' }, "top_earners": [';

    FOR rec IN 
        SELECT employee_id, last_name, salary 
        FROM employees 
        WHERE department_id = p_dept_id 
        ORDER BY salary DESC 
        LIMIT 5
    LOOP
        result_text := result_text || ' {"id": ' || rec.employee_id || ', "name": "' || rec.last_name || '", "salary": ' || rec.salary || '},';
    END LOOP;

    result_text := RTRIM(result_text, ',') || ' ] }';

    FOR dept_rec IN 
        SELECT department_id, budget 
        FROM departments 
        WHERE (p_min_dept IS NULL OR department_id >= p_min_dept)
          AND (p_max_dept IS NULL OR department_id <= p_max_dept)
    LOOP
        UPDATE employees 
        SET salary = COALESCE(salary, 3000) * (1 + p_percent/100.0)
        WHERE department_id = dept_rec.department_id;

        GET DIAGNOSTICS v_row_count = ROW_COUNT;
        v_rows_updated := v_rows_updated + v_row_count;

        SELECT SUM(salary) INTO v_total_budget
        FROM employees
        WHERE department_id = dept_rec.department_id;

        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, CURRENT_TIMESTAMP;

    RETURN result_text;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        result_text := '{"error": "Department not found"}';
        RETURN result_text;
    WHEN OTHERS THEN
        result_text := '{"error": "' || SQLERRM || '"}';
        RETURN result_text;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id integer, p_percent numeric, p_min_dept integer DEFAULT NULL, p_max_dept integer DEFAULT NULL) RETURNS text AS $$
DECLARE
    v_dept_name departments.department_name%TYPE;
    v_stats record;
    v_result record;
    v_cursor refcursor;
    v_sql text;
    v_rows_updated integer := 0;
    v_total_budget numeric;
    dept_rec record;
    v_result_text text;
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    SELECT count(*), avg(salary), max(salary) INTO v_stats FROM employees WHERE department_id = p_dept_id;
    v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';
    OPEN v_cursor FOR EXECUTE v_sql USING p_dept_id;
    v_result_text := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_stats.count || ', "salary_stats": { "average": ' || v_stats.avg || ', "max": ' || v_stats.max || ' }, "top_earners": [';
    LOOP
        FETCH v_cursor INTO v_result;
        EXIT WHEN NOT FOUND;
        v_result_text := v_result_text || ' {"id": ' || v_result.employee_id || ', "name": "' || v_result.last_name || '", "salary": ' || v_result.salary || '},';
    END LOOP;
    CLOSE v_cursor;
    v_result_text := rtrim(v_result_text, ',') || ' ] }';
    FOR dept_rec IN SELECT department_id, budget FROM departments WHERE (department_id >= p_min_dept OR p_min_dept IS NULL) AND (department_id <= p_max_dept OR p_max_dept IS NULL) LOOP
        UPDATE employees SET salary = coalesce(salary, 3000) * (1 + p_percent / 100.0) WHERE department_id = dept_rec.department_id;
        GET DIAGNOSTICS v_rows_updated = ROW_COUNT;
        SELECT sum(salary) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;
        IF v_total_budget > dept_rec.budget THEN
            RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
        END IF;
    END LOOP;
    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, current_timestamp;
    RETURN v_result_text;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        RETURN '{"error": "Department not found"}';
    WHEN OTHERS THEN
        RETURN '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id INTEGER, p_adjustment_type VARCHAR, p_adjustment_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP) RETURNS VOID AS $$
DECLARE
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    v_product_id INTEGER;
    v_quantity_on_hand NUMERIC;
    v_adjustment_qty NUMERIC;
    cur CURSOR FOR
        SELECT product_id, quantity_on_hand,
               CASE p_adjustment_type
                   WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                   WHEN 'DAMAGE' THEN -damaged_quantity
                   WHEN 'RETURN' THEN returned_quantity
                   ELSE 0
               END
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
        ORDER BY product_id;
BEGIN
    SAVEPOINT bulk_update_start;
    OPEN cur;
    LOOP
        FETCH cur INTO v_product_id, v_quantity_on_hand, v_adjustment_qty;
        EXIT WHEN NOT FOUND;
        BEGIN
            UPDATE inventory
            SET quantity_on_hand = quantity_on_hand + v_adjustment_qty,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type
            WHERE product_id = v_product_id AND warehouse_id = p_warehouse_id;
            IF NOT FOUND THEN
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (v_product_id, p_warehouse_id, GREATEST(0, v_adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;
            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (v_product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, v_quantity_on_hand, v_adjustment_qty, v_quantity_on_hand + v_adjustment_qty, CURRENT_USER);
            v_total_processed := v_total_processed + 1;
        EXCEPTION
            WHEN OTHERS THEN
                v_error_count := v_error_count + 1;
                INSERT INTO error_log (error_date, error_message, product_id)
                VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, v_product_id);
        END;
    END LOOP;
    CLOSE cur;
    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = CURRENT_TIMESTAMP
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
    RETURN;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK TO SAVEPOINT bulk_update_start;
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id INTEGER, p_adjustment_type VARCHAR, p_adjustment_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP) RETURNS VOID LANGUAGE plpgsql AS $$
DECLARE
    v_total_processed INTEGER := 0;
    v_error_count INTEGER := 0;
    rec RECORD;
BEGIN
    SAVEPOINT bulk_update_start;
    FOR rec IN 
        SELECT product_id, quantity_on_hand, 
               CASE p_adjustment_type
                   WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                   WHEN 'DAMAGE' THEN -damaged_quantity
                   WHEN 'RETURN' THEN returned_quantity
                   ELSE 0
               END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            UPDATE inventory
            SET quantity_on_hand = quantity_on_hand + rec.adjustment_qty,
                last_adjustment_date = p_adjustment_date,
                last_adjustment_type = p_adjustment_type
            WHERE product_id = rec.product_id AND warehouse_id = p_warehouse_id;

            IF NOT FOUND THEN
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;

            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.quantity_on_hand, rec.adjustment_qty, rec.quantity_on_hand + rec.adjustment_qty, CURRENT_USER);

            v_total_processed := v_total_processed + 1;
        EXCEPTION
            WHEN OTHERS THEN
                v_error_count := v_error_count + 1;
                INSERT INTO error_log (error_date, error_message, product_id)
                VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, rec.product_id);
        END;
    END LOOP;

    UPDATE inventory_staging
    SET status = 'PROCESSED', processed_date = CURRENT_TIMESTAMP
    WHERE warehouse_id = p_warehouse_id AND status = 'PENDING';

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
EXCEPTION
    WHEN OTHERS THEN
        ROLLBACK TO bulk_update_start;
        RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL) RETURNS void AS $$
DECLARE
    v_start_date date;
    v_end_date date;
    v_report_id integer;
    v_total_sales numeric := 0;
    v_total_commission numeric := 0;
    rec record;
BEGIN
    v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + interval '3 months' - interval '1 day')::date;
    v_report_id := nextval('sales_report_seq');
    CREATE TEMPORARY TABLE temp_sales_data (
        sales_rep_id integer,
        sales_rep_name varchar(100),
        total_sales numeric,
        commission_rate numeric,
        commission_amount numeric
    ) ON COMMIT DROP;
    INSERT INTO temp_sales_data
    SELECT
        sr.sales_rep_id,
        sr.sales_rep_name,
        COALESCE(SUM(s.sale_amount), 0),
        sr.commission_rate,
        COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate
    FROM sales_representatives sr
    LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN v_start_date AND v_end_date
    WHERE (p_sales_rep_id IS NULL OR sr.sales_rep_id = p_sales_rep_id)
    GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate
    ORDER BY 3 DESC;
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
    VALUES (v_report_id, p_year, p_quarter, now(), (SELECT COUNT(*) FROM temp_sales_data));
    FOR rec IN SELECT * FROM temp_sales_data LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
        VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
    END LOOP;
    UPDATE sales_report_header SET total_sales = v_total_sales, total_commission = v_total_commission WHERE report_id = v_report_id;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS VOID AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_report_sql TEXT;
    v_start_date DATE;
    v_end_date DATE;
    rec RECORD;
    total_reps INTEGER := 0;
BEGIN
    v_start_date := to_date(p_year::TEXT || '-' || LPAD(((p_quarter - 1) * 3 + 1)::TEXT, 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + INTERVAL '3 months') - INTERVAL '1 day';
    v_report_id := nextval('sales_report_seq');
    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) AS total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps) VALUES (v_report_id, p_year, p_quarter, CURRENT_TIMESTAMP, 0);
    FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount) VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
        total_reps := total_reps + 1;
    END LOOP;
    UPDATE sales_report_header SET total_sales = v_total_sales, total_commission = v_total_commission, total_reps = total_reps WHERE report_id = v_report_id;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year INTEGER, p_quarter INTEGER, p_sales_rep_id INTEGER DEFAULT NULL) RETURNS VOID AS $$
DECLARE
    v_report_id INTEGER;
    v_total_sales NUMERIC := 0;
    v_total_commission NUMERIC := 0;
    v_report_sql TEXT;
    v_start_date DATE;
    v_end_date DATE;
    rec RECORD;
    v_total_reps INTEGER := 0;
BEGIN
    v_start_date := TO_DATE(p_year::TEXT || '-' || LPAD(CAST((p_quarter-1)*3+1 AS TEXT), 2, '0') || '-01', 'YYYY-MM-DD');
    v_end_date := (v_start_date + INTERVAL '3 months' - INTERVAL '1 day')::DATE;
    SELECT nextval('sales_report_seq') INTO v_report_id;
    v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) as total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate as commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';
    INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps) VALUES (v_report_id, p_year, p_quarter, CURRENT_TIMESTAMP, 0);
    FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
        INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount) VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
        v_total_sales := v_total_sales + rec.total_sales;
        v_total_commission := v_total_commission + rec.commission_amount;
        v_total_reps := v_total_reps + 1;
    END LOOP;
    UPDATE sales_report_header SET total_reps = v_total_reps, total_sales = v_total_sales, total_commission = v_total_commission WHERE report_id = v_report_id;
    RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
EXCEPTION
    WHEN OTHERS THEN
        RAISE EXCEPTION 'Sales report generation failed: %', SQLERRM USING ERRCODE = 'P20002';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE RT_Tang_YW_MainC_InitAwb60Finish()
LANGUAGE plpgsql
AS $$
DECLARE
    Period60 INT;
    Period180 INT;
    Yw_Op_Date TIMESTAMP;
    Yw_Start_Time TIMESTAMP;
    tran_error INT;
    AwbYwDate60 TIMESTAMP;
    AwbYwDate180 TIMESTAMP;
BEGIN
    Period60 := -60;
    Period180 := -180;
    Yw_Start_Time := CURRENT_TIMESTAMP;
    tran_error := 0;
    SELECT MAX(Yw_Op_Date) INTO Yw_Op_Date FROM Tbl_Yw_Log WHERE OPTRESULT = 'RUN';
    IF Yw_Op_Date IS NULL THEN
        Yw_Op_Date := CURRENT_TIMESTAMP;
    END IF;
    AwbYwDate60 := Yw_Op_Date + (Period60 * INTERVAL '1 day');
    AwbYwDate180 := Yw_Op_Date + (Period180 * INTERVAL '1 day');
    EXECUTE 'SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED';
    BEGIN
        BEGIN
            DELETE FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp
            WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix
                FROM Tbl_Cargo
                WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                    SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix
                    FROM Tbl_Awb_Basic
                    WHERE Op_Date < DATE(AwbYwDate60)
                      AND Op_Date > DATE(AwbYwDate180)
                )
                AND FinishFlag != 0
                AND Flight_Dep = Flight_Dest
                GROUP BY AwbPrefix, AwbNo, AwbPostfix
            );
        EXCEPTION
            WHEN OTHERS THEN
                ROLLBACK;
                INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
        END;
        COMMIT;
    END;
    BEGIN
        BEGIN
            INSERT INTO Cargo2YWTemp.Tbl_Awb_Basic_Temp (AwbPrefix, AwbNo, AwbPostfix, YwTreate)
            SELECT DISTINCT AwbPrefix, AwbNo, AwbPostfix, 1
            FROM (
                SELECT AwbPrefix, AwbNo, AwbPostfix, MIN(FinishFlag) AS TmpFinishFlag
                FROM Tbl_Cargo
                WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                    SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix
                    FROM Tbl_Awb_Basic
                    WHERE Op_Date < DATE(AwbYwDate60)
                      AND Op_Date > DATE(AwbYwDate180)
                )
                AND FinishFlag != 0
                AND Flight_Dep = Flight_Dest
                GROUP BY AwbPrefix, AwbNo, AwbPostfix
            ) AS Tbl_Temp
            WHERE TmpFinishFlag > 0;
        EXCEPTION
            WHEN OTHERS THEN
                ROLLBACK;
                INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
        END;
        COMMIT;
    END;
    BEGIN
        BEGIN
            UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp
            SET AwbDate = a.Op_Date,
                AwbDest = SUBSTRING(a.routing FROM LENGTH(a.routing) - 2 FOR 3),
                Piece = a.Piece
            FROM Tbl_Awb_Basic a
            WHERE a.AwbNo = Tbl_Awb_Basic_Temp.AwbNo
              AND a.AwbPrefix = Tbl_Awb_Basic_Temp.AwbPrefix
              AND a.AwbPostfix = Tbl_Awb_Basic_Temp.AwbPostfix;
        EXCEPTION
            WHEN OTHERS THEN
                ROLLBACK;
                INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
        END;
        COMMIT;
    END;
    BEGIN
        BEGIN
            UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp bt
            SET YwTreate = 0
            FROM (
                SELECT SUM(Piece) AS Piece, AwbPrefix, AwbNo, AwbPostfix
                FROM (
                    SELECT c.Piece, c.AwbPrefix, c.AwbNo, c.AwbPostfix
                    FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp t
                    JOIN Tbl_Cargo c ON c.Flight_Dep = t.AwbDest
                        AND c.Flight_Dest = t.AwbDest
                        AND c.AwbNo = t.AwbNo
                        AND c.AwbPrefix = t.AwbPrefix
                        AND c.AwbPostfix = t.AwbPostfix
                ) b
                GROUP BY AwbPrefix, AwbNo, AwbPostfix
            ) a
            WHERE a.AwbNo = bt.AwbNo
              AND a.AwbPrefix = bt.AwbPrefix
              AND a.AwbPostfix = bt.AwbPostfix
              AND bt.Piece = a.Piece;
