CREATE OR REPLACE PROCEDURE RT_Tang_YW_MainC_InitAwb60Finish()
LANGUAGE plpgsql
AS $$
DECLARE
    Period60 INT;
    Period180 INT;
    Yw_Op_Date TIMESTAMP;
    Yw_Start_Time TIMESTAMP;
    tran_error INT;
    AwbYwDate60 TIMESTAMP;
    AwbYwDate180 TIMESTAMP;
BEGIN
    Period60 := -60;
    Period180 := -180;
    Yw_Start_Time := CURRENT_TIMESTAMP;
    tran_error := 0;

    SELECT MAX(Yw_Op_Date) INTO Yw_Op_Date FROM Tbl_Yw_Log WHERE OPTRESULT = 'RUN';
    IF Yw_Op_Date IS NULL THEN
        Yw_Op_Date := CURRENT_TIMESTAMP;
    END IF;

    AwbYwDate60 := Yw_Op_Date + (Period60 * INTERVAL '1 day');
    AwbYwDate180 := Yw_Op_Date + (Period180 * INTERVAL '1 day');

    SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

    BEGIN
        tran_error := 0;
        BEGIN
            DELETE FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp
            WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix
                FROM Tbl_Cargo
                WHERE AwbPrefix || RTRIM(AwbNo) || AwbPostfix IN (
                    SELECT AwbPrefix || RTRIM(AwbNo) || AwbPostfix
                    FROM Tbl_Awb_Basic
                    WHERE Op_Date < TO_CHAR(AwbYwDate60, 'YYYY-MM-DD')
                      AND Op_Date > TO_CHAR(AwbYwDate180, 'YYYY-MM-DD')
                )
                AND FinishFlag != 0
                AND Flight_Dep = Flight_Dest
                GROUP BY AwbPrefix, AwbNo, AwbPostfix
            );
        EXCEPTION
            WHEN OTHERS THEN
                ROLLBACK;
                tran_error := tran_error + 1;
                INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
        END;
        IF tran_error = 0 THEN
            COMMIT;
        END IF;

        BEGIN
            tran_error := 0;
            BEGIN
                UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp t
                SET AwbDate = a.Op_Date,
                    AwbDest = SUBSTRING(a.routing FROM LENGTH(a.routing) - 2 FOR 3),
                    Piece = a.Piece
                FROM Tbl_Awb_Basic a
                WHERE a.AwbNo = t.AwbNo
                  AND a.AwbPrefix = t.AwbPrefix
                  AND a.AwbPostfix = t.AwbPostfix;
            EXCEPTION
                WHEN OTHERS THEN
                    ROLLBACK;
                    tran_error := tran_error + 1;
                    INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                    RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
            END;
            IF tran_error = 0 THEN
                COMMIT;
            END IF;

            BEGIN
                tran_error := 0;
                BEGIN
                    UPDATE Cargo2YWTemp.Tbl_Awb_Basic_Temp bt
                    SET YwTreate = 0
                    FROM (
                        SELECT SUM(Piece) AS Piece, AwbPrefix, AwbNo, AwbPostfix
                        FROM (
                            SELECT c.Piece, c.AwbPrefix, c.AwbNo, c.AwbPostfix
                            FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp t
                            JOIN Tbl_Cargo c ON c.Flight_Dep = t.AwbDest
                                AND c.Flight_Dest = t.AwbDest
                                AND c.AwbNo = t.AwbNo
                                AND c.AwbPrefix = t.AwbPrefix
                                AND c.AwbPostfix = t.AwbPostfix
                        ) b
                        GROUP BY AwbPrefix, AwbNo, AwbPostfix
                    ) a
                    WHERE a.AwbNo = bt.AwbNo
                      AND a.AwbPrefix = bt.AwbPrefix
                      AND a.AwbPostfix = bt.AwbPostfix
                      AND bt.Piece = a.Piece;
                EXCEPTION
                    WHEN OTHERS THEN
                        ROLLBACK;
                        tran_error := tran_error + 1;
                        INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Awb_Basic_Temp', CURRENT_TIMESTAMP);
                        RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
                END;
                IF tran_error = 0 THEN
                    COMMIT;
                END IF;

                BEGIN
                    tran_error := 0;
                    BEGIN
                        DELETE FROM Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp
                        WHERE CCANo IN (SELECT DISTINCT AwbNo FROM Cargo2YWTemp.Tbl_Awb_Basic_Temp);

                        INSERT INTO Cargo2YWTemp.Tbl_Cargo_ChargeCorrection_Temp (CCAPrefix, CCANo, CCAPostfix)
                        SELECT DISTINCT CCAPrefix, CCANo, CCAPostfix
                        FROM Tbl_Cargo_ChargeCorrectionDetail a
                        JOIN Cargo2YWTemp.Tbl_Awb_Basic_Temp t ON a.AwbPrefix = t.AwbPrefix
                            AND a.AwbNo = t.AwbNo
                            AND a.AwbPostfix = t.AwbPostfix;
                    EXCEPTION
                        WHEN OTHERS THEN
                            ROLLBACK;
                            tran_error := tran_error + 1;
                            INSERT INTO TBL_SP_ErrLog (ErrTable, ErrDate) VALUES ('Tbl_Cargo_ChargeCorrection_Temp', CURRENT_TIMESTAMP);
                            RAISE EXCEPTION 'Tbl_Cargo_ChargeCorrection_Temp 出现异常';
                    END;
                    IF tran_error = 0 THEN
                        COMMIT;
                    END IF;
                END;
            END;
        END;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_bulkinventoryupdate(warehouseid INT, adjustmenttype VARCHAR(50), adjustmentdate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    productid INT;
    currentqty INT;
    adjustmentqty INT;
    newqty INT;
    totalprocessed INT := 0;
    errorcount INT := 0;
    errormessage VARCHAR(4000);
    inventory_cursor CURSOR FOR
        SELECT ProductID, QuantityOnHand,
            CASE adjustmenttype
                WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                WHEN 'DAMAGE' THEN -DamagedQuantity
                WHEN 'RETURN' THEN ReturnedQuantity
                ELSE 0
            END
        FROM InventoryStaging
        WHERE WarehouseID = warehouseid AND Status = 'PENDING'
        ORDER BY ProductID;
BEGIN
    IF adjustmentdate IS NULL THEN
        adjustmentdate := CURRENT_TIMESTAMP;
    END IF;
    BEGIN
        OPEN inventory_cursor;
        LOOP
            FETCH inventory_cursor INTO productid, currentqty, adjustmentqty;
            EXIT WHEN NOT FOUND;
            BEGIN
                newqty := currentqty + adjustmentqty;
                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = productid AND WarehouseID = warehouseid) THEN
                    UPDATE Inventory SET QuantityOnHand = newqty, LastAdjustmentDate = adjustmentdate, LastAdjustmentType = adjustmenttype
                    WHERE ProductID = productid AND WarehouseID = warehouseid;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (productid, warehouseid, GREATEST(newqty, 0), adjustmentdate, adjustmenttype);
                END IF;
                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (productid, warehouseid, adjustmentdate, adjustmenttype, currentqty, adjustmentqty, newqty, CURRENT_USER);
                totalprocessed := totalprocessed + 1;
            EXCEPTION
                WHEN OTHERS THEN
                    errorcount := errorcount + 1;
                    INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                    VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, productid);
            END;
        END LOOP;
        CLOSE inventory_cursor;
        UPDATE InventoryStaging SET Status = 'PROCESSED', ProcessedDate = CURRENT_TIMESTAMP
        WHERE WarehouseID = warehouseid AND Status = 'PENDING';
        COMMIT;
        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', totalprocessed, errorcount;
    EXCEPTION
        WHEN OTHERS THEN
            errormessage := SQLERRM;
            ROLLBACK;
            IF inventory_cursor%ISOPEN THEN
                CLOSE inventory_cursor;
            END IF;
            RAISE EXCEPTION 'Bulk inventory update failed: %', errormessage;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_BulkInventoryUpdate(WarehouseID INT, AdjustmentType VARCHAR(50), AdjustmentDate TIMESTAMP DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    ProductID INT;
    CurrentQty INT;
    AdjustmentQty INT;
    NewQty INT;
    TotalProcessed INT := 0;
    ErrorCount INT := 0;
    ErrorMessage VARCHAR(4000);
    rec RECORD;
BEGIN
    IF AdjustmentDate IS NULL THEN
        AdjustmentDate := CURRENT_TIMESTAMP;
    END IF;
    FOR rec IN 
        SELECT ProductID, QuantityOnHand, 
            CASE AdjustmentType
                WHEN 'RECOUNT' THEN PhysicalCount - QuantityOnHand
                WHEN 'DAMAGE' THEN -DamagedQuantity
                WHEN 'RETURN' THEN ReturnedQuantity
                ELSE 0
            END AS AdjustmentQty
        FROM InventoryStaging 
        WHERE WarehouseID = SP_BulkInventoryUpdate.WarehouseID AND Status = 'PENDING'
        ORDER BY ProductID
    LOOP
        BEGIN
            SAVEPOINT before_row;
            NewQty := rec.QuantityOnHand + rec.AdjustmentQty;
            IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = rec.ProductID AND WarehouseID = SP_BulkInventoryUpdate.WarehouseID) THEN
                UPDATE Inventory 
                SET QuantityOnHand = NewQty, 
                    LastAdjustmentDate = AdjustmentDate, 
                    LastAdjustmentType = AdjustmentType
                WHERE ProductID = rec.ProductID AND WarehouseID = SP_BulkInventoryUpdate.WarehouseID;
            ELSE
                INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                VALUES (rec.ProductID, SP_BulkInventoryUpdate.WarehouseID, GREATEST(NewQty, 0), AdjustmentDate, AdjustmentType);
            END IF;
            INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
            VALUES (rec.ProductID, SP_BulkInventoryUpdate.WarehouseID, AdjustmentDate, AdjustmentType, rec.QuantityOnHand, rec.AdjustmentQty, NewQty, CURRENT_USER);
            TotalProcessed := TotalProcessed + 1;
        EXCEPTION
            WHEN OTHERS THEN
                ErrorCount := ErrorCount + 1;
                INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                VALUES (CURRENT_TIMESTAMP, 'Inventory update failed: ' || SQLERRM, rec.ProductID);
                ROLLBACK TO SAVEPOINT before_row;
        END;
    END LOOP;
    UPDATE InventoryStaging 
    SET Status = 'PROCESSED', ProcessedDate = CURRENT_TIMESTAMP 
    WHERE WarehouseID = SP_BulkInventoryUpdate.WarehouseID AND Status = 'PENDING';
    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', TotalProcessed, ErrorCount;
EXCEPTION
    WHEN OTHERS THEN
        ErrorMessage := SQLERRM;
        ROLLBACK;
        RAISE EXCEPTION 'Bulk inventory update failed: %', ErrorMessage;
END;
$$;

