CREATE OR REPLACE PROCEDURE SP_DataMigration(source_table VARCHAR(128), target_table VARCHAR(128), batch_size INT DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE
    sql_text TEXT;
    source_count INT;
    target_count INT;
    batch_count INT := 0;
    migration_id INT;
    error_message TEXT;
    row_count INT;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (source_table, target_table, CURRENT_TIMESTAMP, 'RUNNING')
    RETURNING MigrationID INTO migration_id;

    sql_text := 'SELECT COUNT(*) FROM ' || quote_ident(source_table);
    EXECUTE sql_text INTO source_count;

    UPDATE MigrationLog SET SourceCount = source_count WHERE MigrationID = migration_id;

    sql_text := 'DELETE FROM ' || quote_ident(target_table);
    EXECUTE sql_text;

    WHILE batch_count < source_count LOOP
        sql_text := 'INSERT INTO ' || quote_ident(target_table) || 
                    ' SELECT * FROM ' || quote_ident(source_table) || 
                    ' WHERE NOT EXISTS (SELECT 1 FROM ' || quote_ident(target_table) || 
                    ' t WHERE t.ID = ' || quote_ident(source_table) || '.ID) ' ||
                    ' LIMIT ' || batch_size;
        EXECUTE sql_text;
        GET DIAGNOSTICS row_count = ROW_COUNT;
        IF row_count = 0 THEN
            EXIT;
        END IF;
        batch_count := batch_count + row_count;
    END LOOP;

    sql_text := 'SELECT COUNT(*) FROM ' || quote_ident(target_table);
    EXECUTE sql_text INTO target_count;

    UPDATE MigrationLog 
    SET EndTime = CURRENT_TIMESTAMP, 
        TargetCount = target_count, 
        Status = CASE WHEN source_count = target_count THEN 'SUCCESS' ELSE 'WARNING' END 
    WHERE MigrationID = migration_id;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', source_table, source_count, target_count;

EXCEPTION
    WHEN OTHERS THEN
        error_message := SQLERRM;
        UPDATE MigrationLog 
        SET EndTime = CURRENT_TIMESTAMP, 
            Status = 'FAILED', 
            ErrorMessage = error_message 
        WHERE MigrationID = migration_id;
        RAISE EXCEPTION 'Data migration failed: %', error_message;
END;
$$;

