CREATE OR REPLACE FUNCTION archive_old_records(p_archive_date DATE) RETURNS VOID AS $$
DECLARE
    v_deleted_rows INTEGER;
BEGIN
    WITH deleted AS (
        DELETE FROM active_data 
        WHERE record_date < p_archive_date
        RETURNING record_id, record_data
    )
    INSERT INTO archive_table (record_id, record_data)
    SELECT record_id, record_data FROM deleted;

    GET DIAGNOSTICS v_deleted_rows = ROW_COUNT;
    INSERT INTO audit_log (log_message) VALUES (v_deleted_rows || ' rows archived.');
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION getemployeestats(p_dept_id INTEGER) RETURNS TEXT AS $$
DECLARE
    v_dept_name TEXT;
    v_total_emp INTEGER;
    v_avg_salary NUMERIC;
    v_max_salary NUMERIC;
    v_active_projects INTEGER;
    v_employee_id INTEGER;
    v_last_name TEXT;
    v_salary NUMERIC;
    v_result TEXT;
    cur REFCURSOR;
BEGIN
    SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
    SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM employees WHERE department_id = p_dept_id;
    SELECT COUNT(*) INTO v_active_projects FROM projects WHERE department_id = p_dept_id AND status = 'ACTIVE';
    v_result := '{  "department": "' || v_dept_name || '",  "total_employees": ' || v_total_emp || ',  "salary_stats": {    "average": ' || v_avg_salary || ',    "max": ' || v_max_salary || '  },  "active_projects": ' || v_active_projects || ',  "top_earners": [';
    OPEN cur FOR SELECT employee_id, last_name, salary FROM employees WHERE department_id = p_dept_id ORDER BY salary DESC LIMIT 5;
    LOOP
        FETCH cur INTO v_employee_id, v_last_name, v_salary;
        EXIT WHEN NOT FOUND;
        v_result := v_result || '    {"id": ' || v_employee_id || ', "name": "' || v_last_name || '", "salary": ' || v_salary || '},';
    END LOOP;
    CLOSE cur;
    v_result := rtrim(v_result, ',') || '  ]}';
    RETURN v_result;
EXCEPTION
    WHEN no_data_found THEN
        RETURN '{"error": "Department not found"}';
    WHEN others THEN
        RETURN '{"error": "' || SQLERRM || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION usp_UpdateOrderStatus(p_CustomerID INT, p_OldStatus VARCHAR(20), p_NewStatus VARCHAR(20), OUT RowsUpdated INT, OUT ErrorMessage VARCHAR(200)) RETURNS RECORD AS $$
DECLARE
    ProcessingFee DECIMAL(10,2) := 0;
    fee_cursor CURSOR FOR 
        SELECT Fee 
        FROM OrderDetails 
        WHERE OrderID IN (
            SELECT OrderID 
            FROM Orders 
            WHERE CustomerID = p_CustomerID AND Status = p_NewStatus
        );
BEGIN
    IF p_OldStatus = p_NewStatus THEN
        ErrorMessage := '新旧状态不能相同';
        RETURN 400;
    END IF;

    SAVEPOINT update_order_status_sp;

    UPDATE Orders 
    SET Status = p_NewStatus, LastUpdated = CURRENT_TIMESTAMP 
    WHERE CustomerID = p_CustomerID 
    AND Status = p_OldStatus 
    AND OrderDate > CURRENT_TIMESTAMP - INTERVAL '3 months';

    GET DIAGNOSTICS RowsUpdated = ROW_COUNT;

    IF RowsUpdated = 0 THEN
        ErrorMessage := '未找到符合条件的订单';
        RETURN 404;
    END IF;

    INSERT INTO OrderLog (OrderID, OldStatus, NewStatus, ChangeDate)
    SELECT OrderID, p_OldStatus, p_NewStatus, CURRENT_TIMESTAMP
    FROM Orders
    WHERE CustomerID = p_CustomerID AND Status = p_NewStatus;

    OPEN fee_cursor;
    LOOP
        FETCH fee_cursor INTO ProcessingFee;
        EXIT WHEN NOT FOUND;
        ProcessingFee := ProcessingFee * 0.95;
    END LOOP;
    CLOSE fee_cursor;

    RELEASE SAVEPOINT update_order_status_sp;
    ErrorMessage := '更新成功';
    RETURN 200;

EXCEPTION
    WHEN others THEN
        ROLLBACK TO SAVEPOINT update_order_status_sp;
        ErrorMessage := SQLERRM || ' (Line: ?)';
        RETURN 500;
END;
$$ LANGUAGE plpgsql;

DROP TRIGGER IF EXISTS trg_audit_emp_sal ON emp;
CREATE OR REPLACE FUNCTION trg_audit_emp_sal_func()
RETURNS TRIGGER AS $$
BEGIN
INSERT INTO emp_audit(emp_id, old_sal, new_sal)
VALUES (OLD.empno, OLD.sal, NEW.sal);
RETURN NEW;
END;
$$ LANGUAGE plpgsql;
CREATE TRIGGER trg_audit_emp_sal
BEFORE UPDATE OF sal ON emp
FOR EACH ROW
EXECUTE PROCEDURE trg_audit_emp_sal_func();

