DECLARE
    mycursor CURSOR FOR SELECT "LastName" FROM "Person"."Person";
    lastname VARCHAR(50);
BEGIN
    OPEN mycursor;
    FETCH NEXT FROM mycursor INTO lastname;
    CLOSE mycursor;
END $$;

