CREATE OR REPLACE PROCEDURE SP_ProcessPayroll(PayPeriodStart TIMESTAMP, PayPeriodEnd TIMESTAMP, DepartmentID INT DEFAULT NULL)
AS
$$
DECLARE
    v_PayrollID INT;
    v_EmployeeID INT;
    v_HourlyRate DECIMAL(10,2);
    v_RegularHours DECIMAL(5,2);
    v_OvertimeHours DECIMAL(5,2);
    v_GrossPay DECIMAL(10,2);
    v_TaxDeduction DECIMAL(10,2);
    v_NetPay DECIMAL(10,2);
    v_TotalGross DECIMAL(15,2) := 0;
    v_TotalNet DECIMAL(15,2) := 0;
    v_cur REFCURSOR;
    v_fetch_status INT := 0;
BEGIN
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (PayPeriodStart, PayPeriodEnd, DepartmentID, CURRENT_TIMESTAMP, 'PROCESSING')
    RETURNING PayrollID INTO v_PayrollID;

    OPEN v_cur FOR
        SELECT e.EmployeeID, e.HourlyRate, COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours, COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
        FROM Employees e
        LEFT JOIN Timesheet t ON e.EmployeeID = t.EmployeeID AND t.WorkDate BETWEEN PayPeriodStart AND PayPeriodEnd
        WHERE e.Status = 'ACTIVE' AND (DepartmentID IS NULL OR e.DepartmentID = DepartmentID)
        GROUP BY e.EmployeeID, e.HourlyRate
        ORDER BY e.EmployeeID;

    LOOP
        FETCH v_cur INTO v_EmployeeID, v_HourlyRate, v_RegularHours, v_OvertimeHours;
        EXIT WHEN NOT FOUND;

        v_GrossPay := (v_RegularHours * v_HourlyRate) + (v_OvertimeHours * v_HourlyRate * 1.5);
        v_TaxDeduction := v_GrossPay * 0.20;
        v_NetPay := v_GrossPay - v_TaxDeduction;

        INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
        VALUES (v_PayrollID, v_EmployeeID, v_HourlyRate, v_RegularHours, v_OvertimeHours, v_GrossPay, v_TaxDeduction, v_NetPay);

