EXCEPTION
    WHEN OTHERS THEN
        IF v_cur IS NOT NULL THEN
            CLOSE v_cur;
        END IF;

        UPDATE PayrollHeader
        SET Status = 'FAILED', ErrorMessage = SQLERRM
        WHERE PayrollID = v_PayrollID;

