CREATE OR REPLACE PROCEDURE SP_UpdateProductCategories(IN p_OldCategoryID INT, IN p_NewCategoryID INT, IN p_EffectiveDate TIMESTAMP DEFAULT NULL)
LANGUAGE PLPGSQL
AS $$
DECLARE
    v_ProductCount INT;
    v_UpdatedCount INT := 0;
    v_ProductID INT;
    v_ProductName VARCHAR(100);
    v_EffectiveDate TIMESTAMP;
    product_cursor CURSOR FOR SELECT ProductID, ProductName FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';
    fetch_status INT := 0;
BEGIN
    v_EffectiveDate := COALESCE(p_EffectiveDate, CURRENT_TIMESTAMP);

    IF p_OldCategoryID = p_NewCategoryID THEN
        RAISE EXCEPTION 'Old and new category IDs cannot be the same';
    END IF;

    SELECT COUNT(*) INTO v_ProductCount FROM Products WHERE CategoryID = p_OldCategoryID AND Status = 'ACTIVE';

    IF v_ProductCount = 0 THEN
        RAISE NOTICE 'No active products found in category %', p_OldCategoryID;
        RETURN;
    END IF;

    OPEN product_cursor;
    LOOP
        FETCH product_cursor INTO v_ProductID, v_ProductName;
        EXIT WHEN NOT FOUND;

        BEGIN
            INSERT INTO ProductCategoryHistory (ProductID, OldCategoryID, NewCategoryID, ChangeDate, ChangedBy)
            VALUES (v_ProductID, p_OldCategoryID, p_NewCategoryID, v_EffectiveDate, CURRENT_USER);

            UPDATE Products 
            SET CategoryID = p_NewCategoryID, LastModifiedDate = v_EffectiveDate, LastModifiedBy = CURRENT_USER
            WHERE ProductID = v_ProductID;

            v_UpdatedCount := v_UpdatedCount + 1;
        EXCEPTION
            WHEN OTHERS THEN
                INSERT INTO CategoryUpdateErrors (ProductID, ErrorMessage, ErrorDate)
                VALUES (v_ProductID, SQLERRM, CURRENT_TIMESTAMP);
        END;
    END LOOP;
    CLOSE product_cursor;

    UPDATE ProductCategories 
    SET LastUpdateDate = v_EffectiveDate 
    WHERE CategoryID IN (p_OldCategoryID, p_NewCategoryID);

    RAISE NOTICE 'Category update completed. % products updated out of % total.', v_UpdatedCount, v_ProductCount;
EXCEPTION
    WHEN OTHERS THEN
        IF product_cursor IS NOT NULL THEN
            CLOSE product_cursor;
        END IF;
        RAISE EXCEPTION 'Category update failed: %', SQLERRM;
END;
$$;

