CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL) RETURNS text AS $$
DECLARE
  v_total_emp integer := 0;
  v_avg_salary numeric := 0;
  v_max_salary numeric := 0;
  v_dept_name text;
  v_rows_updated integer := 0;
  v_total_budget numeric := 0;
  p_result text := '';
  rec record;
  dept_rec record;
  _rowcount integer;
  _errmsg text;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  IF NOT FOUND THEN
    RETURN '{"error": "Department not found"}';
  END IF;

  SELECT COUNT(*), AVG(salary), MAX(salary) INTO v_total_emp, v_avg_salary, v_max_salary FROM employees WHERE department_id = p_dept_id;

  p_result := '{ "department": "' || v_dept_name || '", "total_employees": ' || v_total_emp || ', "salary_stats": { "average": ' || COALESCE(v_avg_salary,0) || ', "max": ' || COALESCE(v_max_salary,0) || ' }, "top_earners": [';

  FOR rec IN EXECUTE 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5' USING p_dept_id
  LOOP
    p_result := p_result || ' {"id": ' || rec.employee_id || ', "name": "' || rec.last_name || '", "salary": ' || COALESCE(rec.salary,0) || '},';
  END LOOP;

  p_result := rtrim(p_result, ',') || ' ] }';

  FOR dept_rec IN
    SELECT department_id, budget
    FROM departments
    WHERE (p_min_dept IS NULL OR department_id >= p_min_dept)
      AND (p_max_dept IS NULL OR department_id <= p_max_dept)
  LOOP
    UPDATE employees
    SET salary = COALESCE(salary, 3000) * (1 + p_percent / 100.0)
    WHERE department_id = dept_rec.department_id;
    GET DIAGNOSTICS _rowcount = ROW_COUNT;
    v_rows_updated := v_rows_updated + _rowcount;

    SELECT COALESCE(SUM(salary),0) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;

    IF v_total_budget > dept_rec.budget THEN
      RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
    END IF;
  END LOOP;

  RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

  RETURN p_result;
EXCEPTION WHEN OTHERS THEN
  GET STACKED DIAGNOSTICS _errmsg = MESSAGE_TEXT;
  RETURN '{"error": "' || replace(_errmsg,'"','\"') || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(p_dept_id numeric, p_percent numeric, p_min_dept numeric DEFAULT NULL, p_max_dept numeric DEFAULT NULL) RETURNS text AS $$
DECLARE
  v_total_emp numeric;
  v_avg_salary numeric;
  v_max_salary numeric;
  v_sql text;
  v_dept_name text;
  v_rows_updated integer := 0;
  v_total_budget numeric := 0;
  rec record;
  dept_rec record;
  p_result text := '';
  v_updated integer;
  v_errm text;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  IF NOT FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN p_result;
  END IF;

  SELECT COUNT(*)::numeric, AVG(salary), MAX(salary)
    INTO v_total_emp, v_avg_salary, v_max_salary
  FROM employees
  WHERE department_id = p_dept_id;

  v_sql := 'SELECT employee_id, last_name, salary FROM employees WHERE department_id = $1 ORDER BY salary DESC LIMIT 5';

  p_result := '{ "department": "' || COALESCE(v_dept_name, '') || '", "total_employees": ' || COALESCE(v_total_emp, 0)::text ||
               ', "salary_stats": { "average": ' || COALESCE(v_avg_salary, 0)::text || ', "max": ' || COALESCE(v_max_salary, 0)::text ||
               ' }, "top_earners": [';

  FOR rec IN EXECUTE v_sql USING p_dept_id LOOP
    p_result := p_result || ' {"id": ' || COALESCE(rec.employee_id, 0)::text ||
                 ', "name": "' || COALESCE(rec.last_name, '') ||
                 '", "salary": ' || COALESCE(rec.salary, 0)::text || '},';
  END LOOP;

  p_result := rtrim(p_result, ',') || ' ] }';

  FOR dept_rec IN
    SELECT department_id, budget
    FROM departments
    WHERE (p_min_dept IS NULL OR department_id >= p_min_dept)
      AND (p_max_dept IS NULL OR department_id <= p_max_dept)
  LOOP
    UPDATE employees
       SET salary = COALESCE(salary, 3000) * (1 + p_percent/100.0)
     WHERE department_id = dept_rec.department_id;

    GET DIAGNOSTICS v_updated = ROW_COUNT;
    v_rows_updated := v_rows_updated + COALESCE(v_updated, 0);

    SELECT SUM(salary) INTO v_total_budget FROM employees WHERE department_id = dept_rec.department_id;

    IF v_total_budget IS NOT NULL AND v_total_budget > dept_rec.budget THEN
      RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
    END IF;
  END LOOP;

  RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();

  RETURN p_result;

EXCEPTION
  WHEN others THEN
    GET STACKED DIAGNOSTICS v_errm = MESSAGE_TEXT;
    RETURN '{"error": "' || replace(COALESCE(v_errm, 'Unknown error'), '"', '\"') || '"}';
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION checkandupdatesalaries(
  p_dept_id numeric,
  OUT p_result text,
  p_percent numeric,
  p_min_dept numeric DEFAULT NULL,
  p_max_dept numeric DEFAULT NULL
)
LANGUAGE plpgsql
AS $$
DECLARE
  v_total_emp bigint := 0;
  v_avg_salary numeric := 0;
  v_max_salary numeric := 0;
  v_employee_id integer;
  v_last_name text;
  v_salary numeric;
  v_dept_name text;
  v_rows_updated integer := 0;
  v_rowcount integer := 0;
  v_total_budget numeric := 0;
  dept_rec record;
  v_errmsg text;
BEGIN
  SELECT department_name INTO v_dept_name FROM departments WHERE department_id = p_dept_id;
  IF NOT FOUND THEN
    p_result := '{"error": "Department not found"}';
    RETURN;
  END IF;

  BEGIN
    SELECT COUNT(*), AVG(salary), MAX(salary)
      INTO v_total_emp, v_avg_salary, v_max_salary
    FROM employees
    WHERE department_id = p_dept_id;

    p_result := '{ "department": "' || coalesce(v_dept_name,'') || '", "total_employees": ' || coalesce(v_total_emp,0)::text || ', "salary_stats": { "average": ' || coalesce(v_avg_salary,0)::text || ', "max": ' || coalesce(v_max_salary,0)::text || ' }, "top_earners": [';

    FOR v_employee_id, v_last_name, v_salary IN
      SELECT employee_id, last_name, salary
      FROM employees
      WHERE department_id = p_dept_id
      ORDER BY salary DESC NULLS LAST
      LIMIT 5
    LOOP
      p_result := p_result || ' {"id": ' || v_employee_id::text || ', "name": "' || replace(coalesce(v_last_name,''), '"', '\"') || '", "salary": ' || coalesce(v_salary,0)::text || '},';
    END LOOP;

    p_result := rtrim(p_result, ',') || ' ] }';

    FOR dept_rec IN
      SELECT department_id, budget
      FROM departments
      WHERE (department_id >= p_min_dept OR p_min_dept IS NULL)
        AND (department_id <= p_max_dept OR p_max_dept IS NULL)
    LOOP
      UPDATE employees
      SET salary = coalesce(salary, 3000) * (1 + p_percent/100.0)
      WHERE department_id = dept_rec.department_id;
      GET DIAGNOSTICS v_rowcount = ROW_COUNT;
      v_rows_updated := v_rows_updated + v_rowcount;

      SELECT coalesce(SUM(salary),0) INTO v_total_budget
      FROM employees
      WHERE department_id = dept_rec.department_id;

      IF v_total_budget > coalesce(dept_rec.budget,0) THEN
        RAISE EXCEPTION 'Budget exceeded in department %', dept_rec.department_id;
      END IF;
    END LOOP;

    RAISE NOTICE 'Updated % records, Time %', v_rows_updated, now();
    RETURN;
  EXCEPTION
    WHEN others THEN
      GET STACKED DIAGNOSTICS v_errmsg = MESSAGE_TEXT;
      p_result := '{"error": "' || replace(coalesce(v_errmsg,''), '"', '\"') || '"}';
      RETURN;
  END;
END;
$$;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id numeric, p_adjustment_type text, p_adjustment_date timestamp without time zone DEFAULT now())
RETURNS void
LANGUAGE plpgsql
AS $func$
DECLARE
  v_total_processed integer := 0;
  v_error_count integer := 0;
  rec RECORD;
  v_rowcount integer;
  v_adj_qty numeric;
  err_msg text;
BEGIN
  FOR rec IN
    SELECT product_id,
           quantity_on_hand AS current_qty,
           CASE p_adjustment_type
             WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
             WHEN 'DAMAGE' THEN -damaged_quantity
             WHEN 'RETURN' THEN returned_quantity
             ELSE 0
           END AS adjustment_qty
    FROM inventory_staging
    WHERE warehouse_id = p_warehouse_id
      AND status = 'PENDING'
    ORDER BY product_id
  LOOP
    BEGIN
      v_adj_qty := rec.adjustment_qty;

      UPDATE inventory inv
      SET quantity_on_hand = inv.quantity_on_hand + v_adj_qty,
          last_adjustment_date = p_adjustment_date,
          last_adjustment_type = p_adjustment_type
      WHERE inv.product_id = rec.product_id
        AND inv.warehouse_id = p_warehouse_id;

      GET DIAGNOSTICS v_rowcount = ROW_COUNT;

      IF v_rowcount = 0 THEN
        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
        VALUES (rec.product_id, p_warehouse_id, GREATEST(0, v_adj_qty), p_adjustment_date, p_adjustment_type);
      END IF;

      INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
      VALUES (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, rec.current_qty + rec.adjustment_qty, current_user);

      v_total_processed := v_total_processed + 1;
    EXCEPTION WHEN others THEN
      v_error_count := v_error_count + 1;
      GET STACKED DIAGNOSTICS err_msg = MESSAGE_TEXT;
      INSERT INTO error_log (error_date, error_message, product_id)
      VALUES (now(), 'Inventory update failed: ' || COALESCE(err_msg, ''), rec.product_id);
    END;
  END LOOP;

  UPDATE inventory_staging
  SET status = 'PROCESSED',
      processed_date = now()
  WHERE warehouse_id = p_warehouse_id
    AND status = 'PENDING';

  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;

  RETURN;
EXCEPTION WHEN others THEN
  GET STACKED DIAGNOSTICS err_msg = MESSAGE_TEXT;
  RAISE EXCEPTION 'Bulk inventory update failed: %', COALESCE(err_msg, '');
END;
$func$;

CREATE OR REPLACE FUNCTION SP_BULK_UPDATE_INVENTORY(p_warehouse_id numeric, p_adjustment_type text, p_adjustment_date timestamp without time zone DEFAULT now())
RETURNS void AS $$
DECLARE
  v_total_processed integer := 0;
  v_error_count integer := 0;
  rec record;
  adj_qty numeric;
BEGIN
  FOR rec IN
    SELECT
      product_id,
      quantity_on_hand AS current_qty,
      CASE p_adjustment_type
        WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
        WHEN 'DAMAGE' THEN -damaged_quantity
        WHEN 'RETURN' THEN returned_quantity
        ELSE 0
      END AS adjustment_qty
    FROM inventory_staging
    WHERE warehouse_id = p_warehouse_id
      AND status = 'PENDING'
    ORDER BY product_id
  LOOP
    BEGIN
      adj_qty := rec.adjustment_qty;

      UPDATE inventory inv
      SET quantity_on_hand = inv.quantity_on_hand + adj_qty,
          last_adjustment_date = p_adjustment_date,
          last_adjustment_type = p_adjustment_type
      WHERE inv.product_id = rec.product_id
        AND inv.warehouse_id = p_warehouse_id;

      IF NOT FOUND THEN
        INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
        VALUES (rec.product_id, p_warehouse_id, GREATEST(0, adj_qty), p_adjustment_date, p_adjustment_type);
      END IF;

      INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
      VALUES (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, adj_qty, rec.current_qty + adj_qty, current_user);

      v_total_processed := v_total_processed + 1;
    EXCEPTION
      WHEN OTHERS THEN
        v_error_count := v_error_count + 1;
        INSERT INTO error_log (error_date, error_message, product_id)
        VALUES (now(), 'Inventory update failed: ' || SQLERRM, rec.product_id);
    END;
  END LOOP;

  UPDATE inventory_staging
  SET status = 'PROCESSED',
      processed_date = now()
  WHERE warehouse_id = p_warehouse_id
    AND status = 'PENDING';

  RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;

  RETURN;
EXCEPTION
  WHEN OTHERS THEN
    RAISE EXCEPTION 'Bulk inventory update failed: %', SQLERRM;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION sp_bulk_update_inventory(p_warehouse_id numeric, p_adjustment_type text, p_adjustment_date timestamp without time zone DEFAULT now())
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
    v_total_processed integer := 0;
    v_error_count integer := 0;
    rec RECORD;
    new_qty numeric;
    err_msg text;
BEGIN
    FOR rec IN
        SELECT product_id,
               quantity_on_hand AS current_qty,
               CASE p_adjustment_type
                    WHEN 'RECOUNT' THEN physical_count - quantity_on_hand
                    WHEN 'DAMAGE' THEN -damaged_quantity
                    WHEN 'RETURN' THEN returned_quantity
                    ELSE 0
               END AS adjustment_qty
        FROM inventory_staging
        WHERE warehouse_id = p_warehouse_id
          AND status = 'PENDING'
        ORDER BY product_id
    LOOP
        BEGIN
            UPDATE inventory inv
               SET quantity_on_hand = inv.quantity_on_hand + rec.adjustment_qty,
                   last_adjustment_date = p_adjustment_date,
                   last_adjustment_type = p_adjustment_type
             WHERE inv.product_id = rec.product_id
               AND inv.warehouse_id = p_warehouse_id;

            IF NOT FOUND THEN
                INSERT INTO inventory (product_id, warehouse_id, quantity_on_hand, last_adjustment_date, last_adjustment_type)
                VALUES (rec.product_id, p_warehouse_id, GREATEST(0, rec.adjustment_qty), p_adjustment_date, p_adjustment_type);
            END IF;

            new_qty := rec.current_qty + rec.adjustment_qty;

            INSERT INTO inventory_audit_log (product_id, warehouse_id, adjustment_date, adjustment_type, old_quantity, adjustment_quantity, new_quantity, created_by)
            VALUES (rec.product_id, p_warehouse_id, p_adjustment_date, p_adjustment_type, rec.current_qty, rec.adjustment_qty, new_qty, current_user);

            v_total_processed := v_total_processed + 1;
        EXCEPTION WHEN others THEN
            v_error_count := v_error_count + 1;
            GET DIAGNOSTICS err_msg = MESSAGE_TEXT;
            INSERT INTO error_log (error_date, error_message, product_id)
            VALUES (now(), 'Inventory update failed: ' || COALESCE(err_msg, 'unknown error'), rec.product_id);
        END;
    END LOOP;

    UPDATE inventory_staging
       SET status = 'PROCESSED',
           processed_date = now()
     WHERE warehouse_id = p_warehouse_id
       AND status = 'PENDING';

    RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_total_processed, v_error_count;
END;
$$;

CREATE OR REPLACE FUNCTION sp_generate_sales_report(p_year integer, p_quarter integer, p_sales_rep_id integer DEFAULT NULL)
RETURNS void
LANGUAGE plpgsql
AS $$
DECLARE
  v_report_id bigint;
  v_total_sales numeric := 0;
  v_total_commission numeric := 0;
  v_report_sql text;
  v_start_date date;
  v_end_date date;
  v_total_reps integer := 0;
  rec RECORD;
BEGIN
  v_start_date := to_date(p_year::text || '-' || lpad(((p_quarter - 1) * 3 + 1)::text, 2, '0') || '-01', 'YYYY-MM-DD');
  v_end_date := (date_trunc('month', v_start_date::timestamp) + interval '3 months' - interval '1 day')::date;

  v_report_id := nextval('sales_report_seq');

  v_report_sql := 'SELECT sr.sales_rep_id, sr.sales_rep_name, COALESCE(SUM(s.sale_amount), 0) AS total_sales, sr.commission_rate, COALESCE(SUM(s.sale_amount), 0) * sr.commission_rate AS commission_amount FROM sales_representatives sr LEFT JOIN sales s ON sr.sales_rep_id = s.sales_rep_id AND s.sale_date BETWEEN $1 AND $2 WHERE ($3 IS NULL OR sr.sales_rep_id = $3) GROUP BY sr.sales_rep_id, sr.sales_rep_name, sr.commission_rate ORDER BY total_sales DESC';

  INSERT INTO sales_report_header (report_id, report_year, report_quarter, created_date, total_reps)
  VALUES (v_report_id, p_year, p_quarter, now(), 0);

  FOR rec IN EXECUTE v_report_sql USING v_start_date, v_end_date, p_sales_rep_id LOOP
    INSERT INTO sales_report_detail (report_id, sales_rep_id, sales_rep_name, total_sales, commission_rate, commission_amount)
    VALUES (v_report_id, rec.sales_rep_id, rec.sales_rep_name, rec.total_sales, rec.commission_rate, rec.commission_amount);
    v_total_sales := v_total_sales + COALESCE(rec.total_sales, 0);
    v_total_commission := v_total_commission + COALESCE(rec.commission_amount, 0);
    v_total_reps := v_total_reps + 1;
  END LOOP;

  UPDATE sales_report_header
  SET total_sales = v_total_sales,
      total_commission = v_total_commission,
      total_reps = v_total_reps
  WHERE report_id = v_report_id;

  RAISE NOTICE 'Sales report generated successfully. Report ID: %', v_report_id;
END;
$$;

