  RAISE EXCEPTION 'Sales report generation failed: %', v_err_text;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE PROCEDURE dbo.rt_tang_yw_mainc_initawb60finish()
LANGUAGE plpgsql
AS $$
DECLARE
    period60 integer;
    period180 integer;
    yw_op_date timestamp;
    yw_start_time timestamp;
    awb_yw_date60 date;
    awb_yw_date180 date;
BEGIN
    period60 := -60;
    period180 := -180;
    yw_start_time := now();
    SELECT max(yw_op_date) INTO yw_op_date FROM tbl_yw_log WHERE optresult = 'RUN';
    IF yw_op_date IS NULL THEN
        yw_op_date := now();
    END IF;
    awb_yw_date60 := (yw_op_date + (period60 || ' days')::interval)::date;
    awb_yw_date180 := (yw_op_date + (period180 || ' days')::interval)::date;

    SET LOCAL TRANSACTION ISOLATION LEVEL READ COMMITTED;

    BEGIN
        DELETE FROM cargo2ywtemp.tbl_awb_basic_temp t
        WHERE EXISTS (
            SELECT 1
            FROM tbl_cargo c
            WHERE c.awbprefix = t.awbprefix
              AND c.awbno = t.awbno
              AND c.awbpostfix = t.awbpostfix
              AND c.finishflag <> 0
              AND c.flight_dep = c.flight_dest
              AND EXISTS (
                  SELECT 1
                  FROM tbl_awb_basic b
                  WHERE b.awbprefix = c.awbprefix
                    AND b.awbno = c.awbno
                    AND b.awbpostfix = c.awbpostfix
                    AND b.op_date::date < awb_yw_date60
                    AND b.op_date::date > awb_yw_date180
              )
        );

        INSERT INTO cargo2ywtemp.tbl_awb_basic_temp (awbprefix, awbno, awbpostfix, ywtreate)
        SELECT DISTINCT awbprefix, awbno, awbpostfix, 1
        FROM (
            SELECT c.awbprefix, c.awbno, c.awbpostfix, min(c.finishflag) AS tmpfinishflag
            FROM tbl_cargo c
            WHERE c.finishflag <> 0
              AND c.flight_dep = c.flight_dest
              AND EXISTS (
                  SELECT 1 FROM tbl_awb_basic b
                  WHERE b.awbprefix = c.awbprefix
                    AND b.awbno = c.awbno
                    AND b.awbpostfix = c.awbpostfix
                    AND b.op_date::date < awb_yw_date60
                    AND b.op_date::date > awb_yw_date180
              )
            GROUP BY c.awbprefix, c.awbno, c.awbpostfix
        ) tbl_temp
        WHERE tmpfinishflag > 0;
    EXCEPTION WHEN OTHERS THEN
        INSERT INTO tbl_sp_errlog (errtable, errdate) VALUES ('Tbl_Awb_Basic_Temp', now());
        RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;

    BEGIN
        UPDATE cargo2ywtemp.tbl_awb_basic_temp t
        SET awbdate = a.op_date,
            awbdest = substr(a.routing, length(a.routing) - 2, 3),
            piece = a.piece
        FROM tbl_awb_basic a
        WHERE a.awbno = t.awbno
          AND a.awbprefix = t.awbprefix
          AND a.awbpostfix = t.awbpostfix;
    EXCEPTION WHEN OTHERS THEN
        INSERT INTO tbl_sp_errlog (errtable, errdate) VALUES ('Tbl_Awb_Basic_Temp', now());
        RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;

    BEGIN
        UPDATE cargo2ywtemp.tbl_awb_basic_temp bt
        SET ywtreate = 0
        FROM (
            SELECT awbprefix, awbno, awbpostfix, sum(piece) AS piece
            FROM (
                SELECT c.piece, c.awbprefix, c.awbno, c.awbpostfix
                FROM cargo2ywtemp.tbl_awb_basic_temp t
                JOIN tbl_cargo c
                  ON c.flight_dep = t.awbdest
                 AND c.flight_dest = t.awbdest
                 AND c.awbno = t.awbno
                 AND c.awbprefix = t.awbprefix
                 AND c.awbpostfix = t.awbpostfix
            ) b
            GROUP BY awbprefix, awbno, awbpostfix
        ) a
        WHERE a.awbno = bt.awbno
          AND a.awbprefix = bt.awbprefix
          AND a.awbpostfix = bt.awbpostfix
          AND bt.piece = a.piece;
    EXCEPTION WHEN OTHERS THEN
        INSERT INTO tbl_sp_errlog (errtable, errdate) VALUES ('Tbl_Awb_Basic_Temp', now());
        RAISE EXCEPTION 'Tbl_Awb_Basic_Temp 出现异常';
    END;

    BEGIN
        DELETE FROM cargo2ywtemp.tbl_cargo_chargecorrection_temp cct
        WHERE cct.ccano IN (SELECT DISTINCT awbno FROM cargo2ywtemp.tbl_awb_basic_temp);

        INSERT INTO cargo2ywtemp.tbl_cargo_chargecorrection_temp (ccaprefix, ccano, ccapostfix)
        SELECT DISTINCT d.ccaprefix, d.ccano, d.ccapostfix
        FROM tbl_cargo_chargecorrectiondetail d
        JOIN cargo2ywtemp.tbl_awb_basic_temp t
          ON d.awbprefix = t.awbprefix
         AND d.awbno = t.awbno
         AND d.awbpostfix = t.awbpostfix;
    EXCEPTION WHEN OTHERS THEN
        INSERT INTO tbl_sp_errlog (errtable, errdate) VALUES ('Tbl_Cargo_ChargeCorrection_Temp', now());
        RAISE EXCEPTION 'Tbl_Cargo_ChargeCorrection_Temp 出现异常';
    END;

END;
$$;

