CREATE OR REPLACE PROCEDURE sp_bulkinventoryupdate(p_warehouseid integer, p_adjustmenttype varchar(50), p_adjustmentdate timestamp DEFAULT NULL)
LANGUAGE plpgsql
AS
$$
DECLARE
    v_productid integer;
    v_currentqty integer;
    v_adjustmentqty integer;
    v_newqty integer;
    v_totalprocessed integer := 0;
    v_errorcount integer := 0;
    v_errormessage text;
    v_rowcount integer;
BEGIN
    IF p_adjustmentdate IS NULL THEN
        p_adjustmentdate := now();
    END IF;

    BEGIN
        FOR v_productid, v_currentqty, v_adjustmentqty IN
            SELECT productid,
                   quantityonhand,
                   CASE p_adjustmenttype
                       WHEN 'RECOUNT' THEN physicalcount - quantityonhand
                       WHEN 'DAMAGE' THEN -damagedquantity
                       WHEN 'RETURN' THEN returnedquantity
                       ELSE 0
                   END AS adjustmentqty
            FROM inventorystaging
            WHERE warehouseid = p_warehouseid
              AND status = 'PENDING'
            ORDER BY productid
        LOOP
            BEGIN
                v_newqty := v_currentqty + v_adjustmentqty;

                UPDATE inventory
                   SET quantityonhand = v_newqty,
                       lastadjustmentdate = p_adjustmentdate,
                       lastadjustmenttype = p_adjustmenttype
                 WHERE productid = v_productid
                   AND warehouseid = p_warehouseid;

                GET DIAGNOSTICS v_rowcount = ROW_COUNT;

                IF v_rowcount = 0 THEN
                    INSERT INTO inventory (productid, warehouseid, quantityonhand, lastadjustmentdate, lastadjustmenttype)
                    VALUES (v_productid, p_warehouseid, CASE WHEN v_newqty < 0 THEN 0 ELSE v_newqty END, p_adjustmentdate, p_adjustmenttype);
                END IF;

                INSERT INTO inventoryauditlog (productid, warehouseid, adjustmentdate, adjustmenttype, oldquantity, adjustmentquantity, newquantity, createdby)
                VALUES (v_productid, p_warehouseid, p_adjustmentdate, p_adjustmenttype, v_currentqty, v_adjustmentqty, v_newqty, current_user);

                v_totalprocessed := v_totalprocessed + 1;

            EXCEPTION WHEN OTHERS THEN
                v_errorcount := v_errorcount + 1;
                INSERT INTO errorlog (errordate, errormessage, productid)
                VALUES (now(), 'Inventory update failed: ' || SQLERRM, v_productid);
            END;
        END LOOP;

        UPDATE inventorystaging
           SET status = 'PROCESSED',
               processeddate = now()
         WHERE warehouseid = p_warehouseid
           AND status = 'PENDING';

        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', v_totalprocessed, v_errorcount;

    EXCEPTION WHEN OTHERS THEN
        v_errormessage := SQLERRM;
        RAISE EXCEPTION 'Bulk inventory update failed: %', v_errormessage;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_bulkinventoryupdate(warehouse_id integer, adjustment_type varchar(50), adjustment_date timestamp without time zone DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    product_id integer;
    current_qty integer;
    adjustment_qty integer;
    new_qty integer;
    total_processed integer := 0;
    error_count integer := 0;
    error_message varchar(4000);
    rec RECORD;
    msg_text text;
BEGIN
    IF adjustment_date IS NULL THEN
        adjustment_date := now();
    END IF;

    BEGIN
        START TRANSACTION;

        FOR rec IN
            SELECT ProductID, QuantityOnHand, PhysicalCount, DamagedQuantity, ReturnedQuantity
            FROM InventoryStaging
            WHERE WarehouseID = warehouse_id AND Status = 'PENDING'
            ORDER BY ProductID
        LOOP
            product_id := rec.ProductID;
            current_qty := rec.QuantityOnHand;

            IF adjustment_type = 'RECOUNT' THEN
                adjustment_qty := rec.PhysicalCount - rec.QuantityOnHand;
            ELSIF adjustment_type = 'DAMAGE' THEN
                adjustment_qty := -rec.DamagedQuantity;
            ELSIF adjustment_type = 'RETURN' THEN
                adjustment_qty := rec.ReturnedQuantity;
            ELSE
                adjustment_qty := 0;
            END IF;

            BEGIN
                new_qty := current_qty + adjustment_qty;

                IF EXISTS (SELECT 1 FROM Inventory WHERE ProductID = product_id AND WarehouseID = warehouse_id) THEN
                    UPDATE Inventory
                    SET QuantityOnHand = new_qty,
                        LastAdjustmentDate = adjustment_date,
                        LastAdjustmentType = adjustment_type
                    WHERE ProductID = product_id AND WarehouseID = warehouse_id;
                ELSE
                    INSERT INTO Inventory (ProductID, WarehouseID, QuantityOnHand, LastAdjustmentDate, LastAdjustmentType)
                    VALUES (product_id, warehouse_id, CASE WHEN new_qty < 0 THEN 0 ELSE new_qty END, adjustment_date, adjustment_type);
                END IF;

                INSERT INTO InventoryAuditLog (ProductID, WarehouseID, AdjustmentDate, AdjustmentType, OldQuantity, AdjustmentQuantity, NewQuantity, CreatedBy)
                VALUES (product_id, warehouse_id, adjustment_date, adjustment_type, current_qty, adjustment_qty, new_qty, current_user);

                total_processed := total_processed + 1;
            EXCEPTION
                WHEN OTHERS THEN
                    error_count := error_count + 1;
                    GET STACKED DIAGNOSTICS msg_text = MESSAGE_TEXT;
                    INSERT INTO ErrorLog (ErrorDate, ErrorMessage, ProductID)
                    VALUES (now(), 'Inventory update failed: ' || COALESCE(msg_text, ''), product_id);
            END;
        END LOOP;

        UPDATE InventoryStaging
        SET Status = 'PROCESSED',
            ProcessedDate = now()
        WHERE WarehouseID = warehouse_id AND Status = 'PENDING';

        COMMIT;
        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', total_processed, error_count;
    EXCEPTION
        WHEN OTHERS THEN
            GET STACKED DIAGNOSTICS error_message = MESSAGE_TEXT;
            ROLLBACK;
            RAISE EXCEPTION 'Bulk inventory update failed: %', error_message;
    END;
END;
$$;

CREATE OR REPLACE PROCEDURE sp_bulkinventoryupdate(IN warehouseid integer, IN adjustmenttype varchar(50), IN adjustmentdate timestamp DEFAULT NULL)
LANGUAGE plpgsql
AS $$
DECLARE
    totalprocessed integer := 0;
    errorcount integer := 0;
    errormessage text;
    inner_errmsg text;
    newqty integer;
    rec RECORD;
BEGIN
    IF adjustmentdate IS NULL THEN
        adjustmentdate := current_timestamp;
    END IF;

    BEGIN
        START TRANSACTION;

        FOR rec IN
            SELECT s.productid AS productid,
                   s.quantityonhand AS currentqty,
                   CASE adjustmenttype
                       WHEN 'RECOUNT' THEN s.physicalcount - s.quantityonhand
                       WHEN 'DAMAGE' THEN -s.damagedquantity
                       WHEN 'RETURN' THEN s.returnedquantity
                       ELSE 0
                   END AS adjustmentqty
            FROM inventorystaging s
            WHERE s.warehouseid = warehouseid
              AND s.status = 'PENDING'
            ORDER BY s.productid
        LOOP
            BEGIN
                newqty := rec.currentqty + rec.adjustmentqty;

                IF EXISTS (SELECT 1 FROM inventory i WHERE i.productid = rec.productid AND i.warehouseid = warehouseid) THEN
                    UPDATE inventory i
                       SET quantityonhand = newqty,
                           lastadjustmentdate = adjustmentdate,
                           lastadjustmenttype = adjustmenttype
                     WHERE i.productid = rec.productid
                       AND i.warehouseid = warehouseid;
                ELSE
                    INSERT INTO inventory (productid, warehouseid, quantityonhand, lastadjustmentdate, lastadjustmenttype)
                    VALUES (rec.productid, warehouseid,
                            CASE WHEN newqty < 0 THEN 0 ELSE newqty END,
                            adjustmentdate, adjustmenttype);
                END IF;

                INSERT INTO inventoryauditlog (productid, warehouseid, adjustmentdate, adjustmenttype, oldquantity, adjustmentquantity, newquantity, createdby)
                VALUES (rec.productid, warehouseid, adjustmentdate, adjustmenttype, rec.currentqty, rec.adjustmentqty, newqty, current_user);

                totalprocessed := totalprocessed + 1;
            EXCEPTION WHEN OTHERS THEN
                GET STACKED DIAGNOSTICS inner_errmsg = MESSAGE_TEXT;
                errorcount := errorcount + 1;
                INSERT INTO errorlog (errordate, errormessage, productid)
                VALUES (current_timestamp, 'Inventory update failed: ' || COALESCE(inner_errmsg, ''), rec.productid);
            END;
        END LOOP;

        UPDATE inventorystaging s
           SET status = 'PROCESSED',
               processeddate = current_timestamp
         WHERE s.warehouseid = warehouseid
           AND s.status = 'PENDING';

        COMMIT;

        RAISE NOTICE 'Bulk inventory update completed. Processed: %, Errors: %', totalprocessed, errorcount;
    EXCEPTION WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS errormessage = MESSAGE_TEXT;
        ROLLBACK;
        RAISE EXCEPTION 'Bulk inventory update failed: %', errormessage;
    END;
END;
$$;

CREATE PROCEDURE SP_ProcessPayroll(p_payperiodstart timestamp, p_payperiodend timestamp, p_departmentid int DEFAULT NULL)
AS $$
DECLARE
  v_payrollid int;
  v_employeeid int;
  v_hourlyrate numeric(10,2);
  v_regularhours numeric(5,2);
  v_overtimehours numeric(5,2);
  v_grosspay numeric(10,2);
  v_taxdeduction numeric(10,2);
  v_netpay numeric(10,2);
  v_totalgross numeric(15,2) := 0;
  v_totalnet numeric(15,2) := 0;
  v_errmsg text;
BEGIN
  BEGIN
    INSERT INTO PayrollHeader (PayPeriodStart, PayPeriodEnd, DepartmentID, CreatedDate, Status)
    VALUES (p_payperiodstart, p_payperiodend, p_departmentid, now(), 'PROCESSING')
    RETURNING PayrollID INTO v_payrollid;

    FOR v_employeeid, v_hourlyrate, v_regularhours, v_overtimehours IN
      SELECT e.EmployeeID,
             e.HourlyRate,
             COALESCE(SUM(CASE WHEN t.Hours <= 8 THEN t.Hours ELSE 8 END), 0) AS RegularHours,
             COALESCE(SUM(CASE WHEN t.Hours > 8 THEN t.Hours - 8 ELSE 0 END), 0) AS OvertimeHours
      FROM Employees e
      LEFT JOIN Timesheet t
        ON e.EmployeeID = t.EmployeeID
       AND t.WorkDate BETWEEN p_payperiodstart AND p_payperiodend
      WHERE e.Status = 'ACTIVE'
        AND (p_departmentid IS NULL OR e.DepartmentID = p_departmentid)
      GROUP BY e.EmployeeID, e.HourlyRate
      ORDER BY e.EmployeeID
    LOOP
      v_grosspay := (v_regularhours * v_hourlyrate) + (v_overtimehours * v_hourlyrate * 1.5);
      v_taxdeduction := v_grosspay * 0.20;
      v_netpay := v_grosspay - v_taxdeduction;

      INSERT INTO PayrollDetail (PayrollID, EmployeeID, HourlyRate, RegularHours, OvertimeHours, GrossPay, TaxDeduction, NetPay)
      VALUES (v_payrollid, v_employeeid, v_hourlyrate, v_regularhours, v_overtimehours, v_grosspay, v_taxdeduction, v_netpay);

      v_totalgross := v_totalgross + v_grosspay;
      v_totalnet := v_totalnet + v_netpay;
    END LOOP;

    UPDATE PayrollHeader
       SET TotalGrossPay = v_totalgross,
           TotalNetPay = v_totalnet,
           Status = 'COMPLETED',
           CompletedDate = now()
     WHERE PayrollID = v_payrollid;

    RAISE NOTICE 'Payroll processing completed. Payroll ID: %', v_payrollid;
  EXCEPTION
    WHEN OTHERS THEN
      GET STACKED DIAGNOSTICS v_errmsg = MESSAGE_TEXT;
      UPDATE PayrollHeader
         SET Status = 'FAILED',
             ErrorMessage = v_errmsg
       WHERE PayrollID = v_payrollid;
      RAISE EXCEPTION 'Payroll processing failed: %', v_errmsg;
  END;
END;
$$
LANGUAGE plpgsql;

