    Yw_Op_Date timestamp;
    YwUser varchar(10);
    StartTime timestamp;
    EndTime timestamp;
    iden bigint;
begin
    set transaction isolation level read committed;

    StartTime := now();
    YwUser := 'YWUSER';
    Yw_Op_Date := StartTime;

    raise notice '新建开始抽取日期';
    call sp_ywa_inslog(Yw_Op_Date, StartTime, null, 0, 'REPORTRUN', 'sp_YwA_DistillUllageReportDataAll--在A库中抽取前七天报表数据到临时表');

    begin
        iden := lastval();
    exception when others then
        iden := null;
    end;

    sFdate1 := date_trunc('day', Yw_Op_Date) - interval '1 second';
    sFdate2 := date_trunc('day', Yw_Op_Date - interval '7 day');

    raise notice '业维日期：%', to_char(Yw_Op_Date, 'YYYY-MM-DD');
    raise notice '最早日期：%', to_char(sFdate1, 'YYYY-MM-DD HH24:MI:SS');
    raise notice '最晚日期：%', to_char(sFdate2, 'YYYY-MM-DD HH24:MI:SS');

    raise notice '删除临时表记录';

    begin
        delete from "00.00.00.30.CARGO2.DBO.TBL_Flight"
        where flightdate >= sFdate2 and flightdate <= sFdate1;

        insert into "00.00.00.30.CARGO2.DBO.TBL_Flight"
        (flightno, flightdate, flight_dep, flight_dest, manifest_id, flight_dep_time, flight_dest_time, flight_dep_acttime, flight_dest_acttime,
         actype, acno, controlflag, flightflag, flight_dep_id, flight_dest_id, area, flightarea, maxactypeload,
         maxbookingweight, maxloadedweight, maxloadedvolume, controlweight, controlvolume, innerweight, innervolume, freeweight, freevolume,
         multisegmentflag, ywcomrun, updateflag, maxbookingvolume, delay_id, service_type, leg_status, socexisted, socsame, yw_op_time, yw_owner)
        select flightno, flightdate, flight_dep, flight_dest, manifest_id, flight_dep_time, flight_dest_time, flight_dep_acttime, flight_dest_acttime,
               actype, acno, controlflag, flightflag, flight_dep_id, flight_dest_id, area, flightarea, maxactypeload,
               maxbookingweight, maxloadedweight, maxloadedvolume, controlweight, controlvolume, innerweight, innervolume, freeweight, freevolume,
               multisegmentflag, ywcomrun, updateflag, maxbookingvolume, delay_id, service_type, leg_status, socexisted, socsame, now(), 'A'
        from "CARGO2.DBO.TBL_Flight"
        where flightdate >= sFdate2 and flightdate <= sFdate1;

        call tangforullagearrcapture();

        delete from "00.00.00.30.CARGO2.DBO.tbl_YW_ullage"
        where flightdate >= sFdate2 and flightdate <= sFdate1;

        insert into "00.00.00.30.CARGO2.DBO.tbl_YW_ullage"
        (oldweight, oldvolume,
         flightno, flightdate, flight_dep, flight_dest, reser, awbcity, goods,
         op_id,
         book_id, awbprefix, awbno, awbpostfix, op_date,
         oldbookweight, oldbookvolume, newbookweight,
         newbookvolume, newweight, newvolume)
        select oldweight, oldvolume,
               flightno, flightdate, flight_dep, flight_dest, reser, awbcity, goods,
               op_id,
               book_id, awbprefix, awbno, awbpostfix, op_date,
               oldbookweight, oldbookvolume, newbookweight,
               newbookvolume, newweight, newvolume
        from "CARGO2.DBO.tbl_ullage"
        where flightdate >= sFdate2 and flightdate <= sFdate1;

        delete from "00.00.00.30.CARGO2.DBO.TBL_YW_ULLAGE_ARR"
        where flightdate >= sFdate2 and flightdate <= sFdate1;

        if iden is not null then
            update tbl_yw_log set endtime = now(), optresult = 'REPORTSUC' where log_id = iden;
        end if;
    exception when others then
        raise notice '操作事务回滚';
        if iden is not null then
            update tbl_yw_log set endtime = now(), optresult = 'REPORTERR' where log_id = iden;
        end if;
    end;
end;
$$;

CREATE OR REPLACE PROCEDURE sp_datamigration(sourcetable text, targettable text, batchsize int DEFAULT 10000)
LANGUAGE plpgsql
AS $$
DECLARE
    sourcecount int;
    targetcount int;
    batchcount int := 0;
    migrationid int;
    errormessage text;
    rows_affected int;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (sourcetable, targettable, now(), 'RUNNING')
    RETURNING MigrationID INTO migrationid;

    EXECUTE 'SELECT count(*) FROM ' || sourcetable INTO sourcecount;
    UPDATE MigrationLog SET SourceCount = sourcecount WHERE MigrationID = migrationid;

    EXECUTE 'DELETE FROM ' || targettable;

    WHILE batchcount < sourcecount LOOP
        EXECUTE 'INSERT INTO ' || targettable || ' SELECT * FROM ' || sourcetable ||
                ' WHERE NOT EXISTS (SELECT 1 FROM ' || targettable || ' t WHERE t.id = ' || sourcetable || '.id) LIMIT ' || batchsize;
        GET DIAGNOSTICS rows_affected = ROW_COUNT;
        IF rows_affected = 0 THEN
            EXIT;
        END IF;
        batchcount := batchcount + rows_affected;
    END LOOP;

    EXECUTE 'SELECT count(*) FROM ' || targettable INTO targetcount;

    UPDATE MigrationLog
    SET EndTime = now(),
        TargetCount = targetcount,
        Status = CASE WHEN sourcecount = targetcount THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = migrationid;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', sourcetable, sourcecount, targetcount;

EXCEPTION WHEN OTHERS THEN
    GET STACKED DIAGNOSTICS errormessage = MESSAGE_TEXT;
    UPDATE MigrationLog
    SET EndTime = now(), Status = 'FAILED', ErrorMessage = errormessage
    WHERE MigrationID = migrationid;
    RAISE EXCEPTION 'Data migration failed: %', errormessage;
END;
$$;

CREATE OR REPLACE PROCEDURE SP_DataMigration(sourcetable varchar(128), targettable varchar(128), batchsize int default 10000)
LANGUAGE plpgsql
AS
$$
DECLARE
    source_count int;
    target_count int;
    batch_count int := 0;
    migration_id int;
    error_message text;
    rowcount int;
BEGIN
    INSERT INTO MigrationLog (SourceTable, TargetTable, StartTime, Status)
    VALUES (sourcetable, targettable, now(), 'RUNNING')
    RETURNING MigrationID INTO migration_id;

    EXECUTE 'SELECT count(*) FROM ' || sourcetable INTO source_count;

    UPDATE MigrationLog SET SourceCount = source_count WHERE MigrationID = migration_id;

    EXECUTE 'DELETE FROM ' || targettable;

    WHILE batch_count < source_count LOOP
        EXECUTE 'INSERT INTO ' || targettable || ' SELECT s.* FROM ' || sourcetable || ' s WHERE NOT EXISTS (SELECT 1 FROM ' || targettable || ' t WHERE t.ID = s.ID) LIMIT ' || batchsize::text;
        GET DIAGNOSTICS rowcount = ROW_COUNT;
        IF rowcount = 0 THEN
            EXIT;
        END IF;
        batch_count := batch_count + rowcount;
    END LOOP;

    EXECUTE 'SELECT count(*) FROM ' || targettable INTO target_count;

    UPDATE MigrationLog
    SET EndTime = now(),
        TargetCount = target_count,
        Status = CASE WHEN source_count = target_count THEN 'SUCCESS' ELSE 'WARNING' END
    WHERE MigrationID = migration_id;

    RAISE NOTICE 'Migration completed for %. Source: %, Target: %', sourcetable, source_count, target_count;

EXCEPTION
    WHEN OTHERS THEN
        GET STACKED DIAGNOSTICS error_message = MESSAGE_TEXT;
        UPDATE MigrationLog
        SET EndTime = now(),
            Status = 'FAILED',
            ErrorMessage = error_message
        WHERE MigrationID = migration_id;
        RAISE EXCEPTION 'Data migration failed: %', error_message;
END;
$$;

